css:
@-moz-document domain("chew.tv") {
/*
  Dark/Night theme for Chew.TV by Mikey Malone
  */
	  
  body{ 
    font-family:Arial, Helvetica, sans-serif;
    background-color: #101518;
    color: white;
  }
  body.grey { 
    background-color: #101518;
  }
  .show-player-container > .info {
    border-right: 1px solid #222;
    border-left: 1px solid #222;
  }
  header > div.nav a.logo {
    background-image: url("https://s3-us-west-2.amazonaws.com/mdlwebsrv/chew-dark.svg");
  }
  .viewport .content-container {
    color: #ddd;
  }
  .pad {
    background: #253036;
  }
  .share-tabs .tabs {
    padding: 20px;
    background: #253036;
  }
  .show-player-container > .info .pad h1 {
    color: #F05A35;
  }
  .show-player-container > .info .pad .user {
    border-bottom: 1px solid #222;
  }
  .channel-shows-container .row, .channel-about-container .row {
    background-color: #253036;
    overflow: hidden;
    border: 1px solid #253036;
  }
  .channel-shows-container .row .block .split .info a.title, .channel-about-container .row .block .split .info a.title {
    color: #F05A35 ;
  }
  .show-player-container > .info .pad .user .details a.name { color: #fff; }
  .show-player-container > .info .pad .description p { color: #ddd; }
  .channel-shows-container .row .block .split .info a.user, .channel-about-container .row .block .split .info a.user { color: #fff; }
  
  .share-tabs {
    border-top: 1px solid #222;
    border-bottom: 1px solid #222;
    margin: 0 -20px 0;
  }
  .share-tabs .tab-btns {
    border-bottom: 1px solid #222;
  }
  .show-player-container > .info {
    background-color: #fff;
    border-bottom: 1px solid #222;
  }
  .panel-round .panel-heading .panel-title { color: #eee;}
  .shows-row .show .info {
    background: #253036;
  }
  .shows-row .show .info .user {
    color: #ccc;
  }
  .shows-row .show .info .title {
    color: #fff;
  }
  .profile-container .user {
    background: #253036;
    border-left: 1px solid #222;
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
  }
  .profile-container .user .info {
    background-color: #253036;
    border-right: #222 1px solid;
  }
  .profile-container .user .info span.name {
    color: #fff;
  }
  .profile-container .user .small-social a.followers, .profile-container .user .small-social div.followers {
    border-right: 1px solid #222;
    border-left: 1px solid #222;
  }
  .profile-container .user .small-social a.following {
    border-right: 1px solid #222;
  }
  .profile-container .display {
    border-left: 1px solid #222;
    border-right: 1px solid #222;
  }
  .profile-container .user .small-social div span, .profile-container .user .small-social a span {
    display: block;
    color: #ddd;
  }
  .profile-container .display {
    background-color: #253036;
    border-bottom: 1px solid #222;
  }
  .channel-shows-container .row .block .split .video, .channel-about-container .row .block .split .video {
    border: 1px solid #222;
  }
  .channel-shows-container .row .block .split .info, .channel-about-container .row .block .split .info {
    background-color: #1A252C;
    width: 100%;
    border: 1px solid #222;
  }
  .channel-shows-container, .channel-about-container {
    color: #ccc;
  }
  nav.sidebar .pin {
    color: white;
    background-color: #253036;
    border-right: 1px solid #222;
  }
  section.account nav .profile .info .username {
    color: white;
  }
  nav.sidebar ul li.section {
    border-bottom: 1px solid #222;
    color: #ccc;
  }
  .rows-header {
    border-bottom: 1px solid #222;
    background-color: #253036;
  }
  .rows-subsection {
    background-color: #313D44;
    color: #ddd;
    border-bottom: 1px solid #222;
  }
  ul.shows.rounded-base {
    background-color: #253036;
  }
  ul.shows > li .image .thumb {
    border: 1px solid #222;
  }
  ul.shows > li {
    background-color: #253036;
    border-bottom: 1px solid #222;
  }
  ul.shows > li .meta {
    border-left: #222;
  }
  ul.shows > li .info h3 {
    color: #fff;
  }
  ul.shows > li .info p {
    color: #ccc;
  }
  a:hover {
    color: #FA5B1F;
  }
  [panner] .shade-left {
    background: linear-gradient(90deg, #222, rgba(255, 255, 255, 0));
  }
  [panner] .shade-right {
    background: linear-gradient(270deg, #000, rgba(255, 255, 255, 0));
  }
  .panel {
    border: 1px solid #222;
    background-color: #253036;
  }
  form.panel .row > .left {
    color: #fff;
    border-bottom: 1px solid #222;
  }
  .panel .row h3 {
    color: #fff;
  }
  section.account h2 {
    color: #eee;
  }
  form.panel .row > .left:not(.raw) label {
    color: #ccc;
  }
  .copyblock .stretch {
    color: #fff;
    background-color: #253036;
    border: 1px solid #222;
  }
  form.panel .row .right {
    color: white;
    background-color: #41494D;
  }
  form.panel .row > .left .icon {
    color: white;
  }
  form.panel .row .left:not(.raw) input, form.panel .row .left:not(.raw) input[type="text"], form.panel .row .left:not(.raw) input[type="password"], form.panel .row .left:not(.raw) input[type="email"], form.panel .row .left:not(.raw) textarea {
    background-color: #3B4B54;
    color: #ddd;
  }
  .channel-player-container .inner .pad .video > .info {
  	color: white;
    background-color: #253036;
    border-color: black;
  }
  .channel-player-container .inner .pad .video > .info .pad h1 a {
	color: #ddd;
}
.channel-player-container .inner .pad .video .info .pad p {
    color: white;
}

.show-panel .show-information .show-title {
    color: #FA5B1F;
    font-size: 2em;
}
.chat-main {
    background-color: #253036;
}
}
