css:
@-moz-document domain("www.nicovideo.jp") {

.log-user-action-stamp,
.log-user-video-round-number-of-view-counter,
.log-user-mylist-add,
.log-user-seiga-image-clip,
.log-user-live-broadcast,
.log-user-live-reserve,
.log-reslist ,
.log-user-live-video-introduced,
.topic_wrap,
.log-res-open-link,
.log-user-solid-favorite,
.autopagerize_page_info,
.log-user-video-review,
.log-community-live-broadcast,
.log-target-type-image
{display:none !important;}

.log
{
    padding: 10px !important;
	margin: 10px !important;
    border-left:10px solid #CCABC1 !important;
    border-bottom:1px solid #CCABC1 !important;
    background-color: 	#FAD2EC;
    box-shadow: 10px 10px 10px rgba(0,0,0,0.3);  
}

.autopagerize_page_separator 
{
    border-width: 0px; margin: 10px 10px !important;
}

.articleBody
{
    margin: 0px !important;
}

.log-user-seiga-image-upload
{
    margin: 10px !important;
    border-left:10px solid #C1CCAB !important;
    border-bottom:1px solid #C1CCAB !important;
    background-color: 	#ECFAD2;
    box-shadow: 10px 10px 10px rgba(0,0,0,0.3);
    }

.log-user-video-upload
{
    margin: 10px !important;
    border-left:10px solid #ABC1CC !important;
    border-bottom:1px solid #ABC1CC !important;
    background-color: 	#D3EDFB;
    box-shadow: 10px 10px 10px rgba(0,0,0,0.3);    
    }

.author-user
{
  font-size:16px;
  font-weight: bold;  
}

.log-body strong
{
  font-size:16px;   
  font-weight: bold;  
}

}
