css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pugjs.org") {

  code {
    background-color: #4a4a41 !important;
    color: lightgray !important;
  }

  /*
  Taken from
  https://github.com/Hippyjake/HippyjakesUserStyles/blob/master/InceptionDark/themes/Monokai.css
  */

  body {
    background: #272822;
    color: #fff
  }

  .cm-s-default {
    background: #443;
    color: #f8f8f2;
    height: 70%
  }

  div.cm-s-default-selected {
    background: #444 !important
  }

  .cm-s-default::-moz-selection,
  .cm-s-default::selection {
    background: rgba(73, 72, 62, .99)
  }

  .cm-s-default-gutters {
    background: #272822;
    border-right: 1px solid #4d4d4d
  }

  .cm-s-default span.cm-variable,
  .cm-s-default-guttermarker,
  .cm-s-default .cm-variable-3 {
    color: #fff
  }

  .cm-s-default-guttermarker-subtle,
  .cm-s-default .cm-builtin,
  .cm-s-default .cm-meta,
  .cm-s-default .cm-qualifier {
    color: #a7ec21
  }

  .cm-s-default-linenumber {
    color: #d0d0d0
  }

  .cm-s-default-cursor {
    border-left: 1px solid #f8f8f0 !important
  }

  .cm-s-default span.cm-comment {
    color: #75715e
  }

  .cm-s-default span.cm-atom,
  .cm-s-default span.cm-link,
  .cm-s-default span.cm-number {
    color: #ae81ff
  }

  .cm-s-default span.cm-attribute,
  .cm-s-default span.cm-property {
    color: #52e3f6
  }

  .cm-s-default span.cm-keyword,
  .cm-s-default span.cm-tag {
    color: #f92672
  }

  .cm-s-default span.cm-string {
    color: #e6db74
  }

  .cm-s-default span.cm-variable-2 {
    color: #9effff
  }

  .cm-s-default span.cm-def {
    color: #fd971f
  }

  .cm-s-default span.cm-bracket {
    color: #f8f8f2
  }

  .cm-s-default span.cm-error {
    background: #f92672;
    color: #f8f8f0
  }

  .cm-s-default-activeline-background {
    background: #373831 !important
  }

  .cm-s-default-matchingbracket {
    color: #a6e22e !important;
    text-decoration: underline
  }
}
