css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("e-papierosy-forum.pl") {
  body{
    background-color: #f2f2f2;
    color:#000;
  }
  
.panel{
  background-color: #d8e8cc;
  font-family: "Arial","Helvetica",sans-serif;
  font-size: 12px;
  border: 1px solid #a7b7c6;
}
  .panel-body .content blockquote{
    background-color: #e8f8dc;
    border: 1px solid #a7b7c6;
  }
  
  .separator::before { 
    border-bottom: 1px dashed #3c6424 !important; width: 250px; 
  }

  .panel-heading{
    background-color: #acd692;
    border: 1px solid #a7b7c6;
  }
  
  .avatar-over > .status::before{
    right: -20px;
  }
  
  .post-id{
    font-size: 9px; 
  }
  
  .alert-info{
    background-color: #BDB;
    color:#000;
  }
  
  .table-hover > tbody > tr:hover > td, .table-hover > tbody > tr:hover > th {
   background-color:  #CDE5BC;
  }
  
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top-color: #A9B8C2;
  }
  
  .table-primary tbody td {
    background-color: #CDE5BC;
  }
  
  .navbar-inverse {
    background-color: #acd692;
}
  .well{
    background-color: #d8e8cc;
}
.navbar {
position:fixed !important;
width:100%;
top:-66px;
}
.navbar:hover {top:0} 
}
