css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("black-book-editions.fr") {
	html{
		background-color: #111;
	}
	body{
		background-color: #111;
		color:#ccc;
	}
	#fil_info a, #fil_info_ks a {
		color:#ccc;
	}
	.msg_txt.alerte_orange{
		color: #333;
	}
	.container {
			background-color: #222;
			color: #ccc;
	}
	.menuzord {
		background: #222;
	}
	.form-control {
		background-color:#111;
		color: #ccc;
	}
	.gbox {
		background-color:#111;
		border-color:#333;
		box-shadow: 0 0 2px rgba(0,0,0,.1),0 1px 0 #000 inset;
	}
	.item, .item_lot {
		background:#333;
		border-color:#666;
	}
	.item:hover {
		background:#444;
	}
	.list-group-item {
		background:#444;
	}
	#board_liste li {
		background:#333;
		color: #ccc;
	}
	#board_liste a {
		color: #ccc;
	}
	#board_liste li:hover {
			background:#555;
	}
	#board_liste .compteur {
		background-color:#555;
	}
	.active {
		background:#333;
	}
	#message_liste blockquote {
		background:#555;
		color: #ccc;
	}
	blockquote {
		background:#555;
		color: #ccc;
	}
	.table td,.table th  {
		color: #ccc;
	}
	.table-striped>tbody>tr:nth-of-type(odd) {
		background-color:#111;
	}
	.table-hover>tbody>tr:hover {
		background-color:#555
	}
	.tab-content {
		background:#333;
	}
	.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus {
		background:#333;
	}
	.nav > li > a:focus, .nav > li > a:hover {
		background:#333;
	}
	.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover {
		background:#333;
	}
	.pagination>li>a,.pagination>li>span {
		background:#222;
	}
	.pagination>li>a:focus,.pagination>li>a:hover,.pagination>li>span:focus,.pagination>li>span:hover {
	 background:#555;
	}
	.pagination>.active>a,.pagination>.active>a:focus,.pagination>.active>a:hover,.pagination>.active>span,.pagination>.active>span:focus,.pagination>.active>span:hover {
		background:#225;
	}
	.pagination>.disabled>a,.pagination>.disabled>a:focus,.pagination>.disabled>a:hover,.pagination>.disabled>span,.pagination>.disabled>span:focus,.pagination>.disabled>span:hover {
	 background:#222;
	}
	.pager li>a:focus,.pager li>a:hover {
	 background:#252;
	}
	.dropdown-menu {
		background:#333;
	}
	.msg_actions { 
		background:#222;
	}
	.msg_actions a {
		color: #ccc;
	}
	.triangle-border {
		background:#111 !important;
	}
	.triangle-border.left:after {
		border-color: transparent #111;
	}
	.news_form textarea {
		background-color:#111;
	}
	.accordion_hp_gamme .accordion-group {
		background-color:#333;
		box-shadow: 0 0 2px rgba(0,0,0,.1),0 1px 0 #000 inset;
		border-color:#555;
	}
	.accordion-heading h4 {
		color:#ccc;
	}
	.info_pledge {
		background-color:#333;
	}
	.forum_direct b a {
		color:#ccc;
	}
	.alertify .ajs-dialog {
		background-color:#333;
	}
	.alertify .ajs-footer {
		background-color:#444;
	}
	#focus_actu b {
		color:#ccc;
	}
	#ks_createur {
		background-color:#333;
	}
	#produit_en_bref {
		background-color:#555;
	}
	#produit_infos {
		background-color:#333;
	}
	#info_mon_compte {
		background-color:#444;
	}
	.sms_from {
		background-color:#125;
	}
	.sms_to {
		background-color:#333;
	}
	.panel-body {
		background-color:#333;
	}
}
