css:
@-moz-document domain(klavogonki.ru) {
#about table.cars {
    border: none;
}

#about table.cars td {
    background: #f5f5f5;
}

#chat-container {
}

#errors_tab {
    background: transparent;
}

#footer {
    background: #f5f5f5;
}

#gamelist > div:first-child, div.banner-airplane, div.ownbanner-back.w728, ins.chat-header, div.hide-block, .secondrow .col1, .firstrow img, .indexstats-block, .col1 #review, .imobilco-book, a.ng-scope img {
    display: none;
}

#gamelist td {
    padding: 1px;
}

#gameloading .rc, .msg .rc, #param_font, #invite-link {
    border: 3px solid #eaeaea;
}

#gametype-select {
    background: #f5f5f5;
    border: 3px solid #cccccc;
    height: 500px;
    left: -25px;
    outline: none;
    top: 45px;
}

#head .right {
    padding: 0px 0px 0px;
}

#index #live .grad {
    display: none;
}

#inputtext {
    width: 872px;
}

#logo a img {
    height: 60px;
}

#playads {
    margin-top: -285px;
}

#players, .player {
    background: #f5f5f5;
}

#posts-list .post, textarea {
    font-family: 'Tahoma';
    font-size: 17px;
}

#recent-games-container {
    background: #f5f5f5;
}

#recent-games-count-dec, #recent-games-count, #recent-games-count-inc {
    color: #428bca;
    font: bold 23px 'Arial';
    position: relative;
    text-shadow: 1px 1px rgba(0,0,0,.1);
    top: 3px;
}

#speedpanel {
    border: 2px solid #e7e7e7;
    border-radius: 10px;
}

#status-block, #main-block, #typeblock {
    width: 900px;
}

#top {
    box-shadow: 1px 1px 5px rgba(0,0,0,.09);
}

#top td.date {
    font-size: 12px;
}

#toplist .active, .left-col ul.left-menu li.active span, .left-col ul.left-menu li.active, .about li.active, .trimenu .active {
    background: #bb2200;
    border-radius: 3px;
    color: #ffffff;
}

#toplist span {
    background: none;
}

#typeblock .rc {
    background: #ebebeb;
}

.car .car-tuning2_ {
    background: rgba(0,0,0,.04);
}

.car .car-tuning2_1, .car .car-tuning2_3, .car .car-tuning2_2 {
    background: rgba(0,0,0,.04);
}

.chat .messages {
    padding: 0px;
}

.chat .messages div {
    border: none;
}

.chat .messages p {
    padding-left: 7px;
}

.chat .messages table input.send {
    position: relative;
    top: 8px;
}

.chat .messages table input.text {
    border: 3px solid #cccccc;
    position: relative;
    top: 8px;
    transition: all .1s;
}

.chat .messages table input.text:focus {
    border: 3px solid #778899;
}

.col3 .rc, .gamelist-create .rc, #play-right .rc, .columns .info .rc, .comments .rc {
    border: 3px solid #fdfcef;
}

.dlg-profile-car-tuning, .profile-journal-post-dlg {
    background: rgba(0,0,0,.04);
}

.fuel-root .month button {
    background: #f5f5f5;
}

.gametype-normal option {
    background: #f5f5f5;
}

.messages .messages-content {
    background: rgba(0,0,0,.04);
}

.messages tbody form {
    padding: 5px;
}

.messages tbody td {
    background-color: #f5f5f5;
}

.messages tbody tr th {
    background-color: #f5f5f5;
}

.people img {
    display: none;
}

.popup_message .popup-box .c {
    background: #f5f5f5;
}

.profile-root .profile-header {
    background: #f5f5f5;
}

.quotetop, .quotemain {
    font-family: 'Arimo';
    font-size: 14px;
}

.recent-game {
    margin: 0px 0.5px 0px;
}

.recent-game a {
    font-size: 12px;
    padding: 3px;
}

.recent-game.pin-game {
    border: 1px solid #666666;
}

.recent-game.pin-game:hover {
    border: 1px solid #666666;
}

.secondrow .col2 {
    left: 27%;
}

.secondrow .col3 {
    left: 30%;
}

.tipsy-inner {
    font-size: 12px;
    max-width: 200px;
}

a.name {
    font-size: 14px;
}

body {
    background: #f5f5f5;
}

div svg rect {
    fill: #f5f5f5;
}

div.car.ng-isolate-scope {
    background: #f5f5f5;
}

div.messages-content div p {
    font-size: 14px;
}

div.search {
    background: #ffffff;
}

div.user-content dl dd {
    background-color: #f5f5f5;
}

div[style="background-color: #777777"], div[style="background-color: #777"] {
    background-color: #00e100;
    opacity: .2;
}

form.ng-pristine.ng-valid input {
    outline: none;
}

form[action="/gamelist/"] select {
    border: 3px solid #fdfdfd;
}

input#create_game, input#delete, .chat input.send, input[name="delete"], input[name="edit"], .invite-btn, input[value="Ã—"], input[onclick="create_BBcode(1)"] {
    background: #428bca;
    border: 1px solid #3573bd;
    border-color: #357ebd;
    border-radius: 3px;
    box-shadow: 0px 0px 2px rgba(0,0,0,.5);
    color: #ffffff;
    font-family: Arial;
    font-weight: normal;
    padding: 6px;
}

input#create_game:hover, input#delete:hover, .chat input.send:hover, input[name="delete"]:hover, input[name="edit"]:hover, .invite-btn:hover {
    background: #357dbf;
}

input.text {
    font-size: 14px;
}

tr.other td {
    background: #f5f5f5;
}

#play-right {
    display: none;
}

.feedback-btn {
display: none;
}
}
