@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("redadn.es") {
  body {
   background: #FAFAFA url("/*[[background]]*/") no-repeat center top fixed;
   background-position: 0px 50px !important; 
  }
  
  #w .si, #w .sil {
   background: none;
  }
  
  div.si {
   display: none;
  }
  
  div.sil {
   background-image: none !important;
   background-color: transparent !important;
  }

  #h {
   border-bottom: none !important;
  }
  
  #h2 {
   display:none !important;
  }

  #w .cu .nb {
   display: none;
  }
  
  #w .cu > div > div:nth-child(3) {
   margin-left: 10px;
  }
  
  #w .cu > div > div:nth-child(4) {
   width: 1000px;
  }
  
  #w .p {
   padding: 40px 0;
  }
  
  #w .c7 {
   width: 930px;
  }
  
  #w .l, #w .lm {
   display:none !important;
  }
  
  #w .cu {
   background-color: transparent !important;
  }
  
  #w .cu > div {
   background-color: #FFF;
   box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  }
  
  #w .li {
    top: -30px;
  } 
}
