css:
@-moz-document domain("roll20.net") {
html,
body {
    height: 100% !important;
}
body {
    background: url('http://pre15.deviantart.net/113b/th/pre/i/2006/138/c/9/ultimate_transmutation_circle_by_zaints.png') center center no-repeat, url('http://static.squarespace.com/static/52ed62c3e4b0957cc4094975/t/52fae643e4b0739646699930/1392174662191/wood.jpg') !important;
    box-shadow: inset 0 0 200px rgba(0, 0, 0, .8) !important;
}
#page-toolbar {
    background-color: #111 !important;
    border-radius: 0 !important;
    height: 160px !important;
    left: 0 !important;
    right: 0 !important;
    width: initial !important;
}
#page-toolbar .availablepage {
    box-sizing: border-box !important;
    height: 160px !important;
    margin: 0 !important;
    padding: 10px !important;
    vertical-align: top !important;
    width: 160px !important;
}
#page-toolbar .availablepage .pagethumb {
    margin-top: 15px !important;
}
#page-toolbar .availablepage span {
    bottom: 20px !important;
    left: 0 !important;
    right: 0 !important;
    text-shadow: none !important;
    color: #eee !important;
    font-size: 12px !important;
}
#page-toolbar .container {
    transition: opacity .4s ease-in-out !important;
}
#page-toolbar .handle {
    background: #111 !important;
    border-radius: 0 0 100% 100% !important;
    bottom: -29px !important;
    color: #eee !important;
    height: 29px !important;
    opacity: 1 !important;
    width: 100px !important;
    right: 50% !important;
    margin-left: -50px !important;
    padding: 0 !important;
}
#page-toolbar .handle .pictos {
    display: none !important;
}
#page-toolbar .handle:after {
    content: 'PAGES' !important;
    font-size: 11px !important;
    letter-spacing: 2px !important;
    height: 100% !important;
    left: 0 !important;
    position: absolute !important;
    top: 0 !important;
    width: 100% !important;
}
#page-toolbar .playerbookmark {
    left: 30px !important;
    top: 25px !important;
}
#page-toolbar.closed .container {
    box-shadow: 0 0 50px rgba(0, 0, 0, .15) !important;
    opacity: 0 !important;
}
#playerzone {
    bottom: 20px !important;
    left: 20px !important;
}
#playerzone .player {
    margin: 0 !important;
    margin-right: 10px !important;
}
#playerzone .player .color_picker {
    top: 0 !important;
}
#playerzone .player .playername {
    background-color: #111 !important;
    font-size: 14px !important;
    line-height: 24px !important;
    padding: 0 !important;
}
#rightsidebar {
    -moz-border-radius-bottomleft: 0 !important;
    -moz-border-radius-bottomright: 0 !important;
    -moz-border-radius-topleft: 0 !important;
    -moz-border-radius-topright: 0 !important;
    -webkit-border-bottom-left-radius: 0 !important;
    -webkit-border-bottom-right-radius: 0 !important;
    -webkit-border-top-left-radius: 0 !important;
    -webkit-border-top-right-radius: 0 !important;
    border-bottom-left-radius: 0 !important;
    border-bottom-right-radius: 0 !important;
    border-top-left-radius: 0 !important;
    border-top-right-radius: 0 !important;
    border-style: none !important;
    overflow: initial !important;
    box-shadow: 0 0 50px rgba(0, 0, 0, .15) !important;
}
#secondary-toolbar {
    background-color: #222 !important;
    border: 0 !important;
    box-shadow: none !important;
    left: 90px !important;
    opacity: 1 !important;
}
#secondary-toolbar .btn {
    border: 0 !important;
    border-radius: 0 !important;
    margin: 5px !important;
}
#secondary-toolbar li {
    border: 0 !important;
    height: initial !important;
}

#textchat .message.system .spacer {
    background-color: #ddd !important;
}
.tabmenu {
    height: 50px !important;
    position: absolute !important;
    left: 0 !important;
    right: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    top: 0 !important;
}
.tabmenu li {
    height: 50px !important;
    width: 16.667% !important;
    text-align: center !important;
    top: 0 !important;
}
.tabmenu li a {
    padding: 0 !important;
    margin: 0 !important;
    line-height: 50px !important;
    border: 0 !important;
    width: 100% !important;
}
#rightsidebar .ui-tabs-panel {
    top: 50px !important;
}
#rightsidebar .ui-tabs-panel#textchat {
    position: absolute !important;
    top: 50px !important;
    height: initial !important;
    bottom: 150px !important;
}
#textchat-input {
    border-top: 0 !important;
    padding: 10px !important;
    height: initial !important;
    min-width: 260px !important;
    box-sizing: border-box !important;
}
#textchat-input * {
    box-sizing: border-box !important;
}
#textchat-input textarea {
    width: 100% !important;
    height: 80px !important;
    resize: none !important;
}
.actions_menu {
    width: 120px !important;
    z-index: 110 !important;
    /* above sidebar */
}
.actions_menu > ul {
    width: initial !important;
    padding: 0 !important;
    border: 0 !important;
}
.actions_menu ul li {
    padding: 0 10px !important;
    line-height: 30px !important;
    min-width: 90px !important;
    border-color: #ddd !important;
    white-space: nowrap !important;
}
.actions_menu ul li:lastchild {
    border-bottom: 0 !important;
}
.actions_menu ul li ul.submenu {
    left: 90px !important;
    border: 0 !important;
    width: initial !important;
    opacity: 1 !important;
    box-shadow: 0 1px 15px rgba(0, 0, 0, .1) !important;
}
#measure img {
    -webkit-filter: brightness(2.3) !important;
}
.nav-tabs li a {
    outline: none !important;
}
#sidebarcontrol {
    opacity: 1 !important;
    padding: 0 !important;
    -webkit-transform: translateX(0) rotate(-90deg) !important;
    -moz-transform: translateX(0) rotate(-90deg) !important;
    width: 100px !important;
    text-align: center !important;
    border: none !important;
    font-size: 12px !important;
    color: #eee !important;
    background-color: #111 !important;
    height: 30px !important;
    line-height: 30px !important;
    text-transform: uppercase !important;
    letter-spacing: 2px !important;
    -webkit-transform-origin: 100% 100% !important;
    -moz-transform-origin: 100% 100% !important;
    border-radius: 100% 100% 0 0 !important;
    top: 50% !important;
    margin-top: -75px !important;
}
#sidebarcontrol .fonticon-menu {
    display: none !important;
}
#sidebarcontrol:after {
    content: 'Menu' !important;
}
}
