css:
@-moz-document url-prefix("https://quantopian.com/algorithms/") {
/* Hide elements */
.app-navbar,
.algo-back,
.subnav,
#contest-submit-button,
#ide-box > div.toolbar > div > div.right.inline-block.pull-right > div:nth-child(3),
#ide-options,
#chat-window-link {
    display: none !important;
}

/* Navbar related */
#ide-box > div.toolbar,
.nav {
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#3d3d3d), to(#3d3d3d)) !important;
    background-color: #3d3d3d !important;
    background: #3d3d3d !important;
    border: 1px solid #474747;
}

#output-pane .dailybars-output .tabbable .nav-tabs li.active a {
    color: #202020 !important;
}

.nav-tabs>.active>a,
.nav-tabs>.active>a:hover,
.nav-tabs>.active>a:focus {
    background-color: #d3d3d3;
}

#output-pane .dailybars-output .dailybars-nav,
#dailybars-status,
.dailybars-controls {
    background-color: #474747 !important;
}

#output-pane .dailybars-output .dailybars-nav {
    border-bottom: none;
}

/* Output area */
#output-pane .dailybars-output .dailybars-results {
    background-color: #202020;
}

#dailybar-stats .dailybar-stat .value {
    color: white;
}

.highcharts-background,
.highcharts-scrollbar > rect,
.highcharts-scrollbar > g > rect {
    fill: #202020;
}

.highcharts-axis {
    stroke: #202020;
}

.highcharts-grid > path {
    stroke: #474747;
}

#output-pane .dailybars-output .dailybars-results #dailybar-chart-outer-container #custom-series-controls {
    background-color: inherit;
}

#output-pane {
    border-top: none;
    border-bottom: none;
}

#output-pane .dailybars-output .tabbable .nav-tabs {
    border-top: none;
}

.to-label, #output-pane .dailybars-output #dailybars-status .progress-pane span {
    color: #fff;
}

#output-pane > div.dailybars-output > div.tabbable {
    margin-left: -3px;
}

/* Splitter and scroll bars */
#splitter-outer-container #splitter-container .splitter-bar-vertical {
    background-color: #474747;
    border-left: 2px solid #474747;
    border-right: 2px solid #474747;
}

::-webkit-scrollbar {
    width: 8px;
    background-color: #969696;
}

::-webkit-scrollbar-thumb {
    border-radius: 5px;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    background-color: #555;
}

::-webkit-scrollbar-track {
    background-color: #d3d3d3;
        border-radius: 5px;
}

/* Buttons */
.btn-primary {
    background-color: #e74c3c;
    background-image: linear-gradient(to bottom, #e74c3c, #e74c3c) !important;
}

.btn {
    background-image: linear-gradient(to bottom, #ecf0f1, #ecf0f1);
}

.progress.progress-striped .bar {
    background-color: #c0392b;
}

/* Fonts (from https://github.com/codemirror/CodeMirror/blob/master/theme/dracula.css) */
.cm-s-ambiance.CodeMirror, .cm-s-ambiance .CodeMirror-gutters {
  color: #f8f8f2 !important;
  border: none !important;
}
.cm-s-ambiance .CodeMirror-gutters { color: #282a36 !important; }
.cm-s-ambiance .CodeMirror-cursor { border-left: solid thin #f8f8f0 !important; }
.cm-s-ambiance .CodeMirror-linenumber { color: #6D8A88 !important; }
.cm-s-ambiance .CodeMirror-selected { background: rgba(255, 255, 255, 0.10) !important; }
.cm-s-ambiance .CodeMirror-line::selection, .cm-s-ambiance .CodeMirror-line > span::selection, .cm-s-ambiance .CodeMirror-line > span > span::selection { background: rgba(255, 255, 255, 0.10) !important; }
.cm-s-ambiance .CodeMirror-line::-moz-selection, .cm-s-ambiance .CodeMirror-line > span::-moz-selection, .cm-s-ambiance .CodeMirror-line > span > span::-moz-selection { background: rgba(255, 255, 255, 0.10) !important; }
.cm-comment { color: #6272a4 !important; }
.cm-string, .cm-string-2 { color: #f1fa8c !important; }
.cm-number { color: #bd93f9 !important; }
.cm-variable-2 { color: white !important; }
.cm-def { color: #ffb86c !important; }
.cm-keyword { color: #ff79c6 !important; }
.cm-operator { color: #ff79c6 !important; }
.cm-keyword { color: #ff79c6 !important; }
.cm-atom { color: #bd93f9 !important; }
.cm-meta { color: #f8f8f2 !important; }
.cm-tag { color: #ff79c6 !important; }
.cm-attribute { color: #50fa7b !important; }
.cm-qualifier { color: #50fa7b !important; }
.cm-property { color: #66d9ef !important; }
.cm-builtin { color: #50fa7b !important; }
.cm-variable-3 { color: #50fa7b !important; }
span[class^="cm-quanto"] { color: #3498db !important; }

.cm-s-ambiance .CodeMirror-activeline-background { background: rgba(255,255,255,0.1) !important; }
.cm-s-ambiance .CodeMirror-matchingbracket { text-decoration: underline !important; color: white !important; }
}
