css:
@-moz-document url-prefix("https://www.jamendo.com/radios"),
               url-prefix("https://www.jamendo.com/artist"),
               url-prefix("https://www.jamendo.com/track"),
               url-prefix("https://www.jamendo.com/album")
{

html { background-color: #161616}

.hero, .hero-text, .hero-text .background {
    background-color: #161616;
}

*{border-color: #333 !important}

.app-bar {
    background-color: #242424;
    color: #ccc;
}

.btn, .btn-fixed_btn, .btn-pill.btn--hybrid span, .btn-social>div:last-child {
    color: #ccc!important;
}

.btn.btn--licensing:before {
    background-color: #333;
}

html {
    color: #999;
}

.section.section--double>header>div>div, .section.section--select-only>header>div>div {
    background-color: #161616;
}

.section.section--double>header:before, .section.section--select-only>header:before {
    border-top: 1px solid #333;
}

.entity-aside .stats {
    background-color: #1b1b1b;
}

.entity-aside .tags {
    background-color: #1b1b1b;
}

.btn-tag:before {
    border: 1px solid #333;
}

.btn-tag {
    color: #bbb !important;
}

.entity-aside .section.about {
    background-color: #1b1b1b;
}

.entity-aside .social-links {
    background-color: #1b1b1b;
}

.entity-aside .secondary {
    background-color: #1b1b1b;
}

.btn.btn--discreet:before {
    background-color: #333;
}

textarea {
    background-color: #101010;
}

.entry .type .send {
    background-color: #161616;
}

.section-feedback {
    background-color: #161616;
}

.range input[type=range] {
    display: none !important;
}

.range.range--track .bar {
    background-color: #666;
}

.messages {
    background-color: #242424;
    color: #999;
}

#skeleton-overlay {
    background-color: rgba(0,0,0,0.8);
}

.overlay-bar {
    color: #999;
    background-color: #242424;
}

.entity-overlay_body {
    background-color: #1b1b1b;
}

.entity-overlay {
    color: #999;
}

#o-download .cc-link {
    color: #999;
}


::-webkit-scrollbar { width: 3px; height: 3px;}
::-webkit-scrollbar-button {  background-color: #666; }
::-webkit-scrollbar-track {  background-color: #999;}
::-webkit-scrollbar-track-piece { background-color: #333;}
::-webkit-scrollbar-thumb { height: 50px; background-color: #666; border-radius: 3px;}
::-webkit-scrollbar-corner { background-color: #999;}
::-webkit-resizer { background-color: #666;}



}
