css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("idope.se") {
  body,#div0,#div2 {
    background-color: #333333;
  }
  #showdiv,.shorthr,#div2child,#top-banner,.address-li:nth-child(2n+1) {
    background-color: #292929;
  }
  
  #showdivchild {
    background-color: #222222 !important;
  }
  .resultdiv,.resultdivtop {
    background-color: #292929 !important;
  }
  .commonhr,#ul {
    background-color: #333333 !important;
  }
  #seletct1div,#seletct2div,#rightloadapp {
    border: none;
  }
  #input {
    background-color: #181818 !important;
    color: #4385f3;
  }
  #inputdiv {
    border: solid 1px #000 !important;
    background-color: #181818 !important;
    color: #ffffffb3;
  }
  #toprecentbotton,#toptopbotton,.resultdivtopname {
    color: #FFF !important;
  }
  .categorydiv,#toptitle,#homepagediv,#bottonloadapp {
    background-color: #333333 !important;
  }
  .categoryname {
    color: #5b5e60;
  }
  .bottondiv {
    background: #333;
  }
  #topdiv,#top-banner-inputdiv,.file-list-top,.comment {
    background: #181818;
  }
  #top-banner-inputdiv{
    border: none;
  }
  .reg-btn,.fast-top-p,.lastSeach-cont .lastSeach-ul li a,.personal-label,.vist-link-div span,.blogitem-header-link {
    color: #FFF;
  }
  #feedbackdiv {
    background-color: #333;
  }
  #textarea,.enter-div {
    background: #282828;
  }
  .register-head {
    background: #333;
    color: #565656;
  }
  .register-div {
    background: #2e2e2e;
  }
  .user-method-page {
    background: #3f3f3f;
  }
  #hottest-blogdiv {
    box-shadow: 1px 5px 7px 3px #202020;
  }
  .bottondiv {
    border-top: 1px solid #202020;
  }
  #rightdiv {
    display:none;
  }
  .chang-list-li a {
    color: #4ABCC9;
  }
  .textarea-box {
    background-color: #181818;
    color: white;
  }
  a {
    color: #4ABCC9;
  }
  #VPNAdvert {
    display: none;
}
}
