css:
@-moz-document domain("bolshoyvopros.ru") {
* {
		border-radius: 0.4em  !important;
		font-family: Segoe Print, Tahoma !important ;
}
.item .left .data .tags a, .item .left .data .tags span {
    font-weight: bold;
    color: #4b4c4c;
	}
a, a:visited, a:link {
    font-weight: bold;
}
.best_users .title span {
    font-weight: bold;
    color: #619a08;
}
.top_ten .user_skills {
    font-weight: bold;
}
.categories .category .questions_count {
    font-weight: bold;
    color: #dc58ac;
}
.best_users .full_rating a {
    font-weight: bold;
    color: #619a08;
}
.online_stats_inside div.link {
    font-weight: bold;
}
div.paging span {
    font-weight: bold;
    color: #225656;
}
.footer .column div {
    font-weight: bold;
    color: #194824;
}
.footer .column a {
    font-weight: bold;
    color: #194824;
}

.question .user .time_asked {
    font-weight: bold;
    color: #4b4c4c;
}
.question .body .classify div {
    font-weight: bold;
    color: #4b4c4c;
}

.link_panel span.hover {
    font-weight: bold;
}
.link_panel {
    font-weight: bold;
    color: #bd4e85;
}
.transparent_tabs .tab.selected a {
    color: #18494e;
    background-color: #ffd507;
    text-shadow: 1px 1px 1px #7bafd7;
}
.answer .user_info .time_asked {
    font-weight: bold;
    color: #4b4c4c;
}
a:hover {
    font-weight: bold;
}

table.community table.params td {
    font-weight: bold;
    color: #4b4c4c;
}
table.community table.params span.gray {
    font-weight: bold;
    color: #ca42be;
}

.badges_bodypage .badges_list th {
    font-weight: bold;
    color: #4b4c4c;
}
.nav_tabs .title_left {
    font-weight: bold;
    color: #619a08
}

.header .auth_area .about {
    font-weight: bold;
    font-size: 14px;
}

.hidden_color {
    font-weight: bold;
    color: #4b4c4c;
}

.item .right a {
    font-weight: bold;
    color: #563356;
}
.item .right {
    font-weight: bold;
    color: #9c349c;
}

.link_panel .comment_button {
    font-weight: bold;
}

#notifications.classic_table .punishment_notification_details {
    color: #000000;
}
.classic_table td.time {
    color: #000000
}
}
