css:
@-moz-document domain("discordapp.com") {

  .channels-wrap {
      padding-left: 5px;
  }

  .private-channels .channel .avatar-small  {
      background-image: none !important;
  } 

  .avatar-small {
      width: 0;
  }

  .message-group {
      padding: 10px 0;
  }

  .message-group .avatar-large {
      margin: 0 15px;
  }

  .avatar-small .status {
      width: 2px;
      height: 15px;
      right: -3px;
      bottom: 7px;
  }

  .private-channels .status {
      border: 0;
  }

  .private-channels .channel.selected:before {
      border-left: 0;
  }

  .private-channels header, .guilds-wrapper .guilds .friends-online, .private-channels .channel .icon-friends, .channel-notices .channel-notice.invite, .protip.inline .label, .chat .welcome-message {
      display: none;
  }

  .typing {
      top: -24px;
      left: 20px;
      font-size: 8px;
      background: none;
  }
  
  .message-group .edit-message .edit-container-inner {
    margin-left: 25px;
  }

  .jump-to-present-bar button:nth-child(2n-1) {
      display: none;
  }

  .guilds-wrapper .btn-download-apps {
      zoom: 41%;
      margin-top: 20px;
  }

  .message-group {
      margin-left: 0;
  }

  .message-group.compact .timestamp {
      width: 30px;
  }

  .guilds-wrapper {
      width: 20px;
      padding: 5px 0 5px 5px;
  }

  .guilds-wrapper .guilds, .private-channels .scroller {
      padding: 0;
  }

  .guilds-wrapper .guilds .guild {
      width: 20px;
      height: 20px;
  }

  .guilds-wrapper .guilds .guild .guild-inner a {
      width: 20px;
      height: 20px;
      background-size: 100%;
  }

  .guilds-wrapper .guilds .friends-icon {
      width: 20px;
      height: 20px;
      background-size: 10px 25px;
  }

  .guilds-wrapper .guilds-add {
      font-size: 16px;
      top: 4px;
  }

  .guilds-wrapper .guilds .guild .guild-inner {
      border-radius: 0 !important;
      -webkit-border-radius: 0 !important;
  }

  .message-group.compact .message {
      margin: 0 0 2px 0;
  }

  .message-group.compact .message .markup {
      text-indent: -35px;
      padding-left: 37px;
  }

  .chat>.title-wrap {
      min-height: 0;
  }

  .guilds-wrapper .guilds .guild+.guild {
      margin-top: 0;
  }

  .guilds-wrapper .guild-separator {
      width: 20px;
  }

  .channel-members-wrap {
      min-width: 110px;
      width: 110px;
  }

  .channel-members, .channel-members .member {
      padding: 0;
  }

  .channel-members .avatar-small {
      height: 0;
  }

  .channel-members .avatar-small .status {
      bottom: -9px;
  }

  .channel-members h2 {
      font-size: 9px;
      padding: 0 10px;
  }

  .channel-members .member+h2 {
      margin-top: 10px;
  }

  .guild-channels .channel-text a, .guild-channels .channel-voice {
      padding: 0 0 0 5px;
  }

  .dropdown-button {
      margin-left: 10px;
  }

  .guild-channels header, .private-channels .channel.btn-friends, .chat form, .channel-textarea {
      margin: 0;
  }

  .private-channels .channel {
      height: 25px !important;
  }

  .private-channels .channel a {
      padding-left: 5px;
      height: 23px;
  }

  #friends .friends-header {
      height: 30px;
  }

  #friends .friends-table .friends-row {
      height: 20px !important;
      margin-left: 20px;
  }

  #friends .friends-table .friends-row:hover {
      padding: 0 10px;
      margin-left: 10px;
      margin-right: 10px;
  }

  .private-channels .search-bar {
      height: 32px;
      padding: 0;
  }

  .guilds-wrapper .guilds .guild.unread:before {
      width: 5px;
      height: 20px;
      border-radius: 0;
      background: red;
      margin-left: 21px;
      margin-top: -10px;
      z-index: 10000;
      animation: timing 5s infinite alternate;
  }

  .guild-channels .channel-text.unread:not(.selected):not(.channel-muted):before {
      top: 3px;
      left: -9px;
  }

  .guilds-wrapper .guilds, .private-channels .scroller {
      min-height: 284px;
      padding-bottom: 39px;
  }

  .need-help-modal .header {
      padding-bottom: 50px;
  }

  .need-help-modal .header input[type=text]{
      display: none;
  }

  .message-group.compact .emoji.jumboable {
      width: 30px;
  }

  .message-group.compact .accessory {
      padding-left: 36px;
  }

  .system-message .system-message-icon {
      margin-left: 7px;
  }

  .markdown-modal.selectable .scroller {
      background: #fff;
      height: 20px;
  }

  #friends .friends-table .friends-row .friends-column-actions .friends-action.friends-action-call, #friends .friends-table .friends-row .friends-column-actions .friends-action.friends-action-remove {
      min-width: 36px;
  }

  #friends .friends-header .tab-bar .tab-bar-item {
      min-width: 0px;
      padding: 2px 4px;
  }

  .channel-members-wrap .scroller-wrap .scroller {
      padding-bottom: 200px;
      margin-bottom: -170px;
  }

  .guild-channels-wrapper .scroller-wrap .scroller {
      padding-bottom: 100px;
      margin-bottom: -100px;
  }

  .context-menu .context-menu{
      overflow-y: auto;
  }

  .account {
      z-index: 1000;
  }

  .chat, #friends {
      z-index: 1000;
  }

  .context-menu, .modal .modal-inner, .create-guild-container, .user-popout, .context-menu {
      z-index: 10000;
  }

  .account .account-details .username{
      display: inline;
  }

  .account .avatar-small {
      width: 3px;
      background-image: none !important;
  }

  .guilds-wrapper .guilds .guild .badge {
      zoom: 70%;
  }

  .avatar-small .status-typing {
      left: -20px;
  }

  .channel-textarea-inner {
      padding: 7px 10px;
  }

  .chat .messages-wrapper {
      padding-bottom: 20px;
  }

  .guild-channels .channel-text.unread:not(.selected):not(.channel-muted):before {
      top: 3px !important;
  }

  ::-webkit-scrollbar
  {
      width: 0px;
  }

  @media screen and (max-width: 700px) {

    .message-group .avatar-large {
        width: 0;
        margin: 0;
        margin-top: -2px;
        margin-right: 20px;
    }

    .channels-wrap {
        background-color: #1e2124;
        width: 0px;
        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
        transition: 0.5s;
    }

    .channels-wrap:hover, .channels-wrap:active {
        width: 240px;
        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
        transition: 0.5s;
    }

    .channels-wrap:hover .guild-header header, .channels-wrap:active .guild-header header {
        padding: 13px 24px;
        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
        transition: 0.5s;
    }

    .guild-header header {
        padding: 0;
        height: 30px;
        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
        transition: 0.5s;
    }

    .account {
        height: 39px;
        width: 0;
    }

    .account .btn-group, .account .avatar-small .status {
        opacity: 0;
    }

    .channels-wrap:hover .account .btn-group, .channels-wrap:hover .account .avatar-small .status, .channels-wrap:active .account .btn-group, .channels-wrap:active .account .avatar-small .status {
        opacity: 1;
        -webkit-transition: 3s;
        -moz-transition: 3s;
        -o-transition: 3s;
        transition: 3s;
    }

    .channels-wrap:hover .account, .channels-wrap:active .account {
        width: 220px; 
        -webkit-transition: 0.5s;
        -moz-transition: 0.5s;
        -o-transition: 0.5s;
        transition: 0.5s;
    }

    .settings .settings-header{
        margin-right: -13px;
    }

    .context-menu .context-menu{
        overflow-y: auto;
        max-height: 250px;
    }
    
    .modal-image {
        zoom: 190%;
        margin-top: -40px;
    }
    
    .modal .settings {
        zoom: 90%;
    } 

    .modal {
        zoom: 70%;
    }

  }

  @media screen and (max-height: 450px) {

    .modal .modal-inner, .create-guild-container, .user-popout, .context-menu, .auth-center, .embed-thumbnail-video, .embed-thumbnail-video .image  {
        zoom: 70%;
    }

    .popout.popout-bottom-right.no-arrow, .popout.popout-bottom.no-arrow, .notice, .confirm-message-modal .protip {
        zoom: 70% !important;
    }

    .popout.popout-top-right.no-arrow, .popout.popout-top.no-arrow {
        zoom: 70% !important;
        top: 80% !important;
    }

    .modal .settings {
        zoom: 90%;
    } 

    .context-menu div{
        zoom: 100%;
    }

    .confirm-message-modal .comment {
        zoom: 50%;
    }

  }

}
