css:
@-moz-document domain("goodgame.ru") {
* {
    background-color: transparent !important;
}
html,
.sub-menu,
.settings-block,
.popup-block,
.settings-popup,
.tab-panel,
.nav-tabs,
.head-container,
.bg-block,
.feed-block .content-container,
.search-results-wrap,
.debug,
.tse-scrollable {
    background-color: #0D0D0D!important;
    color: #ccc;
}
.main-block .main-inner-wrap {
    margin-top: 0px;
}
td:hover,
a:hover,
.news-element:hover,
.thread-block:hover {
    background-color: rgba(28, 28, 28, 0.4) !important;
}
input,
.calendar-block,
.cke_chrome,
.cke_inner {
    background-color: rgba(28, 28, 28, 0.9) !important;
}
.search-block .search-input {
    border: 2px solid rgba(28, 28, 28, 0.3)
}
.tab-panel {
    background-color: rgba(28, 28, 28, 0.3) !important;
}
.info-block {
    background: rgba(28, 28, 28, 0.3) !important;
}
.news-block .news-list .news-element .info-block .update-block {
    background-color: rgba(28, 28, 28, 0.4)!important;
}
.wrapper {
    background: rgba(153, 153, 153, 0) !important;
}
.feed-block .tab-panel .nav-tabs.nav-justified li a .count {
    background-color: rgba(28, 28, 28, 0.6) !important;
}
}
