/* freefeed.net // friendfeed style
V1.00 2016/11/20
By : Mohammad Mirzaie */

@-moz-document domain("freefeed.net") {
    @font-face {
        font-family: Tahoma;
        font-weight: normal;
}
body{
    font-family: Tahoma;
    background: #EAEAEA;
    background-size: auto;
    background-attachment: fixed;
    float: none;

}    
    .comments {
    font-size: 12.5px;
    line-height: 21px !important;
}    
.post .post-body {
    margin-right: 63px;
}
.post .post-body .post-text {
    font-size: 13.5px;
    line-height: 21px !important;
}
.post .post-body .post-header {
    font-size: 12.5px;
    line-height: 21px !important;  
}
    
.posts {
    font-size: 12.5px;
    line-height: 21px !important;
}
    
.content .box-body{
    background-color: #fff!important;
        background-color: #e0e0e0;
    border-radius: 10px;
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}

.timeline-post-container {
    padding: 14px;
}

.timeline-post-container {
    padding: 14px;
    border: 1px solid gray;
    border-radius: 10px;
    margin: 10px;}
.sidebar .box-body{
    padding: 10px 10px 3px 0;
}

.box-header, .box-header-empty, .box-header-timeline, .box-header-friends, .box-header-groups {
    border: none;
}

.sidebar .box-body {
    background-color: #FFFFFF;

    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px;
}
 
div.box-header-timeline {
    font-size: 16px;
}

li.previous a, li.next a {
    background-color: #d1d1d1;
    color: #000000;
}
.box-header-friends {
    font-weight: bold;
    font-size: 15px;
    background: #289628;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 5px;
}
.box-header-search {
    font-weight: bold;
    font-size: 15px;
    border: none;
    background: #289628;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 5px;
}
.box-header-groups {
    border: none;
    background: #FF8300;
    color: white;
    border-radius: 5px 5px 0 0;
    padding: 5px;
}
div.box-header-timeline {
    font-size: 16px;
    background: #5393D8;
    color: white;
    padding: 5px;
    direction: ltr !important;
    border-radius: 10px 10px 0 0;
    margin-left: 0px !important;
}

.col-md-3 *, .sidebar, box *, .search-input, .box-header-timeline{
    font-family: Tahoma;
}
}
