@namespace url(http://www.w3.org/1999/xhtml);
/* Tinted Yucata */
/* This makes Yucata a bit darker to be easier on the eyes.
   It is a VERY conservative userstyle, but I'm happy with it.
   A first attempt could be worse. Also, new loading gif! */
/* RUMPYFLUMP */

@-moz-document domain("www.yucata.de") {
    body {
        background-color: #ddc796;
    }
    #ctl00_cphRightCol_ctl01_PortletWindow {
        display: none;
    }
    img[src='/netimages/c/FooterMeeple.png'] {
        display: none;
    }
    #master-tblmiddle {
        background-color: #ddc796;
    }
    .sf-menu a {
        padding: 9px 9px;
        text-decoration: none;
    }
    div.portletheader,
    #master-menu,
    .sf-menu a,
    .sf-menu a:visited {
        background: #00305e;
        border-left: 2px solid #00305e;
        color: #eee;
    }
    table.display thead th {
        background-color: #2f60a3;
    }
    tr.even {
        background-color: #91c1f1;
    }
    tr.even td.sorting_1 {
        background-color: #91c1f1;
    }
    .page {
        background-repeat: repeat;
        background-image: url(http://i.imgur.com/4KgZ4VD.jpg);
    }
    .loadingindicator {
        width: 32px;
        height: 32px;
        background: url('http://i.imgur.com/QNrBX3U.gif') no-repeat;
        margin-left: auto;
        margin-right: auto
    }
    #master-tblleft {
        background-color: #e2e4ff;
    }
    .button {
	color: #111111;
    }
    #BuddyList {
        font-size: 10pt;
        background-color: #e2e4ff;
    }
}
