@-moz-document url-prefix("https://static-course-assets.s3.amazonaws.com") {
.overlay-header,
#page-header,
#page-footer {
  background: #17A0DC;
  background: -webkit-linear-gradient(left, #202020 1%,#202020 100%);
  background: -moz-linear-gradient(left, #202020 1%, #202020 100%);
  background: -ms-linear-gradient(left, #202020 1%,#202020 100%);
  background: -o-linear-gradient(left, #202020 1%,#202020 100%);
  background: linear-gradient(to right, #202020 1%,#202020 100%);
  height: 54px;
  position: absolute;
  right: 0;
  left: 0;
}

#text-container {
    background-color: #202020 !important;
    z-index: 1;
}

#text h1{
	color: #ffffff;
}

#text-container{
	color: #ffffff;
    border-top: 1px solid white;
    border-bottom: 1px solid white;
}

div#content {
    left: 0 !important;
    width: 100% !important;
}

