#page-content{
	background-color: #202020 !important;
}

/*Map  Hovered topic*/
ul.items a:focus, ul.items a:hover {
    background-color: rgba(239, 151, 26, 0.94);
}
/*Map selected topic*/
#map a.selected {
    background-color: rgba(239, 151, 26, 0.94);
}
  /* Bottom Buttons */
.menu-button:focus .menu-button-icon, .menu-button:hover .menu-button-icon, .menu-button.active .menu-button-icon {
    background-color: #ef971a;
}
  /* Button Lesson open */
.page-menu button:enabled:focus, .page-menu button:enabled:hover, .overlay-header button:enabled:focus, .overlay-header button:enabled:hover {
    background-color: #ef971a;
}

.scroll-pane-up-button:hover {
    background-image:url(https://i.imgur.com/Inyf1f3.png);
}

.scroll-pane-down-button:hover {
    background-image:url(https://i.imgur.com/CBXZefH.png);
}

.overlay-icon {
    background-color: #ef971a;
}

.index-button.active .index-button-icon {
    background-color: #ef971a;
}

.help-button:focus .help-button-icon, .help-button:hover .help-button-icon, .help-button.active .help-button-icon, .index-button:focus .index-button-icon, .index-button:hover .index-button-icon, .index-button.active .index-button-icon {
    background-color: #ef971a;
}

a, a:link, a:visited {
    color: #ef971a;
}
a {
    color: #ef971a;
    text-decoration: none;
}

.logo {
  background-image: url('http://i.imgur.com/LGdtTYM.png');
  height: 64px;
}

