css:
@-moz-document url-prefix("http://vk.com"), url-prefix("https://vk.com") {
  
/* Фикс иконки в диалогах в тёмной теме */
.im-page .im-page--center-empty {background: url(https://vk.com/images/icons/first_comment.png) no-repeat top !important;}

/* Фикс цвета заголовка ответа в тёмной теме (material beta) */
.feedback_header {color: #777;}

/* Фикс заголовков внешних ссылок в тёмной теме */
.page_media_link_title {color: #eee !important;}

/* Фикс заголовка прочтённых ответов в тёмной теме */
.feedback_unread_bar {color: #fff; background: #292929; border-bottom-color: #333; border-top-color: #333; margin-bottom: 0px;}

/* Изменение отступа сайдбара справа в тёмной теме (material beta) */
.side_bar_inner {width: 157px !important;}

/* Фикс заголовка заметки в тёмной теме */
.wall_note_type {color: #eee;}

/* Фикс подсветки при поиске аудио, видео и людей в тёмной теме */
.audio_search_focused .audio_performer>em, .audio_search_focused .audio_title>em, .video_items_list em, .wdd_hl {background-color: #666 !important;}

/* Фикс бордюра табов в тёмной теме */
.ui_tab_sel, .ui_tabs .ui_tab_sel, .ui_tabs_box .ui_tab_sel {border-bottom: 2px solid #666}
.ui_tab_sel:hover, .ui_tabs .ui_tab_sel:hover, .ui_tabs_box .ui_tab_sel:hover {border-bottom: 2px solid #bbb}

/* Фикс цвета ввода сообщения в диалоге, счётчика в уведомлении и заголовка-ссылки окна */
.im-chat-input .im-chat-input--text *, .top_nav_btn b, a.ui_box_header_link {color: #fff !important;}

/* Фикс отображения сноски новых сообщений в диалоге */
.im-to-end .im-to-end--label {background-color: #333;}
.im-to-end:hover .im-to-end--label {background-color: #444;}
.im-to-end, .im-to-end:hover {background-color: #1c1c1c; padding: 6px;}

/* Фикс табов стены в тёмной теме (Material beta) */
wall_tabs .ui_tabs, #wall_tabs .ui_tabs .ui_tab_sel, .bt_comment_form {background-color: #222;}

/* Фикс фона друга при наведении, в окне скрытия друзей, в тёмной теме (Material beta) */
.flist_full .flist_item_wrap:hover {background: #333;}

/* Изменение фона поля на "как видят вашу страницу" в тёмной теме (Material beta) */
#view_as_dd input {background: #1c1c1c; color: #fff;}

/* Порядок в боксе поиска аудио и видео для вложения */
.video_item .video_item_thumb {border-radius: 8px;}
.video_item .video_item_thumb_wrap {background-color: transparent;}
.idd_wrap .idd_selected_value {color: #bbb;}
.idd_wrap .idd_item.idd_hl {background-color: #181818;}

/* Правки для плеера в боксе поиска */
#choose_audio_content ._audio_player_wrap {margin-top: -2px; padding-bottom: 2px;}
#choose_audio_content .audio_row {padding: 4px 10px 6px 10px;}
#choose_audio_content .audio_inline_player .slider .slider_handler, .audio_page_player .slider .slider_handler {width: 12px !important; height: 12px !important; top: -4px !important; margin-left: -4px !important;}
.slider .slider_back {margin-left: 1px;}
.audio_inline_player .audio_inline_player_right {margin-left: 7px; margin-right: 3px;}
.audio_inline_player .audio_inline_player_left {padding: 0 8px;}
#choose_audio_content .audio_row .audio_title_wrap {padding: 0px 6px 0;}

/* Откат к стандартным кнопкам плеера (в beta сломаны) */
.audio_play {border-radius: 50% !important; background: #222324 url(https://vk.com/images/icons/audio_playpause.png) no-repeat 8px 7px !important;}
.audio_play.playing, .audio_row_playing .audio_play {background-position: 8px -15px !important;}
div:not(.audio_rows) .audio_row .audio_play_wrap {padding: 4px 6px 9px 0;}

/* Ширина плеера в шапке */
.top_audio_player {width: 100%;}

/* Тёмное оформление уведомлений */
.notifier_baloon {background: #3b3b3b; color: #fff;}
#notifiers_wrap a, .notifier_baloon_msg span.group_link, .notifier_baloon_msg span.mem_link {color: #eee;}

/* Фикс отображения заявок в друзья */
#friends_req_block {z-index: 6;}
.friends_user_request {margin-top: 80px;}

/* Фикс отображения ссылок в мини-диалоге */
.rb_box_wrap .fc_msg_attachments {margin: 0;}

/* Фикс фона поисковой строки в шапке */
#ts_input:hover, #ts_input:focus {background-color: #0e0e0e;}

/* Фикс заголовка диалога */
.im-page--chat-header {background: #222;}
.im-page--toolsw {background: none; border-color: #333}
.im-page--title-main-in, .im-page--title-main-inner {color: #eee;}

/* Правки цвета для новых кнопок постов */
.post_like, .post_reply, .post_share {color: #aaa;}
.post_like:hover, .post_reply:hover, .post_share:hover {color: #ddd;}
.wall_module .post_like:hover, .wall_module .post_reply:hover, .wall_module .post_share:hover {background: #191919;}
.post_full_like_wrap ._icon, .like_wrap ._icon {filter: grayscale(1) !important; -webkit-filter: grayscale(1) !important;}

/* Правка цвета ссылки действий с приложением в тёмной теме */
.help_tile_alert_urgent .help_tile_alert__title, .tu_mem, .app_actions_menu_wrap {color: #ddd;}

/* Цвет при наведении на трэк в музыке (by user 176665) */
.audio_row:not(.audio_row_current):hover .audio_row_inner {background-color: #141414;}

/* Цвет при выборе на трэка в музыке (by user 176665) */
.audio_row.audio_row_current .audio_row_inner, .audio_row.audio_row_playing .audio_row_inner, .audio_row.audio_row_current .audio_row_inner:hover, .audio_row.audio_row_playing .audio_row_inner:hover {background-color: #141414;}

/* Фон фиксированного плеера при прокрутке вниз (by user 176665) */
.audio_page_player.audio_page_player_fixed {background-color:#151515;}

/* Фон нижней части плей-листа (by user 176665) */
.audio_pl_snippet {background-color: #404040;}

/* Фон верхней части плей-листа (by user 176665) */
.audio_pl_snippet .audio_pl_snippet__header {background-color: #d2d2d2;border-bottom: 1px solid #6b6b6b;}

/* Фон при редактировании блока (где обложка) (by user 176665) */
.audio_pl_edit_box .ape_header {background-color: #292929;}

/* Наведение на кнопку "Добавить аудиозаписи" (by user 176665) */
.audio_pl_edit_box .ape_add_audios_btn:hover, .audio_pl_edit_box .ape_add_pl_audios_btn:hover {background-color: #4e4e4e;}

/* Часть фона выезжающего плеера (by user 176665) */
.audio_layer_container .audio_page_player {background-color: #222222;border-bottom: 1px solid #414141;}

/* Фон значка ноты выезжающего плеера (by user 176665) */
.audio_page_player .audio_page_player__cover {background-color: #222222;}

/* Значок ноты у трэка (by user 176665) */
.audio_row .audio_row_cover_wrap {background-image: url(http://ipic.su/img/img7/fs/audio_row_placeholder.1493821886.png);}

/* Вертикальный разделитель в музыке (by user 176665) */
.audio_page_layout .has_friends_block .audio_page__rows_wrap {border-right: 1px solid rgb(100, 100, 100);}

/* Горизонтальный разделитель в музыке (by user 176665) */
.audio_page_layout .audio_page_separator {background-color: rgb(100, 100, 100);}

/* Фикс типа о новом музыкальном разделе */
.audio_section_promo__title {color: #eee;}
.audio_section_promo__link {background-color: #666;}
.audio_section_promo__link:hover {background-color: #777;}

/* Убран лишний фон кнопок */
.audio_page_player_btn:not(.audio_page_player_share) .btn_icon {background: none !important;}
.audio_page_player .audio_page_player_shuffle, .audio_page_player .audio_page_player_repeat, .audio_page_player .audio_page_player_recoms, .audio_page_player .audio_page_player_status, .audio_page_player .audio_page_player_add {height: auto !important;}

/* Фикс кнопок плеера в шапке */
.top_audio_player.top_audio_player_playing .top_audio_player_play > div {background-position: initial !important;}
.top_audio_player .top_audio_player_btn {padding-top: 18px !important;}
.top_audio_player .top_audio_player_play {padding-top: 0 !important;}

/* Фикс цвета кнопки поиска */
.ui_search_new.ui_search_field_empty .ui_search_button_search {filter: invert();}

/* Фикс закрепления плеера при прокрутке страницы */
.audio_page_player_fixed, .audio_page_player_fixed_shown {padding-top: 14px;}

/* Убираем бессмысленную стрелку на кнопке музыки */
.eltt .eltt_arrow, .eltt .eltt_arrow_back {display: none;}

}
