:root {
	--bg: #313744;
}




@-moz-document url("chrome://devtools/content/inspector/markup/markup.xhtml") {
    span[class="tag-state"][role="presentation"], body {
		background: var(--bg) !important;
    }
}


@-moz-document url("chrome://devtools/content/inspector/inspector.xul") {
	#ruleview-container {
		background: var(--bg) !important;
    }
}




@-moz-document url("chrome://devtools/content/webconsole/webconsole.xul") {
    .message, #output-wrapper {
		background: var(--bg) !important;
    }
}

@-moz-document url("chrome://devtools/content/debugger/debugger.xul") {
    #sources-pane > tabpanels > tabpanel > vbox > scrollbox {
		background: var(--bg) !important;
    }
}

@-moz-document url("chrome://devtools/content/styleeditor/styleeditor.xul") {
    ol.splitview-nav,
    .stylesheet-media-container {
		background: var(--bg) !important;
    }
}

@-moz-document url("chrome://devtools/content/performance/performance.xul") {
    .side-menu-widget-container,
    #empty-notice,
    #recording-notice {
		background: var(--bg) !important;
    }
}

@-moz-document url("chrome://devtools/content/netmonitor/netmonitor.xul") {
    .side-menu-widget-container,
    #all-headers,
    #all-cookies,
    #request-params,
    #response-content-textarea-box,
    #timings-tabpanel > vbox,
    #security-tabpanel > vbox {
		background: var(--bg)  !important;
    }
}

@-moz-document url("chrome://devtools/content/storage/storage.xul") {
    #storage-tree,
    .table-widget-body {
		background: var(--bg) !important;
    }
}

@-moz-document url("chrome://stylish-devtool/content/panel.html") {
    .devtools-sidebar,
   .devtools-main-content {
		background: var(--bg) !important;
    }
}

@-moz-document regexp("data:text/html;.*/codemirror/.*") {
    /* span[class="tag-state"][role="presentation"], */
    .CodeMirror-gutter.CodeMirror-linenumbers,
    .CodeMirror-gutter.CodeMirror-foldgutter,
    .CodeMirror-scroll {
		background: var(--bg) !important;
	}
}
