css:
@-moz-document domain("lernu.net") {
/* Start document domain */

body {
    color: #a7a7a7;
    background: #222;
}
.well {
    background-color: #444;
}
.toolbar {
    background-color: #333;
}
.toolbar.affix {
    border-bottom: 1px solid #444;
}
.toolbar ul.nav > li > a {
    color: #A8A8A8;
}
.section-content a {
    color: #dadada;
}
.logo.svg svg {
    color: #101010;
}
.timeline li .timeline-panel {
    background: #333;
    border: 1px solid #484848;
}
.timeline li .timeline-panel:after {
    border-left: 14px solid #333;
    border-right: 0 solid #333;
}
.timeline li .timeline-panel:before {
    border-left: 15px solid #676767;
    border-right: 0 solid #676767;
}
.timeline:before {
    background-color: #444;
}
.timeline li .tl-circ {
    background: #092344;
    border: 3px solid #444;
}
.timeline .thumbnail {
    background-color: transparent;
}
.section-yellow {
    background-color: #474229;
}
img {
    opacity: 0.4;
}
.cards .lesson-status {
    background-color: rgba(34, 34, 34, 0.8);
    z-index: 100;
}
.cards .card.lesson-disabled:hover {
    background-color: transparent !important;
}
.section-intro h1 {
    color: #999;
}
.section-intro .frame {
    color: #999;
}
.section-inverse {
    background-color: #666;
    color: #fff;
}
.section-content .content-block-1 {
    background-color: #73454a;
}
.section-content .content-block-2 {
    background-color: #9c7839;
}
.section-content .content-block-3 {
    background-color: #216760;
}
.section-content .content-block-4 {
    background-color: #38596f;
}
.section-important {
    background-color: #843a33;
}
.cards .card a {
    color: #dadada;
}
.cards .card.lesson-item {
    border: 1px dashed #2C2C2C;
}
.cards .card.sb:hover:not([data-sound=""]),
.cards .card:hover:not([data-sound=""]) {
    background-color: #444;
    color: #dadada;
}
.cards .lesson-done {
    background-color: #333;
}
.cards .lesson-progress {
    position: absolute;
    background-color: #133B37;
    ;
    border-right: 1px solid #133B37;
}
#lesson-content.lesson-done {
    background-color: #19322F;
}
.sentence,
sentence {
    color: #896E38;
}
.sentence:hover,
sentence:hover {
    background: none;
    color: #3f8a84 !important;
}
var:hover,
var:hover {
    background-color: #222;
    color: #dadada;
}
.seek-bar.seek-bar-display {
    background-color: #333;
}
.play-bar {
    background-color: #133B37;
}
.control {
    background-color: #26425c;
}
.button {
    color: #5D5D5D;
}
.audio {
    color: #989898;
}
.breadcrumb {
    background-color: #333;
    border-radius: 0;
}
#lesson-content.lesson-done.lesson-error-2 {
    background-color: #474229;
}
#lesson-content.lesson-done.lesson-error-1 {
    background-color: #42201d;
}
.form-control {
    color: #959595;
    background-color: #333;
    border: 1px solid #333;
}
.input-mode {
    border: none;
    border-bottom: 1px solid #333 !important;
}
.exercise .drag-items {
    border: 1px dashed #2C2C2C;
    background-color: #333;
}
.exercise .drag-item {
    color: #fff;
    background-color: #444;
    border-color: #333;
}
.lesson-progress-indicators ol li.done {
    background-color: #47603e;
    border-color: #698a5d;
}
.lesson-progress-indicators ol li {
    border: 1px solid #7a2b2b;
    background-color: #685610;
}
.lesson-progress-indicators ol li.active {
    background-color: #803830;
    border-color: #803830;
}
.lesson-progress-indicators ol li.disabled {
    border: 1px solid #5d5757;
    background-color: #444;
}
.input-mode:focus {
    border-color: #66afe9;
    background-color: #605c44;
    -webkit-box-shadow: none;
    box-shadow: none;
    color: #dadada;
}
#header {
    background-color: rgb(28, 66, 63);
}
#footer {
    background-color: #333;
    color: #999;
}
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus {
    color: #BABABA;
    background-color: #255651;
}
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus {
    color: #BABABA;
    background-color: #255651;
}
.menu.navbar-default .navbar-nav > li.active > a {
    color: #BABABA;
}
.navbar-default .navbar-nav > li > a:hover,
.navbar-default .navbar-nav > li > a:focus {
    background-color: #295F59;
    color: #fff;
}
.btn-primary,
.opt-item input:checked + label {
    border: 1px solid #ff523e;
    background-color: #4e1d17;
    color: #ff523e;
    text-decoration: none;
}
.btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active,
.btn-primary:active:hover,
.btn-primary.active:hover,
.btn-primary:active:focus,
.btn-primary.active:focus,
.btn-primary.focus:active,
.btn-primary.active.focus {
    color: #fff !important;
    border: 1px solid transparent;
    background-color: #ff7364;
}
.btn-default,
.opt-list label {
    background-color: #14314d;
    color: #bbbbbb;
    border-color: #bbbbbb;
}
.btn-default:hover,
.btn-default:focus {
    background-color: #345e87;
    color: #14314d;
    border-color: #14314d;
}
.btn-white:hover,
.btn-white:focus {
    background-color: #216760 !important;
    color: #fff !important;
}
.modal-content {
    background-color: #222;
}
.modal-header {
    border-bottom: 1px solid #222;
}
.modal-footer {
    border-top: 1px solid #222;
}
.toolbar ul.nav > li > a:hover,
.toolbar ul.nav > li > a:focus {
    color: #448b85;
    background-color: transparent;
}
@media screen and (min-width: 992px) {
    .section-intro-content {
        background-color: rgba(19, 59, 55, .9);
    }
}
.table-hover > tbody > tr:hover {
    background-color: #171717;
}
.table>thead>tr>th {
    border-bottom: 2px solid #585858;
}
.table>thead>tr>th,
.table>tbody>tr>th,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>tbody>tr>td,
.table>tfoot>tr>td {
    border-top: 1px solid #585858;
}
.pagination .disabled > span,
.pagination .disabled > span:hover,
.pagination .disabled > span:focus,
.pagination .disabled > a,
.pagination .disabled > a:hover,
.pagination .disabled > a:focus {
    color: #737373;
    background-color: #444;
    border-color: #545454;
}
.pagination li > a,
.pagination li > span {
    color: #a7a7a7;
    background-color: #555;
    border: 1px solid #6d6d6d;
}
.pagination li > a:hover,
.pagination li > span:hover,
.pagination li > a:focus,
.pagination li > span:focus {
    color: #fff;
    background-color: #444;
}
.pagination .active > a,
.pagination .active > span,
.pagination .active > a:hover,
.pagination .active > span:hover,
.pagination .active > a:focus,
.pagination .active > span:focus {
    background-color: #803830;
}
.ui-slider {
    background-color: #444;
    box-shadow: 0 0px 0 rgba(255, 255, 255, 0.5), 0px 1px 2px rgba(0, 0, 0, 0.4) inset;
}
.ui-slider .ui-slider-range {
    box-shadow: 0 0px 0 rgba(255, 255, 255, 0.5), 0px 1px 2px rgba(0, 0, 0, 0.4) inset;
    background-color: #133B37;
}
.ui-slider .ui-slider-handle {
    background-color: #26425c;
    border: 1px solid #434343;
}
.media-text .sentence:hover,
.media-text sentence:hover {
    background: none;
    cursor: inherit;
    color: #3f8a84 !important;
}
.select2-container--bootstrap .select2-search--dropdown .select2-search__field,
.select2-container--bootstrap .select2-selection {
    border: 1px solid #6f6f6f;
    background-color: #333;
    color: #999;
}
.select2-dropdown {
    background-color: #333333;
    border: none;
}
.select2-container--bootstrap .select2-results__option[aria-selected=true] {
    background-color: #222;
    color: #a7a7a7;
}
.select2-container--bootstrap .select2-results__option--highlighted[aria-selected] {
    background-color: #666;
    color: #fff;
}
.select2-container--bootstrap .select2-selection--single .select2-selection__rendered {
    color: #a7a7a7;
}
.dropdown-menu {
    background-color: #333;
}
.dropdown-menu>li>a {
    color: #a8a8a8;
}
.dropdown-menu .divider {
    background-color: #444;
}
.dropdown-menu>li>a:hover,
.dropdown-menu>li>a:focus {
    color: #dadada;
    background-color: #666;
}
.nav .open>a,
.nav .open>a:hover,
.nav .open>a:focus {
    color: #dadada !important;
    background-color: #666;
    border-color: #444;
}
.section-content a:hover {
    text-decoration: none;
    -webkit-box-shadow: 0px 10px 49px 11px rgba(0, 0, 0, 0.8);
    -moz-box-shadow: 0px 10px 49px 11px rgba(0, 0, 0, 0.8);
    box-shadow: 0 10px 49px 11px rgba(0, 0, 0, 0.8);
}
.bs-callout-warning {
    border-left-color: #a27014;
}
@media screen and (min-width: 1200px) {
	h2 {
    	color: #a27014;
	}
}
h2 {
    color: #a27014;
}
h1 {
    color: #a27014;
}
.bbcode blockquote {
    border-left: solid rgb(33, 99, 94) 1px;
    background-color: rgb(28, 66, 63);
}
/* End document domain */
}
