css:
@-moz-document domain(sitepoint.com) {
    /* your code goes here */

body, h1, h2, h3, h4, .u0Summary,
.u100Frame .u100Entry .title, 
h1 .hhint,
.slabserif .entryBody,
.inlineFrame .u100Entry .title{font-family: "Helvetica Neue", Microsoft Yahei, Helvetica, Arial, sans-serif} 
.inlineFrame .u100Entry .title {font-weight:normal}

#mainBar {margin-left:0}
h1 .hhint {margin-top:10px; color:#ccc}
.u0Entry {height:60px; padding:10px 10px 20px 10px}
.u0Entry .title {font-size:16px; font-weight:normal;}
.u0Entry.selectedEntry, .u0Entry.quicklisted.selectedEntry {border-color:#eee; border-radius:3px}
.u0Summary {display:block; padding-left:70px; color:#bbb; max-height:35px; overflow:hidden}
#realtimeSign {margin-bottom:0}
.entryBody,
.u100Entry {max-width:1000px}
.u0Entry .sourceInfo {width:auto}
.headerInfo {border:none;}
.shareBarHolder {margin:-80px 0 0 0; float:right;}
.visual {border:none;}
.headerInfo.headerInfo-article {margin-top:0}
.condensed .inlineFrame {padding:40px 0 0 80px; box-shadow:1px 1px 15px #ddd;}
.entryBody p {line-height:2}
.condensed .entryholder .u100Entry {margin-left:0}
.u0Entry:hover .u0Summary {color:gray;}
.recommendationInfo {}
#feedlyTabsHolder .header .nonEmpty {color:#aaa}
#feedlyPart0.area {padding-left:30px}

::-webkit-scrollbar {width:12px; height:12px }
::-webkit-scrollbar-button:vertical { display:none }
::-webkit-scrollbar-track:vertical { background-color:black }
::-webkit-scrollbar-track-piece { background:#ffffff }
::-webkit-scrollbar-thumb:vertical { background-color:#8e8e8e; border-radius:5px}
::-webkit-scrollbar-thumb:vertical:hover { background-color:#3b3b3b }
::-webkit-scrollbar-corner:vertical { background-color:#535353 }

body ::-webkit-scrollbar {width:7px; height:7px }
body ::-webkit-scrollbar-button:vertical { display:none }
body ::-webkit-scrollbar-track:vertical { background-color:black }
body ::-webkit-scrollbar-track-piece { background:#eee }
body ::-webkit-scrollbar-thumb:vertical { background-color:#8e8e8e; border-radius:5px }
body ::-webkit-scrollbar-thumb:vertical:hover { background-color:#3b3b3b }
body ::-webkit-scrollbar-corner:vertical { background-color:#535353 }

.tagsHolder,
.headerInfo-links.right {display:none}
#proAdModule_part {position:absolute; top:-1000px;}
  }
