css:
/*--------------------------------------------------------------------------------
  ggyy8 comic only view mode
  --------------------------------------------------------------------------------
  Author: Louis H - Tested on: Windows XP Firefox 3.5.3 - Last Update: 15.09.2009
  --------------------------------------------------------------------------------
  Description:
    Show only the image and navigation bar when watching comics
  --------------------------------------------------------------------------------*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ggyy8.com"), domain("ggyy8.cc") {

.c_title, div[class^="banner"], iframe[src^="http://js.ggyy8"], #_u114qq {
display:none !important;
}

.newscont {
position: absolute !important;
top:-2px !important;
left:0px !important;
border: 0 !important;
padding: 0 !important;
}

.newscont > iframe {
display:none !important;
}

#tspagelink {
position: fixed !important;
bottom:0 !important;
left:0;
right:0;
background:#fff;
}

}
