@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("crunchyroll.com") {
*
{
    text-shadow:none!important;
	text-decoration:none !important;
    box-shadow:none!important;
    border-radius:0px!important;
}
@font-face
  {
   font-family: 'Roboto';
   font-style: normal;
   font-weight: 300;
  }
* {font-family: 'Roboto' !important;}
body,.new-template-body,textarea,ul.news-top-stories-row li a,.anime-lineup-blurb,span,ul.news-abbr .byline
{
	color: #a7a7a7;	
}
img.portrait,img,.thumb-group-small-mug a img,.user-profile-thin-gb .guestbook-mug img,.thumb-user-small-mug a img,.search-video-thumbs .media-thumb-wide,.welcome-crnews-mug img, .welcome-news-mug img,.welcome-media-mug img,.newsfeed-bottom-nav
{
    border: 0px solid red;
}
/*-review-*/
#showview_content_reviews .no_reviews_notification
{
	background-color: #181818;
}
.default-button, a.default-button, input.default-button, span.default-button
{
    color: #fae155;
    border: 1px solid #fae155;
    background: transparent;
}
/*-comentarios-*/
textarea
{
	background: #151515;
	border: 0px solid red;
}
textarea.text-input
{
    border: 0px solid red;
}
/*-----header-----*/
.site-header
{
    background: #1B1B1B;
}
.header-menubar li a
{
    color: #d8aa7f;
}
input
{
	background-color: #202020;
	color: #7e7876;
}
.header-search-form
{
	border: 0px solid red;
	border-left: 2px solid #7e7876;
}
/*--search--*/
.header-search-autocomplete
{
    border: .0625rem solid #2f2f2f;
    background: #151515;
}
.header-search-autocomplete a
{
	border-bottom:.0625rem solid #2f2f2f;
	border-right:.0625rem solid #2f2f2f;
}
.header_search_result_name
{
    color: #fae155;
}
.header_search_result_type
{
    color: #ff6969;
}
.header-search-autocomplete a:focus, .header-search-autocomplete a:hover, .header_search_autocomplete_item_focused
{
    background-color: #2f2f2f;
}
.search-body
{
	border-left: 1px solid #2f2f2f;
}
ul.search-tabs .highlight a, ul.search-tabs a:hover
{
    background-color: #151515;
}
ul.search-tabs a
{
    color: #0a6da4;	
}
.search-video-thumbs .series,.search-results .name
{
    color: #bb4343;
}
.search-video-thumbs .name
{
    color: #2f5d80;
}
/*-message box-*/
.message-container
{
    background: #293940;
}


.search-form-channel input[type=text]
{
    border: 0px solid #888;
    border-left: 2px solid #fae155;
}
/*-----body-----*/
body.main-page
{
    background: url('/*[[background]]*/') center center  no-repeat fixed !important;
    background: transparent none repeat scroll 0% 0%;
    background-size: cover !important;	
}
#template_skin_leaderboard,span#_fw_container_SKIN,#template_skin_splashlink
{
    display: none !important;
}
#template_container.template-container,#template_body.old-template-body,.lineup-container,#main_content .landscape-element,#source_manga_showview #main_content .landscape-grid li .wrapper
{
    background: #1B1B1B;
}
/*-content-*/
.welcome-simulcasts .welcome-simulcasts-collection a
{
    color: #1d4f73;
}
.welcome-simulcasts .welcome-simulcasts-episode:hover
{
    background-color: #313131;
}
.welcome-simulcasts .welcome-simulcasts-episode
{
    color: #92bd8d;
}
.show-all-simulcasts-button:hover {
    border-color: #161616;
}
.show-all-simulcasts-button
{
	background: #161616;
    border: 1px solid #161616;
	color: #fae155;
}
/*-ultimas noticias-*/
.welcome-crnews-title, .welcome-news-title
{
    color: #3c82b5;
}
/*-series nuevas-*/
.welcome-latest-videos .series
{
    color: #d04a4a;
}
.welcome-latest-videos .name
{
    color: #fae155;
}
.welcome-latest-videos .ordernum
{
    color: #36b7c5;
}
/*-----sidebar-----*/
#welcome_left
{
	border-right: 1px solid #2f2f2f;
}
#sidebar,.welcome-countdown-day,.welcome-crnews-item, .welcome-news-item,.old-template-body .cr-header,ul.news .news-date-label,.showcrunchynews .news-header2,.showcrunchynews .tag-header,.showcrunchynews .comments-header,.forum-category-head, div.forum-category-head,.forum-category-head, h2.forum-category-head,.showforumtopic-postreply-header,.user-profile-header,.user-profile-link,.user-profile-box h3,.library-category-header,.welcome-recent-episodes .welcome-recent-link,.manga-countdown-items,ul.newsfeed .news-date-label,.showcrunchynews .top-news
{
    border-bottom: 1px solid #2f2f2f;
}
#sidebar h3
{
    color: #FFC470;
}
.landscape-grid .wrapper:hover
{
    background: #2f2f2f;
}
.old-template-body a.block-link, a.block-link
{
    color: #bd5067;
}
.welcome-block h1, .welcome-title-list h1
{
    border-bottom: 0px solid red;
}
.welcome-block h1 a, .welcome-title-list h1 a
{
    color: #8DA8FF;
}
.welcome-forum-category
{
    border-bottom: 0px solid red;
}
.welcome-forum-category a
{
    color: #90CFEB;
}
.welcome-pulse-box a
{
    color: #85a06d;
}
.welcome-pulse-box a:hover
{
    background-color: #313131;
}
.welcome-countdown-items a
{
    color: #bd5067;
}
.welcome-countdown-items a:hover
{
    background-color: #313131;
}
.welcome-recent-link a
{
    color: #1d4f73;
}
/*-----footer-----*/
#footer_menu
{
    background-color: #181818;
}
#footer_menu
{
    display:/*[[mostrarwea]]*/;
}
/*-------------------------------secciones-------------------------------*/
/*-----emisiones simultaneas-----*/
.main-tabs,#tabs,.main-tabs a:hover,.main-tabs a.selected, .main-tabs a.selected:hover,.main-tabs a
{
	background: transparent;
}
.main-tabs a.selected
{
    color: #8DA8FF;
}
.main-tabs a
{
    color: #5d73bb;
}
.portrait-element,#store_items li
{
    background: #151515;
}
.wrapper .portrait-element:active, body .wrapper .landscape-element:active
{
    background: #252525!important;
}
.new-template-body h3,.guestbook-list,ul.news .body,.news-footer-more,.search-results a,#source_manga_showview .more-options,ul.newsfeed .body
{
	border-top: 1px solid #2f2f2f;
}
/*-serie destacada - otros espectaculos populares-*/
 .old-template-body a.block-link, a.block-link,.new-template-body a.block-link
{
    color: #bf4f4f;
}
/*-----ver capitulo-----*/
#main_content_white, .white-wrapper
{
    background: #181818;
}
#showmedia_about_info_details span
{
    color: #fae155;
}
.guestbook-sort-bar {
    border-top: 1px solid #181818;
    padding-top: 3px;
    background-color: #181818;
}
.guestbook-spoiler-checkbox
{
    color: #ef3333;
}
/*-----animes-----*/
#source_showview .season-dropdown.open,#source_showview .season-dropdown
{
    color: #fae155;
    background-color: #181818;
	border: 1px solid #fae155;
}
/*-----noticias news-----*/
ul.topbar-nav-sub
{
    background-color: #1b1b1b;
}
ul.topbar-nav-sub li.highlight a
{
    background-color: #fae155;
}
ul.news-top-stories-row li a
{
    border: 0px solid red;
    background: #151515;
}
ul.news h2 a
{
    color: #3ead37;
}
ul.news .news-date-label
{
    color: #fae155;
}
ul.news li.news-item-pegged,ul.news li.news-item,ul.news-abbr,.showcrunchynews .bottom-nav,.orderstatus-grid td
{
	border: 1px solid #2f2f2f;
}
.news-bottom-nav
{
    border: 0px solid red;
    background: #151515;
}
.news-header-right a
{
    color: #469394;
}
.showcrunchynews .previous-label,.showcrunchynews .next-label
{
    color: #fae155;
}

#slidebox
{
    background-color: #151515;
    border: 1px solid #167ac6;
}
#slidebox h2 a
{
    color: #82c1c1;
}
.showcrunchynews h1 a
{
    color: #bfaa34;
}
ul.newsfeed li
{
    border: 0px dotted red;
    border-bottom: 2px solid #2f2f2f !important;
}
ul.newsfeed h2 a
{
    color: #1d9a39;
}
/*-----calendario-------*/
.day.active .calendar-day
{
    background: #151515;
}
.calendar h1>a
{
    color: #655d5b;
}
h1.day-name
{
	color: #de4837;
}
.calendar .pagination-arrow,.simulcast-lineup-toggle .mode-button
{
    background-color: #121212;
}
.simulcast-lineup-toggle .mode-button
{
	border-right: 0px solid red;
}
.simulcast-calendar
{
    color: #00a1bd;
}
/*-------------------------------foro-------------------------------*/
.topbar-nav-trail a
{
    color: #008182;
}
.topbar-nav-trail a:hover
{
    color: #06c2c3;
}
.forum_main-topic-desc,.group-card
{
    background: #151515;
}
a.forum_main-topic-desc-title
{
	color: #bd5067;
}
.group-card-name
{
    color: #e09d00;
}
.forumcategory-site-rules-block,.widget-forumcategory-header,.paginator-lite,.paginator-lite-selected,.showforumtopic-header
{
    background-color: #151515;
}

.widget-forumcategory-display td, .widget-forumcategory-display th,.paginator-lite, .paginator-lite-selected,.forum_series-display td, .forum_series-display th
{
    border: 1px solid #2f2f2f;
}
.forum_series-display-title a:hover
{
    background-color: rgba(0, 0, 0, 0);
    color: #fae155;
}
.forum_series-display-title a
{
    color: #1c7551;
}
.widget-forumcategory-display-title a
{
    color: #008a71;
}
.paginator-lite, .paginator-lite-selected
{
    color: #fae155;
}
.paginator-lite-disabled
{
    border: 1px solid #636363;
    background-color: #151515;
}
.paginator-lite-selected
{
	border: 1px solid #fae155;
}
.paginator-lite:hover
{
    background-color: #455f44;
}
.pollwidget
{
	background: #1b1b1b;
}
.pollwidget-showpoll
{
	background: rgba(0, 0, 0, 0);
    border: 1px solid #0a6da4;
}
.showforumtopic-fblike
{
    background-color: rgba(0, 0, 0, 0);
}
.showforumtopic-messages td
{
    background-color: #181818;
}

.showforumtopic-message-user,.showforumtopic-message-user-footer
{
    border-right: 4px solid #9e2828;
}
.showforumtopic-message-footer
{
	border-bottom: 4px solid #9e2828;
}
.showforumtopic-message-user-name a
{
    color: #119062;
}
.showforumtopic-message-user-details
{
    color: #13b1ab;
}
.bb-quote
{
    color: #808080;
    background-color: #151515;
    border: 1px solid #333;
}
/*-----usuario page----*/
.library-tab-widget .tab-contents-container,.user-profile-interests td a,.acct-contents,.inbox-body-contents
{
	border-left: 1px solid #2f2f2f;	
}
.library-tab-widget .tab-contents-container
{
	border-top: 1px solid #2f2f2f;	
}
.old-template-body .boxcontents
{
    border: 1px solid #2f2f2f;
}
.old-template-body .boxtitle
{
	background: #151515;	
}
.library-tab-widget .tab a
{
    border-top: 0px solid red;
    border-right: 0px solid red;
}
.library-tab-widget .tab-container .highlight a
{
    background-color: #151515;
}
.library-tab-widget .tab a
{
    background-color: #252525;
}
.tab-container .tab a
{
    color: #23a033;
}
.photoalbum-list, .thumb-product .mug
{
    border: 1px solid #2f2f2f;
}
.user-profile-link:hover,.search-results a:hover
{
    background-color: #252525;
}
.user-profile-link:hover img
{
    background-color: rgba(0, 0, 0, 0);
}
/*---editar perfil---*/
div.acct-nav a,.inbox-nav-contents a
{
    color: #bd4242;
}
.acct-nav a:hover,.inbox-nav-contents li a:hover
{
    background-color: #151515;
}
.text-input[type=text], .text-input[type=password], textarea.text-input
{
    border: 1px solid #2f2f2f;
}
.acct-nav a.highlight,.inbox-nav-contents li.highlight a
{
    background-color: #2d2d2d;
}
/*--bandeja de entrada--*/
.pm-subject
{
    color: #fae155;
}
ul.topbar-nav-sub li.dim a:hover
{
    background-color: #080808;
    color: #fae155;
}
/*-cambia color-*/
div[style="background-color:#FFF6CF"],
div[style*="#FFF6CF"] 
{ background-color: #312904 !important;}
}
