css:
@-moz-document domain("mixlr.com") {
#live_page_view #broadcast_main #arena #share {
    display: none;
}
#live_page_view #broadcast_main #arena {
    width: 100%;
}
#live_page_view #broadcast_main #arena #crowd_header,
#live_page_view #broadcast_main #arena #crowd {
    width: 100%;
}
#crowd {
    background-color: #606060;
}
#live_page_view {
    display: none;
}
span {
    color: white;
}
#live_page_view {
    width: 100%;
}
#live_page_view {
    width: 100%;
}
#sidebar {
    background: #606060;
}
#chat_box {
    background: #606060;
}
#main_header {
    color: #606060;
}
.crowd_list {
    background: #606060;
}
.name {
    color: #dfdfdf;
}
#crowd_container {
    width: 35%;
    background: #606060;
}
#crowd_header {
    color: #acacac;
    background: #606060;
}
.message {
    background: grey;
}
a {
    color: #cccccc;
}
#broadcast_main {
    background: #606060;
}
.inner {
    background: #606060;
}
.inner:hover {
    background: #606060;
}
#comments {
    background: #606060;
    width: 60%;
}
#arena {
    background: #606060;
}
#comment_header {
    background: #606060;
    color: #acacac;
}
.broadcaster {
    color: #c0c0c0;
}
::selection {
    color: red;
}
::-webkit-scrollbar {
    width: 0px;
    background: #c0c0c0;
}
::-webkit-scrollbar-button {
    width: 1px;
    height: 1px;
}
::-webkit-scrollbar-thumb {
    border: thin dotted #000000;
}
::-webkit-scrollbar-thumb:hover {
    background: -webkit-linear-gradient(top, #acacac, #c0c0c0);
}
.comment_list li {
    border-top: 1px solid #4c4c4c;
    background: #606060;
}
.broadcaster_details_sidebar .follow_bar a {
    background-color: black;
}
.volume {
    background: #606060;
}
.comment_list .comment_info p,
.comment_list .comment_info .content {
    color: white;
}
.comment_list .broadcast_action .inner .content {
    color: grey;
}
.comment_list .comment_info h3 {
    color: #afafaf;
}
.comment_list li.active,
.comment_list li:hover {
    background: #606060;
}
.comment_list li .inner:hover {
    background: #606060;
}
#comment_counter .inner_counter {
    background: grey;
}
#comment_counter .inner_counter:hover {
    background: black;
}
.comment_list li.editable_comment:hover .inner {
    background: #606060;
}
#chat_box .action_container .action {
    width: 100%;
    background: #606060;
    border: 1px dotted #cccccc;
}
#chat_box form textarea {
    color: white;
    background: #606060;
}
#main_menu[role="navigation"] ul#nav_second li a {
    color: black;
}
#main_menu {
    background: black;
}
#chat_box form button.chat {
    background: black;
    border: 1px solid white;
}
#chat_box form button.chat:hover {
    background: #303030;
    border: 1px solid black;
}
header#main_header h1 {
    background: #4c4c4c;
}
#main_menu[role="navigation"] ul li a {
    color: #000;
    border-right: 1px solid #303030;
}
#artwork_slide_container .artwork_slide h3 span{
color: white;
}
.comment_list .comment_info p>a, .comment_list .comment_info p a:active, .comment_list .comment_info p a:visited, .comment_list .comment_info .content>a, .comment_list .comment_info .content a:active, .comment_list .comment_info .content a:visited {
color: lightgreen;
}
}
