@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lolesports.com") {
  
  body, .article__content, .article__header, .article__hero, .table, table, body .riot-apollo .riot-discussion, .riot-apollo .riot-discussion .riot-comment .riot-collection > .riot-comment > .riot-collection > .riot-comment, .riot-apollo .riot-discussion .riot-comment .riot-collection > .riot-comment > .riot-collection > .riot-comment > .riot-collection > .riot-comment, .widget__header--minimal, .article-lockup-item__content, .widget__header, .fixture--solo .fixture-solo-one-quarter, .widget__header--tabbed > ul li a, .widget__header--tabbed a.is-active, .fixture--stacked .fixture__top, .fixture, .match__info, .match-history__match, .widget__header--hero .widget__header-title, .widget__header--hero h1, .player-biography > .widget__header:first-child, .widget__text, .toggle-panel, .vod-item__mid, .vod-item__section, .article-lockup-item__image, .widget__muted-copy, #article-page, .widget__header h2 {
    background-color: black;
    color: white;
  }
  
  .article-lockup-item {
    border-left: .5555rem solid black;
  }
  
  body .riot-apollo .riot-discussion .riot-list-item .riot-body .high-quality, body .riot-apollo .riot-discussion .riot-comment .riot-footer .timeago abbr, body .riot-apollo .riot-discussion .riot-comment .riot-voting .total-votes, .fixture__team a, .article-lockup-item--tweet .article-lockup-item__copy, .article-lockup-item__title, .article-lockup-item__sell, .fixture--stacked__team a, .vod-item-match-info__date, .vod-item-match-info__vs .team-left .team, .vod-item-match-info__vs .team-right .team, .vod-item-match-info__game:active, .vod-item-match-info__game:focus, .vod-item-match-info__game:hover, .vod-item-match-info__game {
    color: white;
  }

  .article-lockup-item--inline {
    border-bottom: .5555rem solid black;
  }
  
}
