css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://toggl.com/app/timer") {
  .TimerContainer__timerContainer {
    padding-top: 68px;
    position: relative;
  }
  .TimeEntryForm__newTimeEntry {
    position: fixed;
    width: calc(100% - 180px);
    z-index: 10;
    top: 0;
  }
  @media only screen and (max-width:1024px) {
    .TimeEntryForm__newTimeEntry {
      width: calc(100% - 62px);
    }
  }
}
