css:
@-moz-document domain("tvtime.com"), domain("tvshowtime.com") {
* {border-radius:0!important;}

::selection {
    background: #FFDA08;
    color: #0C0A0A;
}

:focus {
    outline: 0;
    outline: none;
}

::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	background-color: #333333 !important;
}

::-webkit-scrollbar {
	width: 4px!important;
	background-color: #80c14b !important;
}

::-webkit-scrollbar-thumb {
	background-color: #A9894F !important;
	border-radius: 0 !important;
}

#user-nav .alt-user-nav {
    right: -7px;
    top: 14px;
}

.page-left section h1 {
    font-size: 11px;
    color: #FAE64F;
}

#show-details .seasons .episode-wrapper span {
    font-size: 12px;
}

i.fa.fa-caret-right.expand-icon {
    color: #A9894F;
}

i.fa.fa-caret-left.reduce-icon {
    color: #A9894F;
}

i.icon-tvst-arrow_left:hover {
    color: #A9894F;
}

i.icon-tvst-arrow_right:hover {
    color: #A9894F;
}

span[itemprop=name] {
    padding: 0 4px;
    font-family: trebuchet ms;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16),0 0 0 1px rgba(0,0,0,0.08);
    color: #9E9E9E;
}

.posters-list>li .poster-details .secondary-link {
    color: #272727;
    font-family: trebuchet ms;
    font-size: 12px;
    display: inline;
    border-left: 2px solid #7CDB62;
    padding: 0 3px 0 3px;
}

.posters-list>li .poster-details .secondary-link:hover {
    color: #272727;
    background: #7CDB62;
    text-decoration: none;
}

#show-details .seasons .episode-list {
    font-family: trebuchet ms;
    overflow-x: hidden;
}

#show-details .seasons .episode-wrapper span {
    font-size: 11px;
}

i.to-watch-icon.icon-tvst-watch:hover {
    color: #7CDB62;
}

.posters-list>li .poster-details h2 a {
    color: #CCCCCC;
    font-family: trebuchet ms;
}

body.white {
    background: #2C2C2C;
}
body.alt {
    background: #2C2C2C;
}

.posters-list>li .poster-details .secondary-link {
    color: #A9894F;
}

.page-column section h1 {
    color: #A9894F;
}

.page-left {
    background-color: #2C2C2C;
}

#friend-requests, #notifications, #putio-zone, #referrals {
    background-color: #2C2C2C;
}

.page-center {
    background-color: #2C2C2C;
}

#episode-details .episode-infos {
    background-color: #2C2C2C;
}

#episode-details .episode-infos.extended {
    border-bottom: 1px solid #222222;
}

.comment form.post>.top .holder {
    background: #2C2C2C;
    border: 1px solid #222;
}

.mentions-input-box {
    background: #2C2C2C;
}

.comment form.post>.top .holder textarea {
    color: #FFF;
}

#content .content-container .right {
    background-color: #2C2C2C;
    border-left: 1px solid #222;
}

#content .content-container .right section {
    border-bottom: 1px solid #222222;
}

.comment .sub-comment {
    background: #2C2C2C;
    border-color: #222222;
}

.nav-tabs {
    border-bottom: 1px solid #222;
}

#meme-wizard .nav li a {
    color: #CCC;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #CCC;
    background-color: #2C2C2C;
    border: 1px solid #222;
}

.dropzone {
    background: #2C2C2C;
}

.comment:not(.new-comment) .post {
    background: #2C2C2C;
    border: 1px solid #222;
}

.comment .post>.top .holder .author-label, .comment .post>.top .holder .author-label a {
    color: #A9894F;
}

.comment form.reply>.holder {
    background: #2C2C2C;
    border: 1px solid #222;
}

.comment .post .options {
    border-top: 1px solid #222222;
}

#show-details.show .show-nav {
    background-color: #2C2C2C;
    border-bottom: 1px solid #222;
}

#show-details .seasons .episode-wrapper .infos a, #show-details .seasons .episode-wrapper .infos:hover i.icon-play-circle {
    color: #CCC;
}

#show-details .seasons .episode-wrapper.odd {
    background-color: #222;
}

.comment .post>.top .holder blockquote p {
    font-family: trebuchet ms;
    font-size: 13px;
    color: #CCC;
}

.comment .reply .author-label a {
    color: #A9894F;
}

.comment .reply>.holder blockquote p {
    font-size: 12px;
    color: #CCC;
    font-family: trebuchet ms;
}

.comment .reply:not(form) {
    border-bottom: 1px solid #222;
}

.comment .other-user-comments a {
    color: #AAA;
    background-color: #2C2C2C;
}

.comments-filters li a.active {
    color: #CCC;
    background-color: #222222;
}

.comments-filters li:nth-child(2) {
    border-left: 1px solid #222222;
    border-right: 1px solid #222222;
}

.comments-filters li a {
    background-color: #222222;
    color: #555;
}

#episode-details.episode .episode-main .emotions li a:hover, #episode-details.episode .episode-main .emotions li.active a {
    background-color: #222;
    border: 1px solid #FFD908;
}

#episode-details.episode .episode-main .emotions li a:hover {
    color: #CCCCCC;
}

#profile .profile-nav {
    background-color: #222;
    border-bottom: 1px solid #2C2C2C;
}

#profile .profile-nav .nav-tabs>li.active, #profile .profile-nav .nav-tabs>li:focus, #profile .profile-nav .nav-tabs>li:hover {
    border-bottom: 2px solid #A9894F;
}


#profile .profile-nav .nav-tabs>li.active .count, #profile .profile-nav .nav-tabs>li.active .label {
    color: #FBFBFB;
}

.nav>li>a:focus, .nav>li>a:hover {
    background-color: #222;
}

#profile .profile-nav .nav-tabs>li .label {
    color: #FFE509;
}

#profile {
    background-color: #2C2C2C;
}

#profile h2.small {
    color: #A9894F;
}

.posters-list>li .poster-details h2 span {
    font-family: trebuchet ms;
    color: #F44336;
}

.header .menu>li a, .page-left .menu>li a {
    font-family: trebuchet ms;
}

#calendar .day.today .day-header .day-info .month-label {
    color: #4CAF50;
}

#calendar .day.today .day-header .day-info .day-number {
    color: #4CAF50;
}

#calendar .day {
    border-bottom: 2px solid #222222;
}

#calendar .day.today .day-header .relative-date, #calendar .day.tomorrow .day-header .relative-date, #calendar .day.yesterday .day-header .relative-date {
    color: #A9894F;
}

#calendar .day .day-header .relative-date {
    border-top: 1px solid #222222;
}

#calendar .day .day-header {
    color: #A9894F;
}

.page-right {
    border-left: 1px solid #222;
}

.page-sidebar {
    background-color: #2C2C2C;
}

#calendar-filters .mode {
    background: #2C2C2C;
    color: #666;
    border: 1px solid #222;
}

.datepicker table tr td.today, .datepicker table tr td.today.disabled, .datepicker table tr td.today.disabled:hover, .datepicker table tr td.today:hover {
    background-color: #4CAF50;
}

#calendar-picker {
    border-top: 1px solid #222;
}

.fanarts-list>li .fanart-container {
    background-color: #222222;
    border: 1px solid #A9894F;
}


.fanarts-list>li .fanart-details p {
    border-top: 1px solid #2C2C2C;
    color: #CCC;
}

.fanarts-list>li .fanart-details h2 a {
    color: #4CAF50;
}

#episode-details #content .not-watched-episode .not-watched-infos {
    border-top: 1px solid #222222;
    border-bottom: 1px solid #222222;
}

#episode-details #content .not-watched-episode .stats-list .stat-item {
    color: #A9894F;
    border-left: 1px solid #222222;
}

.followers img {
    border: 1px solid #222;
}

#episode-details #content .not-watched-episode .display-btn {
    border-bottom: 2px solid #A9894F;
    background-color: #222222;
}

#news .story .comment-infos-author .author-infos .author-name a {
    color: #A9894F;
}

#news .story .comment-infos-about {
    color: #CCC;
    border-bottom: 1px solid #222222;
}

#news .story .comment-infos-about a {
    color: #4CAF50;
}

#news .story .comment-infos-about a:hover {
    color: #FFD90E;
}

body a:hover {
    color: #FFD300;
}

body a {
    color: #7CDB62;
}

a:focus, a:hover {
    color: #7CDB62;
}

#explore section h1 {
    color: #A9894F;
}

.posters-list>li .nav .follow-btn {
    background: #222;
}

.posters-list>li .nav a.followed {
    background: #4CAF50;
    opacity: 0.8;
}

#friend-requests ul li, #notifications ul li, #referrals ul:not(.nav-tabs) li, header #global-search-results ul li {
    background-color: #2C2C2C;
    border: 1px solid #222;
    color: #AAAAAA;
}

#notifications ul li a:first-child {
    color: #A9894F;
}

#friend-requests ul:after, #notifications ul:after, #putio-zone ul:after, #referrals:after, header #global-search-results ul:after {
    border-color: transparent transparent #A9894F;
}

#friend-requests, #notifications, #putio-zone, #referrals {
    box-shadow: 0 0 1px 1px #A9894F;
}

.comment.extended.full .post>.top .review-content, .comment.full .post>.top .holder blockquote p {
    color: #bbb;
}

.users-list>li .user-details h2 a {
    color: #999;
}

#settings .tab-content {
    border: 1px solid #222222;
    background: #2C2C2C;
}

#settings section .title {
    background: #222222;
    border-bottom: 1px solid #A9894F;
}

#settings section h2 {
    background-color: #222222;
    color: #bbb;
}

#settings {
    color: #bbb;
}

#settings section .no-input {
    color: #FFDA0E;
}

#settings section h3 {
    border-bottom: 1px solid #222222;
}

#profile .activity .comment:not(.new-comment) .post {
    border-color: #222222;
}

.popover {
    background-color: #2C2C2C;
}

.data-popover-container .info-box .other-infos {
    color: #bbb;
}

.popover.top>.arrow:after {
    border-top-color: #2C2C2C;
}

.img-thumbnail, body {
    background-color: #A9894F;
}

.data-popover-container .avatar img {
    border-color: #A9894F;
}

#profile .followers p a, #profile .following p a {
    color: #aaa;
}

#profile-badges .badges-group {
    background: #2C2C2C;
    border: 1px solid #222222;
}

#profile-badges ul .badge-item.unlocked .badge-title {
    color: #aaa;
}

#profile-leaderboards, #profile-stats {
    background: #2C2C2C;
    border: 1px solid #222222;
}

#profile-leaderboards ul.leaderboards-list li {
    background-color: #2C2C2C;
    border: 1px solid #222222;
}

#profile-leaderboards ul.leaderboards-list li .leaderboard-infos .rank-number {
    background-color: #222222;
    border: 2px solid #A9894F;
    color: #CCC;
}

#profile .stats-list .stat-item {
    border-left: 1px solid #222222;
}

#profile-shows .progress.you .you-label {
    color: #222;
    font-family: trebuchet ms;
}

#profile-shows .progress.you {
    background-color: #F44336;
}


.comment.full .publish-infos {
    border-left: 1px solid #222;
}

.comment.full .publish-infos .object-infos a {
    color: #A9894F;
}

.modal:not(.full) .modal-header {
    background-color: #333;
}

.modal:not(.full) .modal-content {
    background: #2c2c2c;
    box-shadow: 0 0 2px #000,0 0 15px rgba(0,0,0,.7);
}

.modal-header {
    border-bottom: 1px solid #000000;
}

.modal:not(.full) .modal-title, .tag {
    color: #a9894f;
}


#people .people-list li {
    border-bottom: 1px solid #000000;
}

#meme-wizard .nav-tabs>li.active>a, #meme-wizard .nav-tabs>li.active>a:focus, #meme-wizard .nav-tabs>li.active>a:hover {
    background-color: #222222;
}

.nav-tabs>li>a:hover {
    border-color: #222222;
}

.ui-slider {
    background: #222222;
}

.comment form.post>.top .holder:not(.extended):after {
    border-top-color: #222;
}

.new-comment .post .holder .react-with-pictures-label i {
    background-color: #222;
}

.comment .post .options .like-option .like-btn:hover, .comment .post .options .like-option.liked .like-btn, .community-feed-list .story .options .like-option .like-btn:hover, .community-feed-list .story .options .like-option.liked .like-btn {
    color: #F44336;
}

.fanarts-list>li .nav a, .posters-list>li .nav a {
    color: #aaa;
}

#episode-details .episode-infos .basic-infos {
    color: #bbb;
}

#episode-details.episode .followers, #show-details.show .show-nav .followers {
    color: #999;
}

h2.title, section .title {
    color: #999;
}

#upload-dropzone {
    border: 2px dashed #222;
}

.comments-filters li a:hover {
    background-color: #A9894F;
}

.cast .actor-item .content h3 {
    color: #999;
}

.comment .post .options .like-option.liked .like-label {
