    top: 8px;
}

#global-search input {
    font-family: trebuchet ms;
    border: 0;
    color: #AAA;
}

#global-search .cancel-search {
    color: #A9894F;
}

#news .story .holder .comment-content {
    font-family: trebuchet ms;
    font-size: 13px;
    color: #AAA;
}

footer .social {
    display: none;
}

