css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("https?://([^.]+\.)?yc-tv\.(..|com(\.tr)?).*"),domain("yc-tv.org") {
   *
  {
    font-family: "Segoe UI", Cantarell, Ubuntu, Tahoma, -apple-system, BlinkMacSystemFont, sans-serif;
    font-smooth: always;
    border-style: none !important;
    border-bottom: 0;
    border-top: 0;
    border-left: 0;
    border-right: 0;
    border-color: rgba(255, 0, 0, 0) !important;
    -webkit-overflow-scrolling: touch;
  
    -webkit-font-smoothing: antialiased;
  
    -moz-osx-font-smoothing: grayscale;
  }


::-webkit-scrollbar-track
{
 -webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0.1);
 background-color: none;

 z-index:-9999;
}

::-webkit-scrollbar
{
 width: 5px;
 background-color: rgba(0,0,0,0.1);
 
}

::-webkit-scrollbar-thumb
{
 background-color:rgba(80,114,153,0.8) ;
 border: 0;border-radius:0px;
}


header {
    margin-bottom: -150px !important;     z-index: 9999;
}
body {
    
    color: #fff;
    background-color: #000;}
.full-content {
    background: #111;
    padding: 10px;
    overflow: hidden;
    border-radius: 0 ;
    position: relative;
}

element.style {
    outline: 0px;
    z-index: 1007;
    height: auto;
    width: auto;
    top: 200px;
    left: 145px;
    display: block;
}
.ui-dialog {
    overflow: hidden;
    padding: 10px;
    position: absolute;
    width: 500px;
    border: 0px ;
}

.speedbar_text h1{padding-top:10px;}
.container {
    width: 1140px !important;
    margin: auto;padding: 0px;
    
} 
.bg-films,  .bg-serials, .bg-cartoons, .prefooter,  .footer {
    position: relative;
    max-width: 1140px;
    margin: 0 auto;
    background-color: black;}
.con-slide-wrap {
    width: 1140px;
}
.poiskosnova {
    float: right;
    width: 100%;
    padding: 10px;
    
    height: 100px;
}
 .alfavblok ul li {
    font-family: 'PT Sans', sans-serif;
    float: left;
    padding: 1px;
    text-align: center;
    width: 20px;
    height: 20px;
    margin: 0 5px 5px 0;
    font-size: 12px;
    text-transform: uppercase;
    background: #00936e;border-radius:10px;
}
.razdelblok {
    float: left;
    width: 100%;padding: 10px;
}
.razdelosnova {
    float: left;
    width: 200px;
    margin-right: 0px;
}
.razdelosnova ul {
    list-style: none;
    padding: 0px;
}
.razdelosnova ul li {
    font-family: 'PT Sans', sans-serif;
    padding: 10px;
    margin: 0px 0 5px 0;
    font-size: 11px;
    text-transform: uppercase;
    background: rgba(55,90,127,.6);
    cursor: pointer;
    height: 55px;
    line-height: 35px;
}
.razdelgoda, .razdeljanr {
    
    background: #2c3e50;
    border: 1px solid #34495e;
    float: left;
}

.short {
    position: relative;
    float: left;
    height: auto;
    margin: 0 6px 20px 0;
}
.pref-block {
    width: 270px;
    margin: 5px;
}

.pref-block {
    float: left;
    overflow: hidden;
    background: #222;
    /* border-radius: 4px; */
    height: auto;
    /* min-height: 330px; */
    /* width: 276px; */
    margin-right: 12px;
}
.frc-actor {
    width: 550px;
}

.full-r-col {
    width: 840px;
}
#dle-content { max-width: 1140px;
    margin: 0 auto;  }

.speedbarblok {
    height: 30px;
    line-height: 30px;
    position: relative;
    margin-top: 0px;
    padding-left: 10px;
    background-color: #2C4765;
    border-radius: 0px ;margin-bottom: 20px;
}
.speedbar_text {
    
    margin-top: -20px;
    
}
.footer {
    background: #000;
    height: auto;
    padding: 5px;
    font-size: 12px;
    color: #999;
}
.foot-nm {
    background: #000;
    padding: 10px;
    border-radius: 0px;
}
#yourcinema-write {
    background: #2c3e50;
    width: 840px;
    border: 3px solid #34495e;
    position: relative;
    font-size: 12px;
}
.ffullpoh {
    float: left;
    width: 100%; 
}

 #comments   {
    padding:  10px;
    overflow: hidden;
}
.user-tabs, .tab-content {
    width: 840px;
}
.full-r-col .nav-tabs {
    border-radius: 0px 0px 0 0;
}
/* profile   */
.other-block {
    overflow: hidden;
    margin : 15px 0 10px 0;
}
.collectblok {
    text-align: center;
    width: 281px;
    background: #2c3e50;
    border: 3px solid #34495e;
    float: left;
    margin: 2px 2px  2px 2px;
}

/* ads block */
#tJ7b7NHUvmv4, #avp_zid_440, #b0bcbd9e12, #skin_8712, .skin-block, #vk_groups, .prefooter,  #bn_605c80e02e, #a-5b25ea6867fdb0b5ab6ec30a95b0e74c, .flc-info-block, #bn_c3a9cfc8e0,  .linkblo, .footer-list {display: none  !important; z-index: -9999;}




}
