css:
@namespace url(http://www.w3.org/1999/xhtml);

/*Applies only to homepage*/
@-moz-document url("https://www.youtube.com/") {
  
  /*Remove 'Recommended' Videos in the middle of the page*/
  div#content{
    display: none !important;
  }
  
  /*Remove sidebar on the left
  div#appbar-guide-menu{
    display: none !important;
  }*/
  
  /*Remove all buttons such as 'Upload' and Account Name
  .yt-uix-button{
    display: none !important;
  }*/
  
  /*Bring Upload button, User account button, and notification button to the top right*/
  #yt-masthead-user {
    color: #555;
    float: right;
    margin-left: 50px;
    margin-top: -320px;
  }
  
  /*Remove search button next to search bar*/
  #masthead-search .search-btn-component .yt-uix-button-content {
    display: none;  
  }
  
  /*Place sign in button on the top right*/
  #yt-masthead-signin {
    float: right;
    margin-left: 25px;
    margin-top: -318px;
  }
  
  /*Remove row where 'Home' is written under the search bar*/
  div#masthead-appbar-container{
    display: none !important;
  }
  
  /*Place logo above search bar*/
  #yt-masthead .yt-masthead-logo-container {
    float: none !important;
    margin-top: 3px !important;
    position: relative !important;
    width: 200px !important;
  }
  
  /*Place search bar at middle of the screen*/
  #yt-masthead {
    margin: 286px 8px 372px 644px;
        margin-top: 284px !important;
        margin-right: 8px !important;
        margin-bottom: 498px !important;
        margin-left: 644px !important;
  }
  
  /*Place footer centered at the very bottom of the page, below search bar
  div#footer {
    margin: 23px auto 0px 100px !important;
    padding: 0px 0 !important;
  }*/
  
  /*Change height of search bar*/
  #masthead-search-terms {
    height: 40px !important;
  } 
}

/*Applies to all of YouTube*/
@-moz-document domain("youtube.com") {
  
  /*Places and fits live chat to the right video*/
  .watch-stage-mode #watch7-sidebar {
    top: -2040px !important;
    right: -407px !important;
    width: 25% !important;
  }
 
  /*Hide sidebar when in theater mode*/
  div#watch7-sidebar-contents {
    display: none !important;
  }
  
  /*Change width of content below video*/
  #watch7-content {
    width: 1280px !important;
  }
  
  /*Change background color of site to black*/
  #body {
    background: #000 !important;
  }
   
  /*Removes space between title, description, and comments*/
  .yt-card {
    margin: 0px 0px 0px 0px !important;
  }
  
  /*Removes space between channel header and channel videos when viewing a channel*/
  .channel-header.yt-card {
    margin-bottom: -11px !important;
  }
  
  /*Increase width of search results*/
  .branded-page-v2-col-container {
    width: 100%;
  }
  
  /*Remove space between search bar and search results*/
  #yt-masthead-container {
    padding-bottom: 10px;
    padding-top: 15px;
  }
  
  /*Place search bar at top center of screen*/
  div#yt-masthead {
    margin: 0px 0px 0px 415px;
  }
  
  /*Place footer at center on search results*/
  div#footer {
    margin: 23px auto 0px 475px !important;
    padding: 0px 0 !important;
  }
  
  /*Center links below footer*/
  div#footer-links {
    padding: 15px 0 15px 200px !important;
  }
  
  /*Bring footer higher up on search results and change border color to white*/
  body #footer-container {
    margin-top: -200px;
    background-color: #fff;
    border-top: 58px solid #fff;
        border-top-width: 58px;
        border-top-style: solid;
        border-top-color: rgb(255, 255, 255);
  }
    
  /*Change width of search results*/
  .content-alignment {
    margin-left: auto;
    margin-right: auto;
    width: 1300px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
  }
}

