css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
	url-prefix("http://runouw.com")
{
/* Reset backgrounds */
body, span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span, .forabg, .forumbg, .bg1, .bg2, ul.forums,	a.button1, 
li.row:hover, #tabs a, .blockquote {
	background: none !important;
}

body
	{ background:#E2862C !important;
	  max-width:none;
	}
/* Background colors */
.forabg, .forumbg {
	background:#D35400 !important;
}

#navigation a {
	background: linear-gradient(to right, #555 0%, #666 100%) !important;
}

#navigation a:hover, #navigation #active-subsection a {
	background: linear-gradient(to right, #333 0%, #444 100%) !important;
}
html, body, select, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited,
dl.codebox {
	background-color:rgba(240,240,240,0.5);
}
.bg1, .bg2 {
		background:#3F3838 !important;

}
ul.forums, .panel, #tabs a {
background:#3F3838 !important;
}

.navbar{
	background: none;
  border: none;
} 

li.row:hover {
	background-color: #3E1C34 !important;
}

blockquote {
	background-color: #666 !important;
}
.pagination span a:hover, .pagination span strong {
	background-color: #888 !important;
}
.pagination span strong {
	background-color: #a96 !important;
}
.postlink:hover {
	background-color: #ddd !important;
}
#tabs a span {
	background: inherit !important;
}
/* Text colors */
html, body, .headerbar, h2, h3, .postbody, .postbody .content, #tabs .activetab a span, ul.topiclist li,
.panel li.header dd, .panel li.header dt, .content, .navbar {
	color: #FFF !important;
}
	.inputbox{
color: #bbb !important;
}
a:hover {
	color: #AF0000 !important;
}
.postprofile, dl.details dd {
	color: #D35400 !important;
}
li.row strong, .postprofile strong, label, .panel, #tabs a span, dl.details dt {
	color: #999 !important;
}
select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited a.button1, input.button1, input.button3, a.button2, input.button2{
	color: #000 !important;
}
.postlink:hover, .pagination span a:hover, .pagination span strong {
	color: #333 !important;
}
dl.codebox code {
	color: #6a6 !important;
}
/* Border colors */
hr {
	border-color: #222 #333 #333 !important;
}
blockquote {
	border-color: #333 !important;
}
dl.codebox {
	border-color: #363 !important;
}
select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:hover, .pagination span strong {
    border-color: #444 !important;
}
h2, h3 {
	border-bottom-color: #222 !important;
}
ul.navlinks, #cp-main h3 {
	border-bottom-color: #333 !important;
}
dl.codebox dt {
	border-bottom-color: #363 !important;
}
#tabs .activetab a {
    border-bottom-color: #444 !important;
}
.postlink {
	border-bottom-color: #a96;
}
li.row {
	border-top-color: #333 !important;
	border-bottom-color: #222 !important;
}
.signature, ul.cplist {
	border-top-color: #333 !important;
}
ul.topiclist dd, .postprofile {
	border-left-color: #333 !important;
}
/* Other */
.forabg, .forumbg {
	border-radius: 4px;
  }
	div#page-header a:link, div#page-header a:visited, div.postbody a:link, div.postbody a:visited, a:link, a:visited
	{  color: #D35400;
	}
	a.forumtitle:link, a.forumtitle:visited, a.topictitle:link, a.topictitle:visited {
    color: #D35400;
}
	h2 a:link, h2 a:visited, .pagination a:link, .pagination a:visited, a.left-box:link, a.left-box:visited, a.right-box:link, a.right-box:visited {
    color: #D35400 !important;
}

/* Shit */
.icon-ucp, .icon-home, .icon-members, .icon-faq, .icon-members, .icon-logout {
background: none;
	}
	
	a.button1, input.button1, input.button3, a.button2, input.button2 {	
		background-image: none;
	background-color:rgba(240,240,240,1);}
	input.search {background-image: none;
		background-color:rgba(240,240,240,1);
	}
p.author a:link, p.author a:visited, dt a:link, dt a:visited, dd.lastpost a:link, dd.lastpost a:visited, div.navbar a:link, div.navbar a:visited,  ul.topiclist a:link, ul.topiclist a:visited {
	color:#D35400;
	}
.navbar2
	{
		display:none;
	}

dl.icon dt, dl.icon dd, li.header dt, li.header dd
	{
		color: #FFF;
	}
	
	
}
