css:
/* create by Lofox */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("futura-sciences.com") {
  
  
  html, body {background: #263C4A; color: #e1e1e1; }
  

 
  /*  head    */   
  
  .nav-logo, .nav-logo:hover , a.nav-logo-futura-sciences , a.nav-logo-futura-health   { filter:  brightness(163%)   }
  
  nav.main-nav { 	background: #111;	border-bottom: 1px solid #0e0e0e;}
 
  .nav-action-cta , nav.main-nav .search-cta   {filter: invert(100%)} 
  
  nav.main-nav .nav-list li a { color: #e1e1e1 }
  
  .bg-white  { 	background: #263c4a;	}
  
  .spreadknowledge-nav  {background-color: #000 }
  
  .spreadknowledge-nav , .spreadknowledge-nav a, .spreadknowledge-nav .auth-cta , .spreadknowledge-nav .social-links a {color: #CCC }
  
  
  /*  les recents  */  
  
  .module.sidebar-module.mostrecent-module .latest-item .social li a {color: #bebebe }
  
   .module.sidebar-module .latest-copy {color: #bebebe }
  
  .module.sidebar-module .latest-copy:hover {color: #55AAAD }  
  
  /* popu */ 
  
  .bg-body-bg, .bg-gray-gallery {	background-color: #111;}
  
  .color-black  {color: #e1e1e1 }
  
  div.col-md-3 > section > figure > figcaption {color: #000}
  
  
  
  /* colonne gauhe */  
  
  .module.sidebar-module .item-divider {	border-color: #fff; }
  
  .sidebar-module {border: 1px solid #fff}
  
  .color-blue {color: #395a6f}
  
  
  /* article */  
  
  .module.share-fav li a.gp, .module.share-fav li a.gplus ,.bg-off-gray {background: #111}
  
  .module.article-text p a .link-wrapper  {color: #e1e1e1 }
  
  .module.sidebar-module.advertising-module  {background: #263c4a}
  
  

}
