css:
@-moz-document domain("prodota.ru") {
    .post_block {
        background: #3b3848;
    }
    .breadcrumb a {
        color: #7fbed4;
    }
    .ipsType_pagetitle,
    .ipsType_subtitle {
        font: Normal 24px/1.3 Verdana, Arial, sans-serif;
        color: #7fbed4;
    }
    table.ipb_table .unread .topic_title {
        font-weight: 600;
        font-family: verdana;
        font-size: 13.5px;
        color: #a7c24f;
    }
    table.ipb_table h4,
    table.ipb_table .topic_title {
        font-size: 13.5px;
        font-family: verdana;
    }
    .desc.lighter,
    .desc.lighter.blend_links a {
        color: #abb7c1;
    }
    .statistics_head {
        font-size: 15px;
        color: #d4d4d4;
        font-weight: 500;
    }
    .progress_bar span {
        background: #2f2d3a url(http://prodota.ru/forum/public/style_images/Prodota_Images/gradient_bg.png) repeat-x left 50%;
    }
    .date,
    .poll_question .votes {
        color: #b1b0b1;
    }
    .popupInner h3 {
        color: #2f2d3a;
        font-weight: normal;
    }
    .pagination .back a,
    .pagination .forward a {
        background: #3b3848;
        color: #b1b0b5;
        font-weight: normal;
    }
    .pagination .pages li.active,
    .pagination .back a:hover,
    .pagination .forward a:hover {
        background: #43aff1 !important;
    }
    .maintitle {
        background: #3b3848;
        color: #b1b0b5;
        font-weight: normal;
        border-top: 1px solid #2f2d3a;
        border-left: 1px solid #2f2d3a;
        border-right: 1px solid #2f2d3a;
    }
    .ipsBadge {
        font-weight: bold;
        color: #353442;
    }
    .ipsBadge_grey {
        background: #e64444;
    }
    .post_block h3 {
        background-color: rgba(47, 45, 58, 0.4);
        font-weight: normal;
    }
    .desc,
    .desc.blend_links a,
    p.posted_info {
        font-size: 12px;
        color: #898598;
    }
    .desc.lighter,
    .desc.lighter.blend_links a {
        color: #b1b0b5;
    }
    .maybehelpy_edit,
    .bbc_spoiler_show {
        color: #7fbed4;
        font: bold 11.5px/18px Verdana !important;
    }
    .post_body .post {
        color: #898598;
    }
    .desc.lighter,
    .desc.lighter.blend_links a {
        color: #b1b0b5;
    }
    .post_body .post {
        color: #898598;
    }
    .ipsList_withminiphoto > li .list_content {
        background: #3b3848;
    }
    .ipsList_withminiphoto>li {
        margin-bottom: 0px;
        background: #3b3848;
    }
    .ipsBox {
        background: rgb(59, 56, 72);
        border: 1px solid #2f2d3a;
    }
    #content {
        background-color: #2f2d3a;
        border: solid 1px #2f2d3a;
    }
    .breadcrumb {
        color: #bebcc7;
        font-size: 11px;
    }
    .pagination .pages a,
    .pagejump {
        display: inline-block;
        font-weight: normal;
        padding: 1px 4px;
        color: #b1b0b5;
    }
    .maintitle {
        background: #3b3848;
        color: #b1b0b5;
        font-weight: normal;
        border-top: 1px solid #2f2d3a;
        border-left: 1px solid #2f2d3a;
        border-right: 1px solid #2f2d3a;
    }
    .author_info .group_title {
        font-weight: normal;
        color: #b1b0b5;
        margin-top: 5px;
    }
    #primary_nav .active a {
        background: #2f2d3a;
        color: #7fbed4;
        font-weight: normal;
        text-shadow: none;
    }
    #primary_nav {
        background: #2f2d3a;
        font-size: 13px;
        padding: 4px 0 0 0;
        border: 1px solid #2f2d3a;
        width: 87% !important;
    }
    #header_bar {
        background: rgb(47, 45, 58);
    }
    body,
    body#ipboard_body.redirector {
        background: transparent url(http://i.imgur.com/ac2LJhb.jpg) repeat 0 0 !important;
    }
    .ipsField .ipsField_title {
        color: #ffffff;
        font-weight: normal;
    }
    .maintitle {
        background: #3b3848;
        color: #b1b0b5;
        font-weight: normal;
        text-shadow: 1px 1px 1px #3b3848;
        padding: 9px 9px 10px;
        font-size: 15px;
        border-top: 1px solid #2f2d3a;
        border-left: 1px solid #2f2d3a;
        border-right: 1px solid #2f2d3a;
    }
    .bbc_url,
    .bbc_email {
        color: #d05f88;
    }
    .ipsBox_container {
        background: #3b3848;
    }
    .unread {
        background-color: #3b3848;
    }
    .ipsSideBlock h3 {
        font: normal 14px helvetica, arial, sans-serif;
        color: #2f2d3a;
        font-weight: bold;
        background: url(http://prodota.ru/forum/public/style_images/Prodota_Images/maintitle.png) repeat-x scroll center top #c52659;
        margin: -10px -10px 10px;
        border-bottom: 1px solid #2f2d3a;
    }
    .ipsSideBlock {
        background: #3b3848;
        padding: 10px;
        margin-bottom: 10px;
        border: 1px solid #2f2d3a;
        border-radius: 3px 3px 0 0;
    }
    .ipsUserPhoto_medium {
        width: 50px;
        height: 50px;
        padding: 1px;
        border: 1px solid #3b3848;
        background: #3b3848;
        -webkit-box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
        -moz-box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
        box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
    }
    .input_text,
    .ipsTagBox_wrapper {
        background: #3b3848;
        border: 1px solid #2f2d3a;
    }
    .general_box {
        background: #3b3848;
        margin-bottom: 10px;
    }
    .row1 {
        background-color: #3b3848;
    }
    .ipsVerticalTabbed_tabs li.active a {
        width: 135px;
        border-right: 1px solid #2f2d3a;
        background: #2f2d3a;
        color: #69c4f1;
        font-weight: bold;
    }
    .ipsVerticalTabbed_tabs li {
        background: #3b3848;
        color: #3b3848;
        border-bottom: 1px solid #2f2d3a;
        font-size: 13px;
    }
    .ipsVerticalTabbed_tabs li a {
        color: #b1b0b5;
    }
    .ipsVerticalTabbed_tabs>ul {
        border-top: 1px solid #2f2d3a;
        border-left: 1px solid #2f2d3a;
        border-right: 1px solid #2f2d3a;
    }
    .general_box h3 {
        font: normal 14px helvetica, arial, sans-serif;
        background: #2f2d3a;
        color: #fff;
    }
    .ipsVerticalTabbed_tabs li a {
        outline: 0;
        color: #aba8bd;
    }
    .ipsFilterbar li a {
        color: #898598;
        text-shadow: 0px 1px 0px #2f2d3a;
    }
    .unread .altrow,
    .unread.altrow {
        background-color: #2f2d3a;
    }
    .ipsFilterbar li.active a {
        background: #2f2d3a;
        color: #fff;
        font-weight: bold;
    }
    .ipsType_pagedesc {
        color: #f5f5f5;
    }
    .ipsSideMenu h4 {
        font-weight: bold;
        color: #f5f5f5;
    }
    .ipsHeaderMenu {
        background: #3b3848;
        background: -moz-linear-gradient(top, #fff 0%, #f6f6f6 70%, #ededed 100%);
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #3b3848), color-stop(70%, #3b3848), color-stop(100%, #2f2d3a));
    }
    #user_link_menucontent #links li {
        text-shadow: 0px 1px 0 rgb(47, 45, 58);
    }
    .ipsSideBlock h3 {
        font: normal 14px helvetica, arial, sans-serif;
        color: #bdbcc1;
        background: #3b3848;
        margin: -10px -10px 10px;
        border-bottom: 1px solid #2f2d3a;
    }
    #user_link.menu_active {
        background: #2f2d3a;
        color: #ffffff;
    }
    #user_link:hover,
    #notify_link:hover,
    #inbox_link:hover {
        background-color: #72CEEE;
    }
    .ipsSideMenu ul li.active a {
        background: #43aff1 url(http://prodota.ru/forum/public/style_images/Prodota_Images/icon_check_white.png) no-repeat 6px 8px;
        color: #fff;
        font-weight: normal;
    }
    .maintitle a {
        color: #ffffff;
        font-weight: bold;
        text-shadow: 1px 1px 1px #2f2d3a;
    }
    #primary_nav a {
        color: #7fbed4;
        background: #2f2d3a;
        border-right: 1px solid #f6f6f6;
    }
    .ipsUserPhoto_mini {
        border: 1px solid #2f2d3a;
        background: #3b3848;
        -webkit-box-shadow: 0px 2px 2px rgb(59, 56, 72);
        -moz-box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
        box-shadow: 0px 2px 2px rgb(59, 56, 72);
    }
    .ipsPad {
        background: #3b3848;
    }
    a:hover {
        color: #f95c71;
    }
    .status_feedback .row2 {
        background: #2f2d3a;
    }
    .ipsUserPhoto_tiny {
        border: 1px solid #2f2d3a;
        background: #3b3848;
        -webkit-box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
        -moz-box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
        box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.1);
    }
    .left_bord {
        border-left: 1px solid #3b3848;
    }
    .maintitle a {
        color: #ffffff;
        font-weight: normal;
        text-shadow: 1px 1px 1px #3b3848;
    }
    .desc,
    .desc.blend_links a,
    p.posted_info {
        font-size: 12px;
        color: #bdbcc1;
    }
    .col_c_forum a {
        font-weight: normal;
    }
    .post_ignore {
        background: #2f2d3a;
        color: #b1b0b5;
    }
    h3,
    strong {
        font-weight: bold;
        color: #d4d5d6;
    }
    .ipsForm_horizontal .ipsField_content,
    .ipsForm_horizontal .ipsField_submit {
        color: #3b3848;
    }
    .input_text,
    .ipsTagBox_wrapper {
        color: white;
        font-style: normal;
        background: #3b3848;
        border: 1px solid #2f2d3a;
    }
    .userpopup h3,
    .userpopup .side+div {
        background: #3b3848;
    }
    .popupInner h3 a {
        color: #7fbed4;
    }
    a {
        color: rgb(127, 190, 222);
    }
    .userpopup .side {
        position: absolute;
        margin-top: -43px;
        margin-left: -5px;
    }
    .message {
        background: #3b3848 !important;
        color: #c2c1c5 !important;
        border: 1px solid #ffffff !important;
    }
    .userpopup .side .ipsButton_secondary {
        background: #2f2d3a;
        color: #bdbcc1;
    }
    #user_latest_status>div {
        background-color: #2f2d3a;
        color: #ffffff;
        font-size: 14px;
    }
    #user_latest_status {
        background: url(http://i.imgur.com/X9Eg4tg.png) no-repeat 0px 50%;
    }
    .ipsVerticalTabbed_tabs li.active a {
        border-right: 1px solid #2f2d3a;
        background: #2f2d3a;
        color: #ffffff;
        font-weight: bold;
    }
    .unread .altrow,
    .unread.altrow {
        background-color: #3b3848;
    }
    .topic_buttons li.disabled a,
    .topic_buttons li.disabled span {
        background: #3b3848;
        box-shadow: none;
        -moz-box-shadow: none;
        -webkit-box-shadow: none;
        text-shadow: none;
        border: 0;
        color: #ffffff;
    }
    .topic_buttons li.non_button a {
        color: #b1b0b5;
    }
    .ipsLikeBar_info {
        background: #353442;
    }
    .post_body .post {
        color: #cbc9d2;
    }
    .bbc_spoiler_content {
        background: #353442;
    }
    blockquote.ipsBlockquote {
        border-left: 2px solid #353442;
        border-right: 2px solid #353442;
        border-bottom: 2px solid rgb(53, 52, 66);
        background: rgb(71, 68, 84);
    }
    p.citation {
        border-left: 2px solid #2f2d3a;
        background: rgb(53, 52, 66);
        border-top: 2px solid #2f2d3a;
        border-right: 2px solid #2f2d3a;
    }
    .ipsType_sectiontitle {
        color: #ffffff;
    }
    .ipb_table tr td {
        border-bottom: 1px dotted #2f2d3a;
        background: #2f2d3a;
    }
    .ipb_table tr td {
        color: white;
    }
    .bar {
        background: #2f2d3a;
    }
    .ipsSettings_sectiontitle {
        font: normal 14px Helvetica, Arial, sans-serif;
        color: #ffffff;
    }
    .unread .altrow,
    .unread.altrow {
        background-color: #2f2d3a;
    }
    .ipsLikeButton {
        color: #ffffff !important;
    }
    .ipsLikeButton.ipsLikeButton_enabled {
        background: url(http://i.imgur.com/VhZitKX.png) no-repeat top left;
    }
    .ipsLikeButton.ipsLikeButton_disabled {
        background: url(http://i.imgur.com/VhZitKX.png) no-repeat bottom left;
    }
    #primary_nav a {
        border-right: 1px solid #3b3848;
    }
    .ipsButton_secondary {
        background: #2f2d3a;
        border: 1px solid #2f2d3a;
        color: #bdbcc1;
    }
    .unread {
        background-color: #2f2d3a;
    }
    ul.post_controls a {
        color: #b1b0b5;
    }
    .topic_buttons li a,
    .topic_buttons li span,
    .ipsButton {
        background: #2f2d3a;
    }
    p {
        margin: 0px;
    }
    body,
    body#ipboard_body.redirector {
        background: transparent url(http://i.imgur.com/jtE1Ico.jpg) repeat 0 0 !important;
    }
    body {
        color: #ffffff;
        background-color: #ffffff;
    }
    html,
    body {
        background: url(http://i.imgur.com/jtE1Ico.jpg) repeat scroll 0 0 transparent;
    }
    .ipb_table tr td {
        border-bottom: 1px dotted #2f2d3a;
        background: #3b3848;
    }
    .unread {
        background-color: #3b3848;
    }
    .unread .altrow,
    .unread.altrow {
        background-color: #3b3848;
    }
    #primary_nav a:hover,
    #primary_nav a.menu_active {
        color: #ed5c6a;
    }
    .bbc_spoiler_content {
        border: 1px #353442 !important;
        background: #2f2d3a;
    }
    .poll_question ol {
        background-color: #3b3848;
    }
    .poll_question h4 {
        background-color: #2f2d3a;
    }
    blockquote.ipsBlockquote {
        font-size: 12px;
        border: 2px solid #2f2d3a;
    }
    cite.ipb {
        border-left: 2px solid #2f2d3a;
        background: #353442;
        background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #474454), color-stop(100%, #2f2d3a));
        background-image: -o-linear-gradient(top, #f6f6f6, #e5e5e5);
        border-bottom: 2px solid #2f2d3a;
        border-right: 2px solid #2f2d3a;
    }
    .ips_photoControls {
        background: #3b3848;
    }
    li.ips_option {
        background: #3b3848;
    }
    .ipsType_pagedesc {
        color: #aba8bd;
    }
    .row1 {
        background-color: #3b3848;
    }
    .signature {
        background: #3b3848;
    }
}
