css:
@-moz-document domain("news.ycombinator.com") {
body, #hnmain {
    background: #222;
}

.comment * {
    color: #aaa;
}

#hnmain a {
    color: #888;
}

td[bgcolor="#ff6600"] {
    opacity: 0.5;
}

td[bgcolor="#ff6600"] a {
    color: #333 !important;
}

form {
    opacity: 0.8;
}

.votearrow {
    background: none;
    border-left: #aaa 2px solid;
    border-top: #aaa 2px solid;
    width: 5px;
    height: 5px;
    margin: 4px 6px;
    padding: 0;
    transform: rotate(45deg);
}

.rotate180 {
    transform: rotate(225deg);
}
}
