@-moz-document domain("donga.com") {
 /* 읽은글 회색 */
A:visited {color: #A6A6A6;}
/* 담장 읽은글 색깔 변경*/
.tbl_type01 .t_left a:visited {color: #A6A6A6;}
 
/* 상단 불펜로고 삭제 */
.logo {display: none;}
 
/* Hot 아이콘 삭제 */
.tbl_type01 .icon_hot {display: none;}
 
/* 명문관, 뉴스 메뉴 없애기 */
#PCHeader > .main_menu > .list_menu > li:nth-of-type(4) > .newsfeed.top04 {display: none;}
#PCHeader > .main_menu > .list_menu > li:nth-of-type(5) > .famous.top05 {display: none;}
.index #PCHeader .main_menu .list_menu li .top09{
display : none;
}
 
/* 광고차단 */
.top_ad, #left_ad, #center_ad02, #right_cont {display: none;}
#center_ad01, .right_menu, .picture {display: none;}
 
/* 게시판 전체 크기 */
.index {
  width: 100%;
  min-width: 1030px;
  background-color: #FFFFFF;
 }
.index #container .contents .left_cont {
  width: 1030px;
  margin-bottom: 9px;
 }

/*조회수 굵게*/
.tbl_type01 .viewV {padding-right:5px; font-weight:bold;}

/* 상단, 하단 배경색 */
.footer, .index #PCHeader .bg {background: #BDBDBD;}

/*게시판칼럼 너비 & 색상*/
.tbl_type01 tbody td:nth-child(1) {background-color:#ffff;}	/*번호 or 게시판*/
.tbl_type01 tbody td:nth-child(2) {background-color:#ffff;}	/*제목*/
.tbl_type01 tbody td:nth-child(3) {background-color:#ffff; width:100px;}	/*글쓴이*/
.tbl_type01 tbody td:nth-child(4) {background-color:#ffff; width: 80px;}	/*날짜*/
.tbl_type01 tbody td:nth-child(5) {background-color:#ffff;}	/*조회*/
 
/* 게시판 헤드 높이 수정 */
.tbl_type01 thead tr td {height: 25px;}
  
.tbl_type01 tbody tr td {padding: 0 4px 0 0;}
 
/* 게시판 글 목록 간격 
.tbl_type01 tbody tr td {height: 32px;}*/
 
/* 말머리 대괄호 추가 */
.tbl_type01 tbody tr td .word:before {content: '▶'}
.tbl_type01 tbody tr td .word:after {content: '◀'}
.tbl_type01 tbody tr td .word,.main_contents .nav .bestTitle {color: #008299;}
 
/* 게시판 음영 */
.tbl_type01 tr:nth-child(odd) {background-color: #fff;}
.tbl_type01 tr:nth-child(even) {background-color: #EAEAEA;}
/*.tbl_type01 tr:hover {background-color: #FFFFFF;}*/
 
/*본문 사진 크기*/
#contentDetail img, video {
  width: auto !important;
  max-width: 978px !important;
  text-align: center !important;
  }
 
/* 게시글 차단글 안보이게 하기 */
*[id^='list_block_'] {display: none;}
  
/* 게시글 내 추천,조회,리플 오른쪽으로 옮기기 */
.view_head .items .text {
  display: inline-block; 
  padding: 0 10px 17px 10px;
  width: 1000px;
  }
 
/* 이전 게시판 가리기 */  
.prev_list {display: none;}
  
/* 검색창 오른쪽으로 이동, 높이 변경 */
.list_search {
  float: left;
  height: 30px;
  width: 318px;
  padding: 0 20px 0 0;
  border: 1px solid #ccc;
  background: #fff;
  }
 
/* 촌스런 담장 제목 없애기 */
.tit_today {display: none;}
 
/* 담장 타이틀 스타일 */
.main_contents .nav .bestTitle {
  color: white;
  text-align: center;
  font-size: 100%;
  background: #BDBDBD;
  padding: 0.5px 0.5px 0.5px;
  font-family: "나눔고딕코딩";
  }
 
/* 댓글 갯수 색상 변경 */
.tbl_type01 .replycnt {
  color: #008299;
  font-weight: bold;
  }
 
/* 댓글 입력 박스 */
.left_cont table {
  width: 100%;
  }
#contentTable {
  width: 95%;
  margin:0 0 0 30px;
  }
 
/* 댓글 레이아웃 배경 */
.reply_list {padding: 10px 0px 10px 0px;}
 
/*댓글 사진 크기*/
.reply_list .txt_box img, video {
  width: auto !important; max-width: 978px !important;
  text-align: center !important;
  }
  
/* 타인 댓글 설정 */
/* 타인 댓글 한줄씩 색구분 */
.reply_list .other_reply .txt:nth-child(odd) {
  position: relative;
  float: left;
  max-width: 90%;
  padding: 15px 10px;
  background: rgba( 255, 255, 255, 0);
  border-radius: 0 10px 10px 10px;
  text-overflow: ellipsis;
  text-align: left;
  font-size: 13px;
  font-family: "인터파크고딕 B" , "InterparkGothicBold";
  }
 
.reply_list .other_con:nth-child(odd) {
  overflow: hidden;
  position: relative;
  margin: 0 0 0 0;
  padding: 10px 0 0 55px;
  background: #f7f7f7
  }
.reply_list .other_con:nth-child(even) {
  overflow: hidden;
  position: relative;
  margin: 0 0 0 0;
  padding: 10px 0 0 55px;
  background: #fff
  }
  
.reply_list, #cmtFormTable td {
  background: #f7f7f7 !important;
  }
 
.reply_list .other_con .txt .icon_arr {display: none;}
 
.reply_list .other_reply .txt span, 
.tbl_type01 thead tr td {
  color:#000;
  }
 
.reply_list .other_reply .txt, 
.reply_list .other_con .txt .icon_arr,
.reply_list .my_reply .txt {
  background: #f7f7f7;
  }
 
/*타인 댓글 포토사진*/
.reply_list .other_con .photo img {
  position: absolute;
  left: 10px;
  border-radius: 20%;
  border:1px solid #ddd;
  }
.reply_list .other_con .photo {
  position: absolute;
  top: 10px;
  left: 0;
  width: 37px;
  height: 37px;
  border-radius: 100%;
  border: 0px solid #bfbfbf;
  }
  
/*타인 댓글 경계선*/
.reply_list .other_con {
  border-bottom:2px solid #ccc;
  }
 
/*타인 닉네임 설정*/
.reply_list .other_reply .txt_box .name {
  color:#5181e3;

  }
  
/*본인 댓글 설정*/
/*본인 닉네임 설정*/
.reply_list .my_reply .txt_box .name {
  color:#FF0000;
  font-weight:bold;
  font-family: "인터파크고딕 B";
  }
.reply_list .my_con {
  border-bottom:2px solid #ccc;
  font-size: 13px !important;	
  }
  
/*본인 댓글 배경색 및 위치*/
.reply_list .my_con {
  overflow: hidden;
  position: relative;
  margin: 0 0 0 0;
  padding: 10px 0 0 55px;
  background: #FFFFF0;

  }
.reply_list .my_reply .txt {
  float: left;
  background: rgba( 255, 255, 255, 0);  
  font-family: "인터파크고딕 B";
  }

.reply_list .my_con .photo {
  position: absolute;
  top: 10px;
  left: 10px;
  width: 37px;
  height: 37px;
  border-radius: 20%;
  border: 1px solid #bfbfbf;
  }
 
.txt {
  margin-top:-10px;
  margin-bottom:-10px;
  }
 
/* 댓글 작성일시 & 아이피 주소의 색상과 글꼴 변경 */
.reply_list .date,
.reply_list .ip {
  display: inline;
  color: gray !important;
  font-size: 10px !important;
  font-family: 나눔바른고딕;
  }
/* 댓글 아이피 괄호로 감싸기 */
.reply_list .ip:before {content:'['}
.reply_list .ip:after {content:']'}
 
/* 하단 웹툰 가리기 */
.bigtoon_sub {display:none;}

/*폰트*/
.left_cont table {font-family:"나눔고딕" !important ; font-size:14px;} /*글목록*/
.ar_txt {font-family:"나눔고딕" !important; font-size:15px;} /*본문*/
body {font-family:"나눔고딕 Extrabold" !important; font-size:16px;} /*글제목*/
.lists .items .text .title a {font-family:"나눔고딕 Bold" !important; font-size:12px;} /*담장글*/

/*커서 하이라이트*/
.tbl_type01 tr:hover {background-color:#D0FFDB;}

/*글번호*/
.tbl_type01 tbody tr td:first-child {font-size:12px; color:#008299;}

/*담장글제목 긴글조정*/
.lists_today .items .text .title a {width:262px !important; text-overflow:ellipsis !important;
overflow:hidden; white-space: nowrap;}
}
