css:
/*
Yandex Mail Compact Redesign Green
Author: JeanPaulLucien
Version: 1.26
Home Page: https://userstyles.org/users/364674
Goal URL: https://mail.yandex.
*/
@-moz-document url-prefix('https://mail.yandex.') {

/*[[custom-bg]]*/    
/*[[invis-mail]]*/

.mail-App-Cover {background-image: none !important;
background-color: transparent !important;}
.mail-Page-Body {font: 10pt/1.5 Arial,sans-serif;}
.mail-App-Header {height: 40px;max-width: 100%;}
.mail-App-Header-Item .mail-Logo {margin-left: 20px;}
.mail-Logo-Yandex {color: rgba(255,255,255,.8);}
.svgicon-boot--Yandex_rus {height: 40px;}
.svgicon-boot--Mail_rus {color: rgb(236, 243, 43);height: 40px;}
.svgicon-boot--Mail_rus use {}	
.mail-App-Header-Item_middle {background: linear-gradient(to right, transparent, rgba(50,50,50,.3) 5%, rgba(50,50,50,.3) 95%, transparent);
padding-left: 2%;}
.mail-App-Header-Item .mail-ui-Link {
		color: azure;
		font: 600 14pt/1.2 Arial !important;
		letter-spacing: 2px;
		text-shadow: -1px -1px 0 black;
	}
	.mail-App-Header-Item .mail-ui-Link:hover {
		background: rgba(22,22,22, .5);
		color: rgb(236, 243, 43);
		text-shadow: -1px -1px 1px #58BC05;
	}

.mail-Search.is-folded .mail-Search-Button {
	color: rgba(0, 0, 0, .85);
}
._nb-pseudo-button ._nb-button-content {
	padding: 0 6px;
	line-height: 26px;
}
._nb-pseudo-button ._nb-button-content {
	line-height: 26px;
	font-size: 12pt;
}

.svgicon-mail--Header-Themes:hover {
	color: azure;
}
.mail-App-Header-Item .mail-GhostButton:hover {
	border-color:rgba(255,255,255,.4);
	color: azure;
}

.mail-GhostButton:not(:hover):not(:focus):not(.is-active) > .svgicon {
	color: floralwhite;
}

	._nb-popup-tail::before {background: green; border: 2px #fff solid;}
	._nb-popup {box-shadow: -6px 6px 15px 5px rgba(15,100,15,.8);background: rgba(15,100,15,.9); border: rgba(255,255,255,.9) 2px solid;border-radius: 4px;}
	._nb-popup-content {padding: 0 12px;}
	._nb-popup-link {font-size: 13pt; text-transform: uppercase; text-align: center; font-family: verdana;}
	._nb-popup-link:focus, ._nb-popup-link:hover {background: linear-gradient(to right, transparent, rgba(236,243,43,0.7) 10%, rgba(236,243,43,0.7) 90%, transparent);}
	.settings-popup {width: auto;}
	.settings-popup-top {background: rgba(201, 226, 172, .9);padding: 2px; border: 1px solid rgba(255, 255, 255, .9); border-radius: 4px;}
	.settings-popup-title {font-size: 14pt; padding: 4px 16px; background: linear-gradient(to left, rgba(15,100,15,.8), #fff); border-radius: 4px 4px 0 0; box-shadow: 0 0 2px #fff; margin: 2px; border: 1px solid rgba(255,255,255,.8);}
	.settings-popup-title:hover {border: 1px #000 solid; box-shadow: 0 0 2px 0 #000;}
	.settings-popup-title-content {color: green;}
	.settings-popup-menu {background: #EDE363; border: 1px #fff solid; box-shadow: 0 0 2px 0 #fff; margin: 2px; padding: 2px 10px;} 
	.settings-popup-menu:hover {background: inherit; border: 1px #000 solid; box-shadow: 0 0 2px 0 #000;}
	.settings-popup-menu-other {background: rgb(212, 135, 242);}
	.settings-popup-menu-service {background: rgb(137, 255, 221);}
	.settings-popup-menu-item {margin: 2px; padding: 5px 15px 5px 0;} 
	.settings-popup-menu-item:hover {background: rgba(75,60,60,.4); box-shadow: 0 0 2px 0 #000; }
	.settings-popup-menu-item-content {font-size: 12pt;}
	
	.mail-Search {
		opacity: 0.65;
	}
	.mail-Search:hover {
		opacity: 0.8;
	}
	.mail-Search.is-fooled {
		max-width: 220px !important;
	}
	
	.mail-App-Header-Item_middle {
		margin-left: 28px;
	}
	.mail-ThemeOverlay, .mail-ThemeOverlay-Content {
		background: rgba(0,0,0,.7);
	}
	.mail-ThemeOverlay-Close {
		top: 28px;
		color: white;
	}
	.mail-ThemeOverlay-Title {
		padding: 15px 0px;
		color: #008000;
		text-shadow: 0px 0px 2px #000;
		font-size: 18pt;
	}
	.mail-ThemeOverlay-List-Item-Thumbnail::after {
		background: linear-gradient(180deg, transparent, rgba(100,155,100,.5));
	}
	.mail-ThemeOverlay-Settings-Container {
		height: auto;
		background: rgb(102, 153, 85) none repeat scroll 0% 0%;
	}
	.mail-GhostButton.mail-AccountSecurity {
	height: 100%;
	width: 16px;
	padding: 8px 0 4px 0;
	transition: all 1s ease 0s;
}
	.mail-GhostButton.mail-AccountSecurity:hover {
		width: 24px;
}	
	.mail-App-Header-Item .mail-User {
		color: rgba(255,255,255, .9);
	}
	.mail-App-Header-Item .mail-User:hover {
		color: azure;
	}
	.mail-App-Header-Item .mail-User-Name {
		font-family: Georgia;
		font-size: 13pt;
		text-shadow: 0 -1px 1px black;
		max-width: 50px !important;
	}
	.mail-App-Header-Item .mail-User-Name:hover {
		max-width: 100% !important;
		overflow: visible;
	}
	.mail-App-Header-Item .mail-User-Picture {
		border-radius: 0;
	}
	.mail-User > .mail-User-Picture {
		box-shadow: none;
		height: 40px;
		width: 40px;
	}
	.mail-User-Picture.is-updated::before {
		width: 10px;
		height: 10px;
		border-radius: 0 0 0 2px;
		background: rgb(0,237,0);
		border: 1px solid rgba(0,0,0,0.9);
}
.mail-Layout-Inner {
		box-shadow: 0 -1px 80px 0 #001E00;
	}
.mail-Layout-Aside {
	width: 1%;
	min-width: 1%;
	background-color: rgba(226, 255, 204, .7);
}
.mail-FolderList-Setup_link {
	font: 9pt Verdana,sans-serif;
	color: rgba(255, 255, 255, .95);
}
.mail-NestedList-Item {
	padding: 0 10px;
	line-height: 26px;
	font-size: 11pt;
	font-family: "Franklin Gothic Book";
}
.mail-Toolbar {
	height: 40px;
}
.mail-Toolbar-Content {
	height: 40px;
}
.mail-Toolbar-Item.mail-Toolbar-Item_abook-deselect-all, .mail-Toolbar-Item.mail-Toolbar-Item_abook-select-all, .mail-Toolbar-Item.mail-Toolbar-Item_main-deselect-all, .mail-Toolbar-Item.mail-Toolbar-Item_main-select-all {
	padding: 0 14px 0 10px;
}
.mail-Toolbar-Item {
	padding: 0 7px;
}
.mail-ui-Icon {
	display: none;
}
.mail-Toolbar-Item_top .mail-ui-Icon {
	display: inline-block;
}
.mail-Toolbar-Item {
	font-size: 11pt;
}
.mail-Toolbar.is-fixed .mail-Toolbar-Content {
	height: 32px;
}
.mail-Toolbar-Background {
	background: rgb(38, 175, 38);
	box-shadow: 0px 1px 2px 0px;
}
.ns-view-container-desc .mail-ui-Icon {
	display: none;
}
.ns-view-toolbar-button-toolbar-settings:hover {
	display: inline-block;
}
.theme-seasons.m-skin-summer-8 .mail-Layout-Content, .mail-Layout-Content {
	background-image: none;
}
.mail-Layout-Content, .theme-seasons .mail-Layout-Content.mail-Layout-Content, .theme-seasons.with-dark-page.with-dark-content:not(.mail-Page_minified) .mail-Search:not(.is-folded) ._nb-button-content, .theme-seasons.with-dark-page.with-dark-content:not(.mail-Page_minified) .mail-Search:not(.is-folded) ._nb-input-view, .theme-seasons.theme-seasons:not(.with-experiment-32841):not(.mail-Page_minified):not(.with-dark-page):not(.with-dark-content) .mail-Search:not(.is-folded) ._nb-button-content, .theme-seasons.theme-seasons:not(.with-experiment-32841):not(.mail-Page_minified):not(.with-dark-page):not(.with-dark-content) .mail-Search:not(.is-folded) ._nb-input-view {
	background-color: rgba(201, 226, 172, .9);
}
.mail-MessageSnippet, .mail-MessageSnippet_height_normal.mail-MessageSnippet_height_normal.mail-MessageSnippet_height_normal, .mail-MessagesList_height_normal .mail-MessageSnippet {
	height: 33px;
	line-height: 32px;
}
.mail-MessageSnippet .mail-MessageSnippet-Checkbox, .mail-MessageSnippet .mail-MessageSnippet-Item .mail-User-Picture, .mail-MessageSnippet_height_normal.mail-MessageSnippet_height_normal.mail-MessageSnippet_height_normal .mail-MessageSnippet-Checkbox, .mail-MessageSnippet_height_normal.mail-MessageSnippet_height_normal.mail-MessageSnippet_height_normal .mail-MessageSnippet-Item .mail-User-Picture, .mail-MessagesList_height_normal .mail-MessageSnippet .mail-MessageSnippet-Checkbox, .mail-MessagesList_height_normal .mail-MessageSnippet .mail-MessageSnippet-Item .mail-User-Picture {
	width: 24px;
	height: 24px;
}
.b-intruder_perda .b-intruder__content {
	padding: 6px 15px 10px;
}
.mail-Page_with_Todo .mail-Layout-Panes_2pane {
	padding-bottom: 20px;
}
.mail-App-Footer_2pane {
	height: 40px;
	padding: 5px 0 5px 15px;
	flex-direction: row;
}
.b-window_minimized {
	width: 148px;
	height: 24px;
}
.mail-Message, .mail-Message-Content, .mail-Message-Toolbar-Content {
	background: transparent !important;
}
.mail-Message-Toolbar {
	background: rgb(201, 226, 172) !important;
}
.mail-Message-Toolbar_thread {
	height: 38px;
	background: #000;
}
.mail-Message-Toolbar_thread .mail-Message-Toolbar-Content {
	min-height: 28px;
	height: auto;
	line-height: 28px;
}
.mail-Message_page .mail-Message-Toolbar-Subject-Wrapper {
	padding: 6px 0;
}
.mail-Message-Toolbar-Subject_message.mail-Message-Toolbar-Subject {
	line-height: 28px;
}
.mail-Message_page .mail-Message-Toolbar-Subject {
	font-size: 17pt;
	line-height: 28px;
	min-height: 28px;
}
.mail-Message-Toolbar-Subject::after {
	background: transparent linear-gradient(to left, rgb(201, 226, 172), rgba(201, 226, 172, 0));
}

.mail-Message-Head-Recievers .mail-User-Picture {
	display: none !important;
}		
.mail-Message_page .mail-Message-Attach .mail-MessageSnippet-Item_attachments {
	padding: 0 16px 0 78px;
}
.mail-Message_page .mail-Message-Body {
	padding: 6px 10px;
	background: rgb(225, 235, 235);
	border: 1px solid rgba(0,0,0,.3);
	border-left: 0;
}
.mail-ThreadSidebar-Item_title {
	padding: 6px 0;
}
.mail-ThreadSidebar-wrap {
	background-color: transparent;
	background: linear-gradient(to bottom, rgb(201, 226, 172) 90%, transparent); /* rgba(240,255,255,.8) */
}
.mail-QuickReply, .mail-QuickReply_container {
	background: transparent;
}
.mail-QuickReply_container {
	margin-right: 291px;
}
.mail-QuickReply::before {
	background: rgb(225, 235, 235);
}
.mail-Compose {
	background: transparent;
}
.mail-User-NameInput-View {background: transparent;} /* Удаление фона у имени отправителя */
.mail-Compose-Field-Input-View {background: azure; box-shadow: 0 0 2px 2px rgba(39,70,10,.5);}
.mail-Suggest {
		background: azure;
		background-image: none;
	}
	.mail-Suggest-Item:not(.mail-ui-Grid_size_double) {height:36px;}
	
	
.cke_top {border: 2px rgba(39, 70, 10, .5) solid; padding: 6px 6px 2px;	border-radius: 5px;}
a.cke_button {	color: rgb(5, 50, 10);}
.cke_inner {background: none;}
.cke_contents {padding-top: 10px;}
.mail-Compose-Field.is-focused .mail-Compose-Field-Input-View {box-shadow:0 0 0 azure,0 0 0 azure,0 0 0 azure,inset 0 -3px 0 #fedd55}
.cke_editable {background-color: azure;}
.cke_1 .cke_bottom {background-image: none;}
.mail-ui-Grid_size_double {height: 36px;}
.mail-User-Picture_size_28.mail-User-Picture_sprite {background-position: -70px 50px;}
	.mail-User-Picture_size_28 {width: 24px;min-width: 16px;height: 24px;}
	.mail-User-Picture {border-radius: 25%;}
	.mail-AbookEntry:hover .mail-AbookEntry-Action > .svgicon {color: rgb(5, 50, 10);}
}
