css:
@-moz-document domain("achilles.tu.kielce.pl") {
#visual-portal-wrapper {
    background-color: #232323;
}
#portal-column-two {
    color: #AAA;
    vertical-align: top;
    min-width:100px;
}

a	{
    color: #CCC;
}


#portal-columns	{
 	width:80%; 
	margin-left: auto;
    margin-right:auto;
}

#portal-column-one	{
 	
 	width:250px;
	vertical-align: top;
}

#portlet-news > dd,#portlet-news > dl,#portlet-news > dd > a > span, #thePloneCalendar > tbody > tr, #thePloneCalendar > thead > tr:nth-child(1)	{
	background-color: #333;   
    color: #CCC;
    
}


#portlet-news > dt, #portlet-navigation-tree > dt, #portlet-login > dt, #portlet-login > dd.portletItemLast.even	{
    background-color: #444;
    border-style: none;
    
}

#thePloneCalendar	{
    background-color: #333;  
    
}
#portal-column-content	{
	vertical-align: top;
}

#region-content, #portlet-navigation-tree > dd, #portlet-login, #portlet-login > dd.portletItem.odd, #visual-portal-wrapper > table.header > tbody, #visual-portal-wrapper > table.header > tbody > tr, #thePloneCalendar > thead > tr > th, #thePloneCalendar > thead > tr > td, #portlet-events > dd {
 	background-color: #333;
	color: #CCC; 
}

#region-content > div:nth-child(2) > h1	{
 	color: #EEE;   
}

#portlet-navigation-tree > dd > ul > li > div > a, #portlet-navigation-tree > dd > ul > li > ul > li > div > a, #portlet-navigation-tree > dd > ul > li > ul > li > ul > li > div > a, #portlet-navigation-tree > dd > ul > li > ul > li > ul > li > ul > li > div > a {
 	border-top-width: 0px;  
    border-right-width: 0px; 
    border-bottom-width: 0px; 
    border-left-width: 1px; 
    padding-left: -5px;
}

.currentNavItem, #portlet-navigation-tree a:hover	{
	border: 0px none black;   
	border-left: 1px solid #333;
}
h1, h2, h3, #portlet-events > dd > a > span	{
 	color: white !important;   
}

html	{
	background-color: #333 ; 
}

#portal-footer > table > tbody > tr, #thePloneCalendar > tbody > tr > td, #portlet-events > dt	{
 background-color: #444;   
}

#visual-portal-wrapper > table.header > tbody > tr > td.header-left, #visual-portal-wrapper > table.header > tbody > tr > td.header-right, #portlet-login, #portal-footer > table > tbody > tr > td.footer-left, #portal-footer > table > tbody > tr > td.footer-right	{
 display: none;   
}
#thePloneCalendar > tbody > tr > td.event > a {
 color: #ce6161 !important;   
}

#thePloneCalendar > tbody > tr > td.event	{
 background-color: #555;  
}


a:visited	{
 color:#ccc !important;
}

#visual-portal-wrapper 	{
border: 10px none black !important;   
}

#portal-footer	{
 background: none;   
}

#portal-footer > table > tbody > tr	{
 height: 25px;   
}

.todaynoevent	{
border: 2px solid #CCC !important;    
}

h3>span, #region-content > div.plain > p > a {
color: #ce6161 !important
}
}
