        margin-right: 10px;
    }
    
    .discussionList .sectionHeaders .lastPost a span {
        margin-right: 10px;
    }
    
    /* Ketut */
    
    .discussionListItem {
        border-bottom: 1px dashed #383a3f;
        padding: 0;
        display: block;
        position: relative;
        background: none;
    }
    
    .discussionListItem.sticky {
        background-color: #202328;
    }
    
    .discussionListItem:nth-child(2n):not(.sticky) {
        background-color: #191c1f;
    }
    
    .discussionList .posterAvatar {
        width: 32px;
        display: inline-block;
    }
    
