        border-right: 3px solid #373865;
        border-radius: 3px 0 0 3px;
    }
    
    .discussionListItem .posterAvatar .avatar.miniMe::after {
        content: "\f075";
        font-family: "FontAwesome";
        display: inline-block;
        color: #b7c8e8;
        top: -12px;
        left: -25px;
        position: absolute;
        font-size: 20px;
        transform: scaleX(-1);
