css:
@-moz-document domain("chaosgamez.com") {
/*
This is a CSS Stylesheet I use for browsing 3DSChaos. and now its for ChaosGamez
It does various things the creators of the site may not like necessarily. Consider donating to their site if you plan to use this.
*/


/*Fix Thanks Boxes*/
.HiddenContent,
.UnhiddenContent {
	background-color: #222;
    border-color: #666;
}

.HiddenContent .attribution,
.UnhiddenContent .attribution {
	background: #333;
    border-color: #666;
}

/*Remove "." and ".," crap throughout the forum*/
.userTitle,
.profilePage .primaryUserBlock .userBlurb
{
	display: none !important;
}

/*Hide New Downloads Section*/
.BRMSContainer,
div #widget-rows-14 {
	display: none !important;
}

/*Hide Chatroom
div #siropuChat {
	display: none;
}*/

/*Fix Annoying Chat Color*/
div #siropuChat span[style^="color: #141414"] {
	color: #909090 !important;
}

/*Fix DateTime Code Background*/
.siropuChatMessages>li .DateTime, #siropuChatForumActivity>li .DateTime {
	background-color: #000000;	
}


/*Hide Gateway/nds-card upsell*/
div .slider-wrapper {
	display: none;
}

/*Hide Donation Button*/
div #widget-22 {
	display: none;
}

/*Hide new scroller thing*/
.PanelScroller .scrollContainer, .PanelScroller .navContainer {
	display: none;
}

/*Disable Stupid Avatar Spin*/
.avatar:hover, .avatar img:hover, .img.s:hover {
    -moz-transition: none;
    -webkit-transition: none;
    -o-transition: none;
    -ms-transition: none;
    transition: none;
    -moz-transform: none;
    -webkit-transform: none;
    -o-transform: none;
    -ms-transform: none;
}
}
