css:
@-moz-document domain("ruten.com.tw")
{
	/* 露天 */
	.mybid-tab-buyer>a:after,
	.mybid-tab-seller>a:after,
	.mybid-attraction-top {
		display: none !important;
	}

	:root {
		--font1: 400 calc(7pt + .3vw) sans-serif;
		--font2: 400 calc(7pt + .2vw) sans-serif;
	}

	html, body,
	a, article, aside, b, body, center, details, div, dl, embed, figcaption, figure, footer, form, h1, h3, h4, h5, header, html, i, iframe, img, label, li, nav, object, ol, p, q, s, section, small, span, strong, table, u, ul, var,
	.rt-product-card-name,
	.index-nav .index-nav-all-categories-nav,
	.rt-header-search-input,
	.rt-header-search-select,
	.board-setup-bd .rt-form-label,
	.filter-category-item,
	.rt-pagination .rt-pager-button,
	.rt-pagination li a {
		font: var(--font1);
	}

	.rt-header-top,
	.rt-header-top .rt-header-topnav-link,
	.index-all-category-level-two-wrap .index-all-category-level-two-title,
	.rt-footer-nav-name {
		font: var(--font2);
	}

	body {
		min-width: 92em;
		background: #ccc;
	}

	.index-content-wrap {
		background: #ccc;
	}

	.rt-page,
	.layout {
		padding: 0 0 6em;
	}

	.rt-header-top {
		padding-bottom: 0;
		white-space: nowrap;
	}

	.rt-header {
		padding: 0;
		background: #ff9709;
	}

	.rt-header-logo {
		filter: saturate(0) brightness(2);
	}

	.index-header-hot-search-item a {
		color: #000;
	}

	.index-popular-category-level-two {
		flex: 0 0 10em;
	}

	.rt-header-dropdown,
	.rt-chat-inbox-bubble {
		box-shadow: 0 2px 4mm 1px rgba(0, 0, 0, .4) !important;
	}

	.rt-chat-inbox::before {
		content: "";
		border-bottom: 5pt solid #fff;
		border-left: 5pt solid transparent;
		border-right: 5pt solid transparent;
		z-index: 101;
	}

	.rt-chat-main-container {
		width: auto;
	}

	.contact-item-container {
		padding: 16px .5em;
	}

	.contact-item-information {
		flex: 1 1 auto;
	}

	.rt-chat-main-side {
		flex: 1 1 auto;
	}

	.rt-chat-main-page {
		flex: 5 5 auto;
	}

	.contact-item-chattime {
		position: static;
		flex: 0 0 auto;
		width: auto;
		max-width: none;
		margin-left: 2mm;
	}

	.rt-footer {
		max-height: 5em;
		overflow: hidden;
		transition: .5s;
	}

	.rt-footer:hover {
		max-height: 20em;
		box-shadow: 0 0 2mm #aaa;
		z-index: 3;
	}

	.index-nav {
		white-space: nowrap;
	}

	.index-main-wrap,
	.index-theme-content-aside,
	.everyone-is-looking-for-item,
	.index-banner-top {
		flex: 1 1 auto;
		margin-left: auto;
		margin-right: auto;
	}

	.index-theme-content-aside>* {
		margin: 0 auto;
	}

	.index-theme-content-main {
		flex: 2 2 auto;
	}

	.index-theme-card-list {
		flex: 1 1 auto;
		padding: 0 2%;
	}

	.everyone-is-looking-for-item-image-wrap,
	.rt-store-cstore-button {
		width: auto;
		height: auto;
	}

	.w-200 {
		flex: 0 0 200px;
		margin-right: 10px;
	}

	.filter-section {
		background: #eee;
	}

	.search-result-wrapper,
	.w-1037,
	.rt-store-content {
		flex: 1 1 auto;
		width: calc(100% - 210px);
	}

	.justify-between,
	.search-result-wrapper,
	.rt-store-main,
	.rt-store-goods-listing,
	.loading-overlay {
		width: 100%;
	}

	.loading-overlay {
		background: rgba(0, 0, 0, .3);
		z-index: 200;
	}

	.ebay-result-container {
		width: calc(80vw - 10px);
		margin: 0 0 1em;
		border-radius: 3mm;
		background: #fed;
		box-shadow: inset 0 0 2mm #c82;
	}

	.ebay-result-container .rt-slideshow {
		padding: 0 3mm;
	}

	.ebay-result-container .rt-slideshow-slide {
		width: 16em;
	}

	.search-result-container.top-part {
		background: #cde;
		border-radius: 3mm;
		box-shadow: inset 0 0 2mm rgba(0, 0, 0, .3);
	}

	.search-result-container .rt-product-card {
		width: 100%;
		background: #eee;
		border-radius: 2mm;
		overflow: hidden;
	}

	.index-product-card-wrap .rt-product-card,
	.product-item,
	.rt-store-product-list-main .rt-product-card {
		flex: 1 1 15em;
		width: auto;
		max-width: 18em;
	}

	.index-product-card-wrap .rt-product-card+.rt-product-card {
		margin: 4px !important;
	}

	.rt-product-card:hover {
		background: #fff;
		box-shadow: 0 0 3mm #03a;
		z-index: 2;
		transform: none;
	}

	.rt-product-card .rt-product-card-img {
		object-fit: cover;
		transition: .2s;
	}

	.rt-product-card-name {
		position: relative;
		height: auto;
		display: -webkit-box;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical;
		z-index: 2;
	}

	.rt-product-card:hover .rt-product-card-img {
		transform: scale(1.05);
	}

	.guess-you-like-hook-inner,
	.back-to-top {
		width: 4em;
		height: 4em;
		right: 4mm;
		z-index: 3;
	}

	.breadcrumb,
	.rt-header-inner,
	.container {
		width: calc(200px + 80vw);
		max-width: none;
	}

	.breadcrumb-content .breadcrumb-text {
		padding: .2em 0;
	}

	.rt-footer-inner {
		width: 95%;
		max-width: 100em;
	}

	.breadcrumb {
		margin: 0 auto .7em;
	}

	.rt-wrap {
		width: 98%;
		min-width: 94em;
		max-width: 100em;
		padding: .5em;
		margin: 2mm auto 0;
		border-radius: 3mm;
		box-sizing: border-box;
		background: #eee;
		box-shadow: 0 0 3mm #777;
	}

	.intro-section-left {
		flex: 1 1 480px;
	}

	.intro-section-right {
		flex: 9 3 720px;
	}

	.item-gallery-main-image-wrap {
		width: auto;
		height: auto;
	}

	.item-detail-table,
	.goods-seller-coupon,
	.rt-store-content {
		background: none;
	}

	.goods-seller-coupon-title {
		display: inline-block;
		width: auto;
	}

	.goods-seller-coupon {
		margin: 0 auto;
	}

	.item-info-detail {
		flex: 1 1 auto;
		max-width: none;
	}

	.item-detail-table {
		width: 100%;
		background: #f6f6f6;
	}

	.item-detail-table td:not(.title) {
		position: relative;
		padding: 0;
	}

	.item-detail-table .title {
		padding: .5em;
	}

	.item-detail-table tr:hover td {
		background: #fff;
	}

	.item-detail-table ul {
		max-height: 9.5em;
		padding: .5em;
		overflow: hidden;
		transition: .3s;
	}

	.item-detail-table tr:hover ul {
		max-height: 30em;
		overflow: auto;
	}

	.item-detail-table td:not(.title)::after {
		position: absolute;
		top: 8em;
		height: 2.5em;
		display: inline-block;
		width: 100%;
		content: "下拉更多⬇︎";
		text-align: right;
		background: linear-gradient(0deg,
				#888 0,
				#f6f6f6 1pt,
				#f6f6f6 20%,
				transparent 100%);
		opacity: 1;
		transition: .3s;
	}

	.item-detail-table tr:hover td::after {
		opacity: 0;
		visibility: hidden
	}

	.item-detail-table .title {
		width: 5.5em;
	}

	.item-info-owner {
		background: #f6f6f6;
	}

	.item-image-wrap {
		margin: 1em auto;
	}

	.item-image-wrap img {
		box-shadow: 0 0 1mm #888;
	}

	.mybid-tab-buyer,
	.mybid-tab-seller {
		position: relative;
		padding: .4em .3em 0;
		border-radius: 4mm 4mm 0 0;
	}

	.mybid-tab-buyer {
		background: linear-gradient(0deg,
				#36c 0,
				#36c 70%,
				transparent 100%);
	}

	.mybid-tab-seller {
		background: linear-gradient(0deg,
				#d22 0,
				#d22 90%,
				transparent 100%);
	}

	.mybid-tab-buyer::before,
	.mybid-tab-seller::before {
		content: "買家專區";
		font-size: 1.1em;
		line-height: .5;
		color: #fff;
		text-shadow: 0 1px 1px #333,
			0 -1px 1px #333,
			1px 0 1px #333,
			-1px 0 1px #333;
	}

	.mybid-tab-seller::before {
		content: "賣家專區";
	}

	.mybid-tab-buyer::after,
	.mybid-tab-seller::after {
		position: absolute;
		left: 2.5rem;
		bottom: calc(.8rem + 2.6mm);
		content: "BUY";
		font: 600 70%/1 sans-serif;
		letter-spacing: .8em;
		color: #fff;
		text-shadow: 0 1px 1px #333,
			0 -1px 1px #333,
			1px 0 1px #333,
			-1px 0 1px #333;
	}

	.mybid-tab-seller::after {
		content: "SELL";
		letter-spacing: .4em;
	}

	.mybid-tab-buyer::first-letter,
	.mybid-tab-seller::first-letter {
		font-size: 1.7em;
	}

	.mybid-tab-buyer>a,
	.mybid-tab-seller>a {
		padding: 0 2mm;
		border-radius: 2mm 2mm 0 0;
		margin-left: 2px;
		background-position: 0 0 !important;
		box-shadow: 1px -1px 1px #888, -1px -1px 1px #888;
	}

	.mybid-tab-buyer>a {
		background: linear-gradient(0deg,
				#d7f3ff 0,
				#fff 100%);
	}

	.mybid-tab-seller>a {
		background: linear-gradient(0deg,
				#ffdbe3 0,
				#fff 100%);
	}

	.main-area {
		display: flex;
		flex-wrap: wrap;
	}

	.main-area .column-base {
		flex: 1 1 40%;
		margin: 2mm 1mm;
		background: #f6f6f6;
	}

	.mybid-table tbody tr:hover td {
		background: linear-gradient(0deg,
				#8ae 0,
				#f5faf5 6%,
				#f5faf5 94%,
				#8ae 100%);
	}

	.rt-common-base .rt-form-select,
	.rt-common-base .rt-form-textarea {
		min-width: 75%;
	}

	#mainPagination,
	.rt-pagination {
		position: fixed;
		bottom: 9mm;
		right: 12%;
		background: rgba(240, 240, 240, .8);
		border-radius: 2mm;
		padding: .4em;
		margin: 0 !important;
		box-shadow: inset 0 0 0 1px #bbb, 0 0 2mm rgba(0, 0, 0, .3);
		backdrop-filter: blur(2mm);
		-webkit-backdrop-filter: blur(2mm);
		z-index: 11;
		zoom: 1.1;
	}

	.rt-button {
		padding: .2em .7em;
	}

	.rt-pagination li a {
		min-width: 1em;
	}

	.rt-pagination .page-link-list {
		margin: 0;
	}

	.related-keyword-list {
		border-radius: 3mm;
		box-shadow: inset 0 0 2mm #888;
		border: none;
	}

	.custom-store-banner {
		margin: -3mm 0 2mm;
	}

	.zone1220 {
		width: 90rem;
	}

	@media (max-width: 1500pt) {
		.rt-nav .rt-chat-container {
			margin: 2pt 0 0 calc(6vw - 133px);
		}

		.rt-nav .rt-chat-inbox[data-v-11889b5c]::before {
			margin: 0 calc(6vw - 139px) -1px auto;
		}
	}

	@media (min-width: 1500pt) {
		.rt-nav .rt-chat-container {
			margin: 0 -5pt;
		}

		.rt-nav .rt-chat-inbox[data-v-11889b5c]::before {
			margin: 0 -5pt -1px auto;
		}
	}
}
