/* ==UserStyle==
@name			[ACG BTË≥áÊ∫êÁ´ô]
@author			Evan Tseng
@preprocessor	default
==/UserStyle== */

@-moz-document domain("share.dmhy.org"), domain("dmhy.anoneko.com")
{
	#bebiv3_ad,
	#resource-tabs hr,
	iframe,
	.footer,
	.ad,
	a[href="http://www.pixivbook.com/"],
	.user-sidebar .box:last-of-type,
	div.table:nth-of-type(4):nth-last-of-type(3),
	div.table:nth-of-type(5):nth-last-of-type(2),
	a[href="http://pixivbook.com/"],
	div[id="1280_ad"],
	div[id="1280_adv"],
	[class^="kiwi-ad-"] {
		display: none !important
	}
	
	body {
		font: 400 calc(10pt + .2vw) sans-serif;
		min-width: 1130px
	}
	
	ol,
	ul {
		padding-left: 2em;
	}
	
	.headerleft {
		height: 82px;
		margin-top: -39px;
		overflow: hidden;
	}
	
	.headerright {
		position: absolute;
		float: none;
		right: 0;
	}
	
	.headerright .links,
	.headerright .links a {
		display: inline-block;
		font-size: 12pt;
		height: 30px
	}
	
	.bg {
		min-width: 900px;
		margin: 0;
	}
	
	.bg>.header {
		position: sticky;
		height: auto;
		top: 0;
		z-index: 2;
		transition: top .8s .5s
	}
	
	.top_sort .menu .nav ul {
		white-space: nowrap;
		border-radius: 0 0 2mm 2mm;
		overflow: hidden;
	}
	
	.top_sort .menu ul li {
		width: auto
	}
	
	.top_sort .menu ul li a {
		font-size: 13pt;
		width: auto;
		min-width: 3em;
		padding: .3em .8em
	}
	
	.top_sort .menu ul li:hover ul {
		width: auto
	}
	
	.quick_search {
		width: 63em;
		padding: 2mm 1em;
		border-radius: 3mm;
	}
	
	.quick_search form {
		display: flex;
		flex-wrap: wrap;
		line-height: 1.6rem;
	}
	
	.quick_search .quick_input {
		flex: 1 1 auto;
		width: auto;
		border-right: none;
	}
	
	.ac_results {
		width: 32em !important;
	}
	
	.ac_results ul {
		max-height: 18em !important;
	}
	
	.ac_results li {
		font-size: 1rem;
		line-height: 1.4;
	}
	
	.formButton {
		flex: 0 0 auto;
		margin: 0;
	}
	
	.quick_search a {
		flex: 0 0 auto;
	}
	
	.quick_search .quick_input,
	.formButton,
	.quick_search a {
		font-size: 1em;
		line-height: 1.6;
		padding: 0 .5em;
		border-color: #777;
	}
	
	select {
		font-size: 1rem;
		margin: 0;
	}
	
	#AdvSearch {
		display: flex;
		flex: 0 0 100%;
	}
	
	#AdvSearch p {
		flex: 1 1 auto;
		display: inline-block;
		margin: 2mm 1em 0 0;
	}
	
	.main {
		padding: 3mm 2mm 1mm;
		margin: 2mm 5mm;
		border-radius: 3mm;
	}
	
	.main>.table.clear {
		width: auto !important;
		margin: 0
	}
	
	table.jmd tr th {
		text-align: right;
		width: 5.5em;
		white-space: nowrap
	}
	
	table.jmd a {
		transition: all 220ms;
		display: inline-block;
		line-height: 1.2 !important;
		padding: 1px .6em 0 !important;
		border: none !important;
		border-radius: 1em;
		margin: 1px 5px 1px 0 !important
	}
	
	table.jmd a:hover {
		transition: all 60ms;
		text-decoration: none !important;
	}
	
	.nav_title {
		padding: 2px .2em;
		line-height: 1.33;
	}
	
	.table {
		padding: 0;
		background-color: transparent
	}
	
	table.tablesorter {
		border-spacing: 0
	}
	
	table.tablesorter thead tr .headerSortDown,
	table.tablesorter thead tr .headerSortUp {
		color: #eee;
		background: #a52;
	}
	
	table.tablesorter th:nth-child(1),
	table.tablesorter td:nth-child(1) {
		width: 5.3em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(2),
	table.tablesorter td:nth-child(2) {
		width: 2.2em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(4),
	table.tablesorter td:nth-child(4) {
		width: 3.6em !important;
		text-align: center;
	}
	
	table.tablesorter th:nth-child(5),
	table.tablesorter td:nth-child(5) {
		width: 4.5em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(6),
	table.tablesorter td:nth-child(6),
	table.tablesorter th:nth-child(7),
	table.tablesorter td:nth-child(7),
	table.tablesorter th:nth-child(8),
	table.tablesorter td:nth-child(8) {
		width: 2.1em !important;
		white-space: nowrap;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(9),
	table.tablesorter td:nth-child(9) {
		width: 6em !important;
	}
	
	table.tablesorter th,
	table.tablesorter td:nth-child(1),
	table.tablesorter td:nth-child(2),
	table.tablesorter td:nth-child(n+5) {
		font-size: .92rem;
	}
	
	table.tablesorter tbody td {
		line-height: 1.25;
		padding: 0 2px;
	}
	
	table.tablesorter td.title {
		position: relative;
		display: flex;
		min-height: 30pt;
		padding: 0 5px;
		vertical-align: middle;
	}
	
	table#topic_list tr td span.tag {
		flex: 0 0 auto;
		line-height: 1.4;
		padding: 0;
		margin: .7em 2mm .7em 0;
		border: none !important;
		border-radius: .3em;
	}
	
	table#topic_list tr td span.tag a {
		display: block;
		padding: 0 .2em;
	}
	
	table#topic_list .title>a {
		position: relative;
		flex: 1 1 auto;
		display: inline-block;
		height: auto;
		line-height: 1.4;
		padding: .7em 0;
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
		z-index: 1;
	}
	
	table#topic_list td.title span[style="color: gray;"] {
		flex: 0 0 auto;
		font-size: .8em;
		line-height: 2.8rem;
		letter-spacing: .1em;
		margin: 0 .3em;
	}
	
	table#topic_list td:last-child>a {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	table#topic_list a:hover {
		border: none
	}
	
	table#topic_list b {
		font-weight: normal;
	}
	
	.table a.sort-7 font {
		font-size: 0;
	}
	
	.table a.sort-7 font::after {
		display: inline;
		font-size: 1rem;
		content: "RAW";
	}
	
	.arrow-magnet {
		display: block;
		width: 100%;
		background: none;
		font-size: 0;
		padding: 0;
		margin: 0 auto;
		opacity: .7;
	}
	
	.arrow-magnet:hover {
		opacity: 1;
		transform: scale(1.07);
	}
	
	.arrow-magnet:before {
		font-size: 9pt;
		line-height: 1.5;
		content: "Á£ÅÂäõ";
	}
	
	.arrow-magnet::after {
		display: inline-block;
		font-size: 9pt;
		line-height: 1.5;
		content: "üß≤";
		transform: rotate(180deg);
	}
	
	.arrow-magnet:hover::after {
		transform: rotate(180deg);
	}
	
	.download-pp {
		display: block;
		width: auto;
		font-size: 0;
		background: none;
		text-align: center;
	}
	
	.download-pp::before {
		display: block;
		width: 100%;
		height: 1px;
		content: "";
	}
	
	.download-pp:after {
		display: inline-block;
		font-size: 9pt;
		content: "PikPak";
		padding: 0 .5em;
		margin: 2px auto;
		border-radius: 1em;
		transition: .16s;
	}
	
	.download-pp:hover::after {
		box-shadow: 0 0 0 1px #666,
			0 0 4px #000;
		transform: scale(1.06);
	}
	
	.topic-main {
		position: relative
	}
	
	.topic-title h3,
	.topic-main #resource-tabs p {
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
		margin: 0 0 .1em
	}
	
	a[name="description-end"] {
		position: absolute;
		top: 0;
	}
	
	.topic-main #resource-tabs {
		position: absolute;
		top: 3rem;
		left: 1em;
		width: calc(100% - 23em);
		padding: 2mm;
		margin: 0;
		overflow: auto;
		backdrop-filter: blur(2mm);
		-webkit-backdrop-filter: blur(2mm);
		transition: .5s .3s
	}
	
	.topic-main #resource-tabs:hover {
		transition: .3s .2s
	}
	
	#resource-tabs .ui-tabs-nav {
		padding: 2px .5em 0;
	}
	
	.ui-tabs .ui-tabs-nav li a {
		padding: 0 .5em;
	}
	
	#resource-tabs hr {
		display: none
	}
	
	.topic-main #resource-tabs .file_list {
		width: auto !important;
		max-height: 2.5rem;
		margin: 0;
		transition: .5s .3s
	}
	
	.topic-main #resource-tabs:hover .file_list {
		max-height: calc(100vh - 16em);
		overflow: auto;
	}
	
	.ui-tabs .ui-tabs-panel {
		padding: 0 !important;
		margin: 0 !important
	}
	
	#preview {
		border: none
	}
	
	#preview>img {
		object-fit: contain;
		min-width: 50vw;
		max-width: 95vw;
		min-height: 50vh;
		max-height: 95vh;
		padding: 5mm;
		border-radius: 3mm;
		backdrop-filter: blur(5mm);
		-webkit-backdrop-filter: blur(5mm);
	}
	
	img[alt="rss"] {
		margin-bottom: calc(.7em - 8px);
		filter: brightness(.8) contrast(2);
	}
	
	.ui-widget {
		font-size: 11pt;
	}
	
	#mini_jmd {
		position: relative;
		width: auto !important;
		max-height: 4.5em;
		overflow: hidden;
		transition: .5s .2s;
	}
	
	#mini_jmd:after {
		content: "";
		display: block;
		position: absolute;
		bottom: 0;
		width: 100%;
		height: 2em;
		max-height: 2em;
		background: linear-gradient(0deg, #333 0, transparent 90%);
		transition: .5s .2s;
	}
	
	#mini_jmd:hover {
		max-height: 15em;
		overflow: auto;
	}
	
	#mini_jmd:hover:after {
		max-height: 0;
	}
	
	.avatar,
	.topics_cult {
		font-weight: 400;
	}
	
	.file_list {
		position: relative;
	}
	
	.file_list li,
	.bt_file_size {
		font-size: .9rem;
	}
	
	.file_list li {
		max-width: 100%;
		padding-right: 5.5em;
		line-height: 1.4rem;
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.file_list li .bt_file_size {
		position: absolute;
		right: 2mm;
	}
	
	#comment_recent .infotable {
		min-width: 15em;
	}
	
	#comment_recent .comment_con {
		max-width: calc(100vw - 36em);
	}
	
	.topic-main #resource-tabs p {
		text-align: left;
	}
	
	.topic-main img {
		height: auto;
		max-width: 100%;
		max-height: 70vh;
	}
	
	#captcha_img {
		filter: grayscale(1) brightness(.68) contrast(7);
	}
	
	@media (prefers-color-scheme: light) {
		.bg>.header {
			background: #247;
			box-shadow: 0 0 0 1px #666, 0 0 2mm #000;
		}
	
		.top_sort,
		.top_sort .menu,
		.top_sort .menu ul li:hover ul li a {
			background: #cdf;
		}
	
		.top_sort .menu ul li a:hover font {
			color: #def;
		}
	
		.top_sort .menu .nav ul {
			box-shadow: 1px 3px 5mm rgba(0, 0, 0, .5);
		}
	
		font[color="#0eb9e7"] {
			color: #17a;
		}
	
		.quick_search {
			box-shadow: inset 0 0 2mm #333
		}
	
		.main {
			background: linear-gradient(90deg,
					#bbb 0,
					#ccc 30%,
					#ccc 50%,
					#bbb 100%);
			box-shadow: 0 0 2mm rgba(0, 0, 0, .3)
		}
	
		table.jmd tr.today {
			background: #ffd700 !important
		}
	
		table.jmd a {
			color: #FFD !important;
			background: rgba(50, 50, 80, 0.8);
		}
	
		table.jmd tr.today a {
			background: rgba(80, 50, 30, 0.8)
		}
	
		table.jmd a:hover {
			color: maroon !important;
			background: #DDD !important;
			box-shadow: 0 1px 6px rgba(0, 0, 0, 0.8);
		}
	
		table.jmd a:active {
			color: red !important;
			background: #CCC !important;
			box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.6);
		}
	
		.table {
			background: rgba(255, 255, 255, .3);
		}
	
		table.tablesorter tr td:nth-child(even) {
			background-color: rgba(120, 120, 140, .1) !important
		}
	
		table.tablesorter tr.odd {
			background-color: rgba(180, 180, 200, .5)
		}
	
		table.tablesorter tr.even {
			background-color: rgba(180, 200, 180, .5)
		}
	
		table.tablesorter tr:hover {
			background: rgba(200, 220, 230, .5) linear-gradient(0deg,
					rgba(0, 30, 150, .6),
					rgba(245, 245, 255, .3) 1mm,
					rgba(245, 245, 255, .3) calc(100% - 1mm),
					rgba(0, 30, 150, .6));
		}
	
		table.tablesorter tbody td {
			background: transparent linear-gradient(0deg,
					rgba(0, 0, 0, .3),
					transparent 1px,
					transparent) !important
		}
	
		table#topic_list tr td span.tag {
			background-color: #edc;
			box-shadow: 0 0 0 1px #999;
		}
	
		table#topic_list tr td span.tag:hover {
			background-color: #fea;
			box-shadow: 0 0 0 1px #999,
				0 0 2mm rgba(0, 0, 0, .5);
		}
	
		table#topic_list td.title span[style="color: gray;"] {
			color: #836 !important;
		}
	
		table#topic_list .title>a {
			color: #334;
		}
	
		table#topic_list .title>a:hover {
			color: #a20;
		}
	
		table#topic_list .title>a:active {
			color: #620
		}
	
		.arrow-magnet {
			color: #004;
		}
	
		.arrow-magnet:after {
			color: #263;
		}
	
		.download-pikpak:before {
			background: #aaa;
		}
	
		.download-pikpak:after {
			color: #233;
			background: #bbd;
			box-shadow: 0 0 0 1px #888;
		}
	
		.download-pikpak:hover:after {
			background: #ddf;
			box-shadow: 0 0 0 1px #888,
				0 0 4px #000;
		}
	
		.topic-main #resource-tabs {
			background: rgba(255, 255, 255, .2);
			box-shadow: 0 0 0 1px rgba(0, 0, 50, .1);
		}
	
		.topic-main #resource-tabs:hover {
			background: rgba(255, 255, 255, .7);
			box-shadow: 0 1px 3mm rgba(0, 0, 0, .5);
		}
	
		#preview>img {
			background: rgba(255, 255, 255, .2);
			box-shadow: 0 0 5mm rgba(0, 0, 0, .5);
		}
	
		#mini_jmd:after {
			background: linear-gradient(0deg,
					#333 0,
					transparent 90%);
		}
	
		.file_list li .bt_file_size {
			background: #fff;
		}
	
		.file_list li.even .bt_file_size {
			background: #e8eafc;
		}
	
		.even {
			background: #e8eafc;
		}
	}
	
	@media (prefers-color-scheme: dark) {
	
		html,
		body {
			color: #ccc;
			background: #555;
		}
	
		a {
			color: #abd;
		}
	
		a:hover {
			color: #cff;
			text-decoration: none;
		}
	
		input,
		textarea {
			color: #bbb;
			background: #111;
		}
	
		font[color="red"],
		[style="color: red;"] {
			color: #f66 !important;
		}
	
		font[color="purple"],
		[style="color: purple;"] {
			color: #c6f !important;
		}
	
		font[color="green"],
		[style="color: green;"],
		.ac_results li .count,
		.bts_1 {
			color: #6d6 !important;
		}
	
		font[color="blue"],
		[style="color: blue;"],
		#bgm-panel th,
		.btl_1 {
			color: #4af !important;
		}
	
		font[color="orange"],
		[style="color: orange;"] {
			color: #fa4 !important;
		}
	
		font[color="#0eb9e7"] {
			color: #4cd !important;
		}
	
		font[color="brown"],
		[style="color: brown;"] {
			color: #c84 !important;
		}
	
		font[color="black"],
		[style="color: black;"] {
			color: #ddd !important;
		}
	
		font[color="#0eb9e7"] {
			color: #17a;
		}
	
		div[style*="background:#DDE3FF;"],
		div[style*="background:#CCDDFF;"],
		div[style*="background: #ccddff;"] {
			background: #445 !important;
		}
	
		div[style*="border:1px solid blue;"] {
			border-color: #8ac !important;
		}
	
		.bg>.header {
			background: #247;
			box-shadow: 0 0 0 1px #666, 0 0 2mm #000;
		}
	
		.top_sort,
		.top_sort .menu,
		.top_sort .menu ul li:hover ul li a,
		table.tablesorter tr th,
		.zend_form dt {
			background: #235;
		}
	
		.nav_title a,
		table.tablesorter tr th {
			color: #ccc;
		}
	
		.zend_form dd {
			background: #556;
		}
	
		.nav_title {
			background: #024;
		}
	
		.ui-widget-header {
			background: linear-gradient(0deg,
					#235,
					#568);
			border-color: #568;
		}
	
		.ui-state-active,
		.ui-widget-content .ui-state-active {
			background: linear-gradient(0deg,
					#253,
					#586);
			border-color: #5a6;
		}
	
		.top_sort .menu ul li a:hover font {
			color: #def;
		}
	
		.top_sort .menu .nav ul {
			box-shadow: 1px 3px 5mm rgba(0, 0, 0, .5);
		}
	
		.quick_search {
			box-shadow: inset 0 0 2mm #333
		}
	
		.quick_search .quick_input {
			color: #444;
		}
	
		.main {
			background: linear-gradient(90deg,
					#333 0,
					#444 30%,
					#444 50%,
					#333 100%);
			box-shadow: 0 0 2mm rgba(255, 255, 255, .3)
		}
	
		table.jmd tr.today {
			background: #873 !important
		}
	
		table.jmd a {
			color: #ffd !important;
			background: rgba(50, 50, 80, 0.8);
		}
	
		table.jmd tr.today a {
			background: rgba(80, 50, 30, 0.8)
		}
	
		table.jmd a:hover {
			color: #620 !important;
			background: #bcd !important;
			box-shadow: 0 1px 6px rgba(0, 0, 0, 0.8);
		}
	
		table.jmd a:active {
			color: red !important;
			background: #ccc !important;
			box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.6);
		}
	
		.table {
			background: rgba(0, 0, 0, .3);
		}
	
		table.tablesorter tr td:nth-child(even) {
			background-color: rgba(150, 150, 150, .1) !important
		}
	
		table.tablesorter tr:nth-child(odd) {
			background-color: #322e20
		}
	
		table.tablesorter tr:nth-child(even) {
			background-color: #2a2623
		}
	
		table.tablesorter tr:hover {
			background: #222 linear-gradient(0deg,
					#700,
					#111 1mm,
					#111 calc(100% - 1mm),
					#700);
		}
	
		table.tablesorter tbody td {
			background: transparent linear-gradient(0deg,
					rgba(255, 255, 255, .3),
					transparent 1px,
					transparent) !important
		}
	
		table.tablesorter tbody tr.even td {
			background: transparent;
		}
	
		table#topic_list tr td span.tag {
			background-color: #700;
			box-shadow: 0 0 0 1px #666
		}
	
		table#topic_list tr td span.tag:hover {
			background-color: #c30;
			box-shadow: 0 0 0 1px #888,
				0 0 2mm rgba(255, 255, 255, .5);
		}
	
		table#topic_list tr td span.tag:hover {
			color: #cef;
