css:
/* create by Lofox */ 

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trends24.in") {
  
  
  .trend-card-advert {display: none}
  

  
  
  html, body {
		background: -moz-linear-gradient(left ,#c6e5f7, #f8f8f8, #fff, #f8f8f8,  #c6e5f7); 
	}
  
	a:hover ,.trend-card a:hover  {color: #444; } 
	a {color: #1e1e1e}
        
	/* head   */
	.app-bar a {
	color: #000;
}
	
	.app-bar__menu-button .icon {filter: invert(100%)}
	
	
	.app-bar {
	background: transparent;
	border: unset;
		box-shadow: 	unset; 
	}
	
	.page-content__title {
	font-size: 2.0em;
		background: -moz-linear-gradient(left ,#c6e5f7, #f8f8f8, #fff, #f8f8f8,  #c6e5f7); 
		box-shadow:  0 0 2px 2px #c6e5f7 !important;
		border-top:  1px solid #1b95e0; 
		border-bottom:   1px solid #1b95e0;  
		color: #1b95e0;
		padding-bottom: 10px; padding-top: 10px;
		
}
	
	/*    <-     -> */  
	
	.page-content__scroller_button {
				background: -moz-radial-gradient(#c6e5f7, #f8f8f8, #fff, #f8f8f8,  #c6e5f7); 		
				box-shadow:  0 0 2px 2px #c6e5f7 !important;

	}
	
   .page-content__scroller__prev__icon , .page-content__scroller__next__icon  {filter: invert(100%)}  
	
	
	/* content */ 
  
  
  .trend-card {
		background: -moz-linear-gradient(left ,#c6e5f7, #f8f8f8, #fff, #f8f8f8,  #c6e5f7); 
	/*	color: #fff;   */
		box-shadow:  0 0 2px 2px #c6e5f7 !important;
	border: 1px solid #1b95e0; 
		border-radius: unset
}
     
  
  .trend-card__time {
	border-bottom: 1px solid #1b95e0;

}
  
  .hilight {
	background-color: rgba(27,149,224, 0.3);
}
 
  .trend-card__list li {	border-bottom:unset;}
	
	
	/* foot  */ 
	
	.page-content {
	border-bottom: unset;
}
	
	
	.footer__switchview , .footer__share , .suggested-locations , .footer__promotion , .footer__about
	{background: transparent;
		border: unset 
	}
	
	.card-block, .footer__share, .suggested-locations, .footer__promotion, .footer__about, .footer__switchview {
	padding: 10px 10px;
	border-radius: 0;
}
	
	.suggested-locations a {
	color: #1e1e1e;

}                    
	
	.footer__promotion__block {
	border-radius: unset;
		background: transparent; 
		box-shadow:  unset; border: unset; 
}
	
	
	.footer__credits {
	border-top: unset; 
	background: transparent ; 
		
}
	
	.footer__credits__text > a {color: #000}
	
	/* Share */
	
	span.footer__share__button:nth-child(2)  {
	background-color:  #1b95e0;
}
	
	span.footer__share__button:nth-child(3)  {
	background-color:  #3b5998;
}
		span.footer__share__button:nth-child(4)  {
	background-color:  #db4437;
	}
	
	/* look for localisation        */  
	
	
	.location-menu {
	background: transparent;
	box-shadow: 0 2px 5px rgba(255,255,255,0.26);
}
	
	.location-menu__country-list > li {
	overflow: hidden;
	border-bottom: 1px solid #1b95e0;
}
	
	
	.location-menu__city-list a:hover  {
	color: #444;
}
	
	
	
	.location-menu__city-list a {
	color: #1e1e1e;
}
	
	

	.location-menu__filter-input  
	
	
	 {
	background-color: rgba(27,149,224, 0.3);
}
  


	
}
