css:
@-moz-document domain("codemirror.net") {
/* syntax highlighting derived from material */
/*
    Name:       material
    Author:     Michael Kaminsky (http://github.com/mkaminsky11)
    Original material color scheme by Mattia Astorino (https://github.com/equinusocio/material-theme)
*/
.CodeMirror { border: 1px solid #444 !important; }
.cm-s-default {
  background-color: #1a1a1a;
  color: rgba(233, 237, 237, 1);
}
.cm-s-default .CodeMirror-gutters {
  background: #1a1a1a;
  color: rgb(83,127,126);
  border: none;
}
.cm-s-default .CodeMirror-guttermarker, .cm-s-default .CodeMirror-guttermarker-subtle, .cm-s-default .CodeMirror-linenumber { color: rgb(83,127,126); }
.cm-s-default .CodeMirror-cursor { border-left: 1px solid #f8f8f0; }
.cm-s-default div.CodeMirror-selected { background: rgba(255, 255, 255, 0.15); }
.cm-s-default.CodeMirror-focused div.CodeMirror-selected { background: rgba(255, 255, 255, 0.10); }
.cm-s-default .CodeMirror-line::selection, .cm-s-default .CodeMirror-line > span::selection, .cm-s-default .CodeMirror-line > span > span::selection { background: rgba(255, 255, 255, 0.10); }
.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-default .CodeMirror-line > span::-moz-selection, .cm-s-default .CodeMirror-line > span > span::-moz-selection { background: rgba(255, 255, 255, 0.10); }

.cm-s-default .CodeMirror-activeline-background { background: rgba(0, 0, 0, 0); }
.cm-s-default .cm-keyword { color: rgba(199, 146, 234, 1); }
.cm-s-default .cm-operator { color: rgba(233, 237, 237, 1); }
.cm-s-default .cm-variable-2 { color: #80CBC4; }
.cm-s-default .cm-variable-3 { color: #82B1FF; }
.cm-s-default .cm-builtin { color: #DECB6B; }
.cm-s-default .cm-atom { color: #F77669; }
.cm-s-default .cm-number { color: #F77669; }
.cm-s-default .cm-def { color: rgba(233, 237, 237, 1); }
.cm-s-default .cm-string { color: #C3E88D; }
.cm-s-default .cm-string-2 { color: #80CBC4; }
.cm-s-default .cm-comment { color: #546E7A; }
.cm-s-default .cm-variable { color: #82B1FF; }
.cm-s-default .cm-tag { color: #80CBC4; }
.cm-s-default .cm-meta { color: #80CBC4; }
.cm-s-default .cm-attribute { color: #FFCB6B; }
.cm-s-default .cm-property { color: #80CBAE; }
.cm-s-default .cm-qualifier { color: #DECB6B; }
.cm-s-default .cm-variable-3 { color: #DECB6B; }
.cm-s-default .cm-tag { color: rgba(255, 83, 112, 1); }
.cm-s-default .cm-error {
  color: rgba(255, 255, 255, 1.0);
  background-color: #EC5F67;
}
.cm-s-default .CodeMirror-matchingbracket {
  text-decoration: underline;
  color: white !important;
}


/* website css */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Source Sans Pro'), local('SourceSansPro-Regular'), url(//themes.googleusercontent.com/static/fonts/sourcesanspro/v5/ODelI1aHBYDBqgeIAH2zlBM0YzuT7MdOe03otPbuUS0.woff) format('woff');
}

body, html { margin: 0; padding: 0; height: 100%; }
section, article { display: block; padding: 0; }

body {
  background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
  font-family: 'Source Sans Pro', Helvetica, Arial, sans-serif;
  line-height: 1.5;
}

p { margin-top: 0; }

h2, h3, h1 {
  font-weight: normal;
  margin-bottom: .7em;
}
h1 { font-size: 140%; }
h2 { font-size: 120%; }
h3 { font-size: 110%; }
article > h2:first-child, section:first-child > h2 { margin-top: 0; }

#nav h1 {
  margin-right: 12px;
  margin-top: 0;
  margin-bottom: 2px;
  color: #fff;
  letter-spacing: .5px;
}

a, a:visited, a:link, .quasilink {
  text-decoration: none;
}

em {
  padding-right: 2px;
}

.quasilink {
  cursor: pointer;
}

article {
  max-width: 700px;
  margin: 0 0 0 160px;
  border-left: 2px solid #E30808;
  border-right: 1px solid #ddd;
  padding: 30px 50px 100px 50px;
  background: #181818;
  z-index: 2;
  color: #aaa;
  position: relative;
  min-height: 100%;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
}

article dt {
  color: #ddd;
}

a, a:visited, a:link, .quasilink { color: #e30808; }

article h1,
article h2,
article h3,
article h4,
article h5,
article h6 { color: #e30808; font-weight: bold; }

#nav {
  position: fixed;
  padding-top: 30px;
  max-height: 100%;
  box-sizing: -moz-border-box;
  box-sizing: border-box;
  overflow-y: auto;
  left: 0; right: none;
  width: 160px;
  text-align: right;
  z-index: 1;
}

@media screen and (min-width: 1000px) {
  article {
    margin: 0 auto;
  }
  #nav {
    right: 50%;
    width: auto;
    border-right: 349px solid transparent;
  }
}

#nav ul {
  display: block;
  margin: 0; padding: 0;
  margin-bottom: 32px;
}

#nav li {
  display: block;
  margin-bottom: 4px;
}

#nav li ul {
  font-size: 80%;
  margin-bottom: 0;
  display: none;
}

#nav li.active ul {
  display: block;
}

#nav li li a {
  padding-right: 20px;
  display: inline-block;
}

#nav ul a {
  color: #ccc;
  padding: 0 7px 1px 11px;
}

#nav ul a.active, #nav ul a:hover {
  border-bottom: 1px solid #E30808;
  margin-bottom: -1px;
  color: #E30808;
}

#logo {
  border: 0;
  margin-right: 12px;
  margin-bottom: 25px;
}

section {
  border-top: 1px solid #E30808;
  margin: 1.5em 0;
}

section.first {
  border: none;
  margin-top: 0;
}

#demo {
  position: relative;
}

#demolist {
  position: absolute;
  right: 5px;
  top: 5px;
  z-index: 25;
}

.yinyang {
  position: absolute;
  top: -10px;
  left: 0; right: 0;
  margin: auto;
  display: block;
  height: 120px;
}

.actions {
  margin: 1em 0 0;
  min-height: 100px;
  position: relative;
}

.actionspicture {
  pointer-events: none;
  position: absolute;
  height: 100px;
  top: 0; left: 0; right: 0;
}

.actionlink {
  pointer-events: auto;
  font-family: arial;
  font-size: 80%;
  font-weight: bold;
  position: absolute;
  top: 0; bottom: 0;
  line-height: 1;
  height: 1em;
  margin: auto;
}

.actionlink.download {
  color: white;
  right: 50%;
  margin-right: 13px;
  text-shadow: -1px 1px 3px #b00, -1px -1px 3px #b00, 1px 0px 3px #b00;
}

.actionlink.fund {
  color: #b00;
  left: 50%;
  margin-left: 15px;
}

.actionlink:hover {
  text-decoration: underline;
}

.actionlink a {
  color: inherit;
}

.actionsleft {
  float: left;
}

.actionsright {
  float: right;
  text-align: right;
}

@media screen and (max-width: 800px) {
  .actions {
    padding-top: 120px;
  }
  .actionsleft, .actionsright {
    float: none;
    text-align: left;
    margin-bottom: 1em;
  }
}

th {
  text-decoration: underline;
  font-weight: normal;
  text-align: left;
}

#features ul {
  list-style: none;
  margin: 0 0 1em;
  padding: 0 0 0 1.2em;
}

#features li:before {
  content: "-";
  width: 1em;
  display: inline-block;
  padding: 0;
  margin: 0;
  margin-left: -1em;
}

.rel {
  margin-bottom: 0;
}
.rel-note {
  margin-top: 0;
  color: #555;
}

pre {
  padding-left: 15px;
  border-left: 2px solid #ddd;
}

code {
  padding: 0 2px;
}

strong {
  text-decoration: underline;
  font-weight: normal;
}

.field {
  border: 1px solid #A21313;
}

}
