css:
/* create by Lofox */ 

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trends24.in") {
  
  
  .trend-card-advert {display: none}
  
  html, body {
		background: -moz-linear-gradient(left ,#000, #111, #222, #111,  #000); 
		color: #fff !important;     
	}
  
	a:hover ,.trend-card a:hover  {color: #fff; font-style:  oblique ;text-decoration: underline  } 
	a {color: #e1e1e1}
        
	/* head   */
	
	
	.app-bar {
	background: transparent;
	border: unset;
		box-shadow: 	unset; 
	}
	
	.page-content__title {
	font-size: 2.0em;
		background: -moz-linear-gradient(left ,#000, #111,  #000); 
		box-shadow:  0 0 1px 1px #1b95e0 !important;
		border-top:  1px solid #1b95e0; 
		border-bottom:   1px solid #1b95e0;  
		color: #1b95e0;
		padding-top: 10px; padding-bottom: 10px; 
}
	
	/*    <-     -> */  
	
	.page-content__scroller_button {
				background: -moz-radial-gradient( #111, #222, #111); 		 box-shadow:  1px 1px 1px 1px #1b95e0 !important; 
	}
	
	
	/* content */ 
  
  
  .trend-card {
		background: -moz-linear-gradient(left ,#000, #111,  #000); 
		color: #fff; 
		box-shadow:  0 0 1px 1px #1b95e0 !important;
		border: 1px solid #1b95e0; 
		border-radius: unset
}
     
 .trend-card a {color: #fff}
  
  .trend-card__time {
	border-bottom: 1px solid #1b95e0;

}
  
  .hilight {
	background-color: rgba(27,149,224, 0.3);
}
 
  .trend-card__list li {	border-bottom:unset;}
	
	
	/* foot  */ 
	
	.page-content {
	border-bottom: unset;
}
	.footer__switchview a {
	color: #1b95e0;
}
	
	.footer__switchview a:hover {
	color: #fff; font-style:  oblique ;text-decoration: underline
}
	
	.footer__switchview , .footer__share , .suggested-locations , .footer__promotion , .footer__about
	{background: transparent;
		border: unset 
	}
	
	.card-block, .footer__share, .suggested-locations, .footer__promotion, .footer__about, .footer__switchview {
	padding: 10px 10px;
	border-radius: 0;
}
	
	.suggested-locations a {
	color: #d1d1d1;

}                    
	.suggested-locations a:hover {
	color: #fff; font-style:  oblique ;text-decoration: underline
}
		
	
	.footer__promotion__block {
	border-radius: unset;
		background: transparent; 
		box-shadow:  unset; border: unset; 
}
	
	
	.footer__credits {
	border-top: unset; 
	background: transparent ; 
}
	
	
		/* Share */
	
	span.footer__share__button:nth-child(2)  {
	background-color:  #1b95e0;
}
	
	span.footer__share__button:nth-child(3)  {
	background-color:  #3b5998;
}
		span.footer__share__button:nth-child(4)  {
	background-color:  #db4437;
}
	
	
	/* look for localisation */  
	
	
	.location-menu {
	background: transparent;
	color: #fff;
	box-shadow: 0 2px 5px rgba(0,0,0,0.26);
}

	
	.location-menu__country-list > li {
	overflow: hidden; border: unset; 
}
	
	.location-menu__city-list a:hover  {
	color: #fff; font-style:  oblique ;text-decoration: underline
}
	
	.location-menu__city-list a {
	color: #d1d1d1;
}
	
	.location-menu__filter-icon {filter: invert(100%)}
	
	
	.location-menu__filter-input  	 {
	background-color: rgba(27,149,224, 0.3);
}
  
	.location-menu__country-header { color: #1b95e0;
	}
	
	
	
	
}
