  div[id^="ad-unit-"],
  div[id^="outbrain_widget_"],
  div[id^="right-rail__ad-"],
  div[id^="section-archive__ad-"],
  [id$="-right-rail-ad"],
  div[id^="mad_is_"],
  span[id^="GGADad_ii_"],
  .left-rail-ad__wrapper,
  .right-rail__ad__wrapper,
  .pushdowns,
  .top-banner__ad,
  .ab-banner,
  #ad-homepage-content-well,
  [id*="homepage-content-well-ad"],
  #ad-homepage-post-brief,
  [id*="homepage-post-brief-ad"],
  iframe[id^="google_ads_"]
