@-moz-document domain("dramafever.com") {
img[src$="//www.dramafever.com/static/8674e39/df2014/images/logo-dramafever@2x.png"]
  {
    height:35px!important; /*width of new image*/
    padding-left:174px!important; /*height of new image*/
    width:0!important;background-image:url('/*[[logo]]*/')!important
  }
*
{
    text-shadow:none!important;
    box-shadow:none!important;
    border-radius:0px!important;
    text-decoration: none!important;
}
@font-face
  {
   font-family: 'Roboto';
   font-style: normal;
   font-weight: 300;
  }
a
{
    color: #62d08f;
}
a:hover
{
    color: #2ed7ff;
}
.pop-list > li img
{
	border: 0px solid red;	
}
body,.info-card .quickview,h3.edit-off,h3.pull-left,.thread-item h4 a,p.comment-text,h5.cast-news-head.pull-left,h5.pull-left,.cast-members .cast-as .character,.show-guide .guide-content .guide-content-section .info,.pages span,.pages a,.list-options > li a,.list-info .exclusive
{
    color: #969696!important;
}
span.hide-ie
{
    color: #82d0a5;
}
[itemprop="name"]
{
	color:#049e4a;
}
.info-card .pipe
{
    color: #a55151;
}
/*----header----*/
.header-main,#site-footer,.info-card,.breadcrumb,.news-article .article-content,.news-comments,.view-tv .video-info-wrapper,.watching-list,.history-list .history-list-item
{
	background: /*[[bodycolor]]*/;
}
.header-main .menu .level-0 a,.header-main  .username
{
    color: #ee2f44;
}
.header-main .menu .level-0 a:hover
{
    color: #f97180;
}
/*----search-----*/
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input
{
    background-color: #151515;
    color: #ee2f44;
}
.header-main .utility .swift-search input
{
    border: 1px solid rgba(0, 0, 0, 0);
    border-left: 3px solid #42bfca;
}
.search-instant-results > ul > .instant-type, .search-instant-results > ul > .instant-type:hover,.pagination
{
    border-top: 1px solid #262626;
    border-bottom: 1px solid #262626;
    background-color: #0f0f0f;
	background-image:none!important;
}
.search-instant-results > ul
{
    background: #1c1c1c;
}
.search-instant-results > ul > li:hover, .search-instant-results > ul > li.hover
{
    background: #0a0a0a;
}
.search-instant-results > ul > li,.breadcrumb,.actor-page .masthead,hr,.thread-posts .post .content .header,.list-options > li
{
    border-bottom: 1px solid #262626;
}
.search-instant-results > ul h3
{
    color: #48c3f4;
}
/*-----body------*/
body, html,.takeover,h3.centered span,div.container.container-main,div#main,.view-browse .table tr:hover,.view-browse .content-sidebar,.view-browse .content-sidebar .filter .options
{
	background: /*[[backgcolor]]*/;
}
li.list-head, li.list-head:hover
{
	background: #1b1b1b;
    border: 1px solid #1b1b1b;
}
.view-browse .content-sidebar .filter .th:hover,th, .th
{
       background-image: none !important;
}
/*-ep-*/
.item-list > li,.episode-list tbody
{
    border-bottom: 1px solid rgba(0,0,0,.2);
    background: #1c1c1c;
}
.item-list > li:hover,.episode-list tbody tr:hover {
    background: #0f0f0f;
}
.item-list > li a,.page-content h3,.page-content h3 a
{
    color: #0d9198;
}
/*-ep-*/
.episode-list thead,th.table-switch.switched
{
    background: #0f0f0f!important;
    border: 1px solid #262626;
}
hr,.table tbody > tr > td,.view-news .thread-item,.view-news .thread-item > .thread-item-content,table.threads tbody tr,table.threads tbody tr td
{
	border-top: 1px solid #262626!important;	
}
.table-pagination
{
    background: #1b1b1b;
    border: 1px solid #262626;
}
.episode-list-window
{
    border-left: 1px solid #262626;
    border-right: 1px solid #262626;
    background: rgba(0, 0, 0, 0);
}
.table thead > tr > th
{
    border-bottom: 0px solid #dddddd;
}
/*---epi view--*/
.info-card
{
    margin-top: 20px;
}
span.series-type
{
	color: #e4a368;
}
.commentarea
{
    border: 1px solid #ee2f44;
}
.thread-item .thread-item-content:hover .active-area,.view-news .thread-item .thread-item-content:hover .active-area
{
    background: #212121;
}
.thread-item,.thread-item > .thread-item-content
{
    border-top: 1px solid #262626;
}
.casting-news a
{
    color: #bf3434 !important;
}
.casting-news a:hover
{
    color: #e85050 !important;
}
.btn-load-more
{
    background: #0f0f0f;
    color: #eef964 !important;
}
.btn-default.active
{
    color: #e1ec49;
    background-color: #0f0f0f;
}
.btn-default
{
    color: #9a9a9a;
    background-color: #0f0f0f;
}
.theme-list li a
{
    background: #0f0f0f;
    color: #797979;
}
h4 > a 
{
	color:#17bf9a !important;
}
/*-----actor page-----*/
.actor-stat-box
{
    background: rgba(0, 0, 0, 0.38);
    border: 0px solid #efefef;
}
.btn-fan-off i,.btn-follow-off i
{
    color: #ee2f44;
    background: rgba(0, 0, 0, 0);
}
.btn-fan-on i,.btn-fan-on
{
    color: #fff;
    background: rgba(0, 0, 0, 0);
}
.btn-fan,.btn-follow
{
    color: #fff;
    background-color: rgba(0, 0, 0, 0);
}
.btn-fan:hover,.btn-follow:hover
{
    color: #fecb35;
    background-color: rgba(0, 0, 0, 0);
}
.stat-hover
{
    background: #1b1b1b;
}
.stat-hover:hover
{
    background: #3b3b3b;
}
.breadcrumb a
{
    background: rgba(0, 0, 0, 0);
    border: 0px solid #DFDFDE;
}
.df-tab-buttons .active a
{
    background: #1b1b1b;
    border: 1px solid #262626;
	color: #dac736;
}
.df-tab-buttons li a
{
    border: 1px solid #262626;
    background: #161616;
}
.df-tab-buttons li a:hover
{
    background: #242424;
}
.image-uploader .upload-instructions
{
    background: #0a0a0a;
}
h3
{
    color: #415d73;
}
.well
{
    background-color: rgba(0, 0, 0, 0.24);
    border: 1px solid rgba(0, 0, 0, 0);
    color: #ccc;
}
.page-sidebar, .change-account
{
    background: #0f0f0f;
}
h3.left span
{
    background: rgba(0, 0, 0, 0);
}
h3.left:after,h3.centered:after
{
    border-top: 0px solid red;
    border-bottom: 0px solid red;
}
h4
{
    color: #0a6b5e;
}
.profile-status
{
    color: #bfbfbf;
    background: rgba(0, 0, 0, 0.22);
}
.profile-status:before
{
    border-top: 0px solid rgba(0, 0, 0, 0.22);
    border-bottom: 0px solid rgba(0, 0, 0, 0.22);
    border-right: 0px solid rgba(0, 0, 0, 0.22);
}
.activity-stats .count
{
    color: #b94a4a;
}
.owl-page span
{
    opacity: 1;
    border: 2px solid #f52c3f;
}
/*-------programacion------*/
.show-guide .guide-nav .days .guide-nav-button
{
    background: #0f0f0f;
    border-bottom: 1px solid #262626;
}

.show-guide .guide-nav .days:before
{
    background: rgba(0, 0, 0, 0);
}
.show-guide .guide-nav .days .guide-nav-button.active
{
    color: #ab3030;
}
.show-guide .guide-content
{
    border-left: 1px solid #262626;
}
.show-guide .guide-content .guide-content-section .series
{
    border: 1px solid #262626;
    background: #0f0f0f;
}
.show-guide .guide-content .guide-content-section .masthead:after,.show-guide .guide-content .guide-content-section .masthead:after
{
	background: linear-gradient(to right, #000 0%,rgba(0,0,0,0) 100%);
}
.show-guide .guide-content .guide-content-section .masthead .info
{
	color: #d8dc22 !important;
}
/*-------news--------*/
.view-news .list-featured > li
{
    border: 2px solid #b9343d;
}
.view-news .list-articles .article
{
    background: #1b1b1b;
}
.view-news .news-categories
{
    background: #0f0f0f;
    border: 1px solid #262626;
}
.view-news .news-categories > li a
{
    color: #2d7794;
}
.view-news .news-categories > li,td,.td
{
    border-right: 1px solid #262626;
}
.view-news .list-articles .article h2 a
{
    color: #209681;
}
.view-news .article .author {
    border-top: 1px solid #262626;
    border-bottom: 1px solid #262626;
}
.view-news .readmore
{
    background: #000;
    border: 1px solid #000;
}
.view-news .news-page-navigation
{
    background: #1b1b1b;
}
.view-news .news-page-navigation:hover
{
	background: #2b2b2b;
}
.view-news .article-tags > li
{
    background-color: #0f0f0f;
}
.view-news .article-tags > li:hover
{
    background-color: #242424;
}
.view-news .article-tags > li h4
{
    color: #1394a2;
}
.news-article .article-nav,.news-article .outbrain-related .odb_li
{
    background: #000;
}
/*------foro------*/
table.threads,tbody,th, .th,.view-browse .content-sidebar .filter .options,input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="url"], input[type="color"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="time"], input[type="week"],.list-info .exclusive
{
    border: 1px solid #262626;
}
th, .th
{
    background-color: #0f0f0f;
}
tr:nth-child(odd)
{
    background: #161616;
}
table.threads tbody tr:hover
{
    background: #252525;
}
tr:nth-child(even)
{
    background: #1b1b1b
}
.thread-posts .post .content .header .user .name
{
    color: #11b335;
}
.tfooter
{
    background: #000;
    border: 1px solid #000;
}
.thread-posts .post.parent .content,.thread-posts .post .content,.thread-posts .post.staff .content
{
    background: #0f0f0f;
    border: 1px solid #262626;
}
.thread-posts .post.staff .content
{
	border-left: 3px solid #f5384a;	
}
.thread-posts .post .content .header .stats > span
{
    border-left: 1px solid #262626;
}
.thread-posts .post .content .footer
{
    background: #0f0f0f;
    border-top: 1px solid #0f0f0f;
}
.redactor_editor, .redactor_editor:focus 
{
    background: #0f0f0f !important;
}
/*-----borrar-------*/
.hidden-xs,div.ad-leaderboard-flex,div.col-ad.res-bot-margin,.view-news .ad-300, .view-news .ad-300x250,div.ad-temp.ad-300x600,div.ad.ad-728x90
{
    display: none !important;
}
div.takeover.page-head-area.takeover-internal
{
    display: /*[[hidewea]]*/!important;
}
#site-footer:last-child
{
    display: /*[[hidefoo]]*/!important;
}
}
