css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cppsamples.com") {
  
  body {
    background-color: #333333;
  }
  
  /* Top Header */
  body > header {
    background-color: #111111;
  }
  
  /* Search */
  .search input {
    background-color: #222222;
  }
  
  /* Paragraph titles */
  h1 {
    color: #aaa;
  }
  
  /* Description text */
  p {
    color: #999;
  }
  
  /* C++11 tags */
  .tags li[class="c++11"] {
    background-color: #395911; /* #484848; */
  }
  
  /* C++14 tags */
  .tags li[class="c++14"] {
    background-color: #2273b3; /* #484848; */
  }
  
  /* Experimental tags */
  .tags li.experimental {
    background-color: #484848;
  }
  
}
