css:
@-moz-document domain("graph.anime.plus") {
.main-wrapper { max-width: none; }
#menu { 
    height: 0; 
    z-index: 1000;
    overflow:hidden;
    padding: 10px 0 0 0;
    background:darkgrey;
    /*transition: all 2s ease;*/ 
}

#main { margin-left: 0; }
#menu:before { height:0; }
#menu-wrapper { height: 50px; float:none; width: 100%;}

#menu-wrapper:hover #menu { z-index:1000; height: auto; }
#menu-wrapper:before {
    height: 40px;
    content: "Menu";
	display: block;
    font-size: 24px;
    line-height: 40px;
    text-align: center;
    border-radius: 6px;
    background: lightgrey;
    border-left: 3px solid #fff;
    border-right: 3px solid #fff;
    text-shadow: 0 0 0;
}
}
